abstract contract BaseToken is ERC20, Ownable {
    using SafeMath for uint256;

    address private fundAddress;
    address private erc19Address;

    mapping(address => bool) private _feeList;
    mapping(address => bool) private _swapPairList;

    ISwapRouter private _swapRouter;
    ISwapFactory private _swapFactory;
    IRepoPool private RP;

    uint256 private constant MAX = ~uint256(0);

    uint256 private initialPrice;

    uint256 private tokenSwapForFund;
    uint256 private tokenSwapForRepo;

    address public mainPair;
    address private currency;

    bool private inSwap;
    modifier lockTheSwap() {
        inSwap = true;
        _;
        inSwap = false;
    }

    constructor(
        address RouterAddress,
        address Currency,
        string memory Name,
        string memory Symbol,
        uint256 Supply,
        address LPAddress,
        address BurnAddress,
        address FundAddress,
        address RepoPoolAddress,
        address ERC19Address
    ) ERC20(Name, Symbol) {
        currency = Currency;
        fundAddress = FundAddress;
        erc19Address = ERC19Address;

        ISwapRouter swapRouter = ISwapRouter(RouterAddress);
        _swapRouter = swapRouter;
        _approve(address(this), address(swapRouter), MAX);
        ERC20(Currency).approve(address(swapRouter), MAX);

        _swapFactory = ISwapFactory(swapRouter.factory());

        RP = IRepoPool(RepoPoolAddress);

        _mint(LPAddress, (Supply * 85) / 100);
        _mint(BurnAddress, (Supply * 15) / 100);

        tokenSwapForFund = 800000 * 10**18;
        tokenSwapForRepo = 300000 * 10**18;

        _feeList[address(this)] = true;
        _feeList[address(swapRouter)] = true;
        _feeList[RepoPoolAddress] = true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        uint256 balance = balanceOf(from);
        require(balance >= amount, "balanceNotEnough");
        require(amount > 0, "amountIsZero");
        require(from != erc19Address && to != erc19Address, "erc19Address");

        if (mainPair == address(0)) {
            address swapPair = _swapFactory.getPair(currency, address(this));
            if (swapPair != address(0)) {
                mainPair = swapPair;
                _swapPairList[swapPair] = true;
            }
        }

        if (initialPrice == 0 && tokenPrice() > 0) {
            initialPrice = tokenPrice();
        }

        bool takeSwapFee;
        if (!_feeList[from] && !_feeList[to]) {
            if (_swapPairList[from] || _swapPairList[to]) {
                if (0 == startTradeBlock) {
                    require(0 < startAddLPBlock && _swapPairList[to], "!startAddLP");
                }
                if (block.number < startTradeBlock.add(5)) {
                    _funTransfer(from, to, amount);
                    return;
                }

                if (_swapPairList[to]) {
                    if (!inSwap) {
                        uint256 tokenSwapFund = calTokensSwap(tokenSwapForFund);
                        uint256 tokenSwapRepo = calTokensSwap(tokenSwapForRepo);

                        uint256 tokenFund = balanceOf(address(this));
                        if (tokenFund > tokenSwapFund) {
                            swapToken(
                                tokenFund.div(10**16).mul(10**16),
                                fundAddress
                            );
                        }

                        RP.addLP();
                        uint256 tokenRepo = balanceOf(address(RP));
                        if (tokenRepo > tokenSwapRepo) {
                            RP.swapToken(
                                tokenRepo.div(10**16).mul(10**16).div(2)
                            );
                        }
                    }
                }
                takeSwapFee = true;
            }
        }

        _tokenTransfer(from, to, amount, takeSwapFee);
    }

    function _funTransfer(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        uint256 feeAmount = (tAmount * 75) / 100;
        super._transfer(sender, address(this), feeAmount);
        super._transfer(sender, recipient, tAmount - feeAmount);
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        bool takeSwapFee
    ) private {
        uint256 feeAmount;

        if (takeSwapFee) {
            uint256 fundFees = tAmount.mul(23).div(1000);
            uint256 repoFees = tAmount.mul(7).div(1000);

            feeAmount = fundFees.add(repoFees);

            super._transfer(sender, address(this), fundFees);
            super._transfer(sender, address(RP), repoFees);
        }

        super._transfer(sender, recipient, tAmount.sub(feeAmount));
    }

    function swapToken(uint256 tokenAmount, address to) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = currency;

        _swapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            to,
            block.timestamp
        );
    }

    uint256 private startTradeBlock;
    uint256 private startAddLPBlock;

    function startAddLP() external onlyOwner {
        require(0 == startAddLPBlock, "startedAddLP");
        startAddLPBlock = block.number;
    }

    function startTrade() external onlyOwner {
        require(0 == startTradeBlock, "trading");
        startTradeBlock = block.number;
    }

    function setFeeLists(address[] memory accounts, bool isFree)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < accounts.length; i++) {
            _feeList[accounts[i]] = isFree;
        }
    }

    function tokenPrice() internal view returns (uint256 price) {
        uint256 tokenOfPair = balanceOf(mainPair);
        uint256 currencyOfPair = ERC20(currency).balanceOf(mainPair);
        if (tokenOfPair > 0 && currencyOfPair > 0) {
            price = currencyOfPair.mul(10**18).div(tokenOfPair);
        }
        return price;
    }

    function calTokensSwap(uint256 tokenSwap) internal view returns (uint256) {
        if (initialPrice == 0) return tokenSwap;
        uint256 multiple = tokenPrice().div(initialPrice);
        if (multiple > 1000) {
            return tokenSwap.div(16);
        } else if (multiple > 100) {
            return tokenSwap.div(8);
        } else if (multiple > 50) {
            return tokenSwap.div(4);
        } else if (multiple > 10) {
            return tokenSwap.div(2);
        }
        return tokenSwap;
    }
}

