interface IYHRefer{
    function user_referrer(address user) external view returns(address);
    function IsRegisterEnable(address _user,address _userReferrer) external view returns (bool);
    function register(address _userReferrer) external view  returns (bool);
    function rootAddr() external view returns(address);
}

contract YH is ERC20 {
    using SafeMath for uint256;
    
    IUniswapV2Router02 public uniswapV2Router;
    IUniswapV2Pair public  uniswapV2Pair;
    address _airdropAddr = address(0xb3A892412af974538C6bdfce72bBeBAb14DeC3CD);
    address _nftStakeAddr = address(0xAB8C06b023e410Ecc34525ac040a33a4b9F8D306);
    address _daoAddr = address(0x58704853b782ec3dA2C00Ece89a53DAb5b98eFaf);
    address _lpRewardAddr = address(0x29C88C43CB2f9A9e6779b35A5a59B66506AaA1ad);
    address _p2eAddr = address(0x828Dca2eb32c013BD1eb39a8AA9dBC59626AD0B3);
    address _m2eAddr = address(0x97A1057917610e1c60a115932A41FCdC8EaFabb9);
    address _proj1Addr = address(0x42cCDf63151d980b94C2EfD38F519a972a018229);
    address _proj2Addr = address(0x7106b0eDAf4d07Faf62b97775d76d8A5DE9120CC);
    address _burnAddr = address(0x0);
    address _fundAddr = address(0x6f01eFB18BFba473Dea559809d96Fe19788Db0E5);
    address _holdDAddr = address(0x439926Eb517130e5604D17788207F1E0b7CeD2a6);
    address _tFeeAddr = address(0x10C5742dae8F0f6f56AFC9768980229F9278b0c9);
    address _adpAddr = address(0x972B933B1a3b3069Eb6E96012E47d562655B2087);
    IYHRefer _refer;

    mapping(address => bool) private _isExcludedFromFees;
    uint256 public inviterFee = 4;
    uint256 public lpFee = 3;
    uint256 public fundFee = 1;
    uint256 public holdDFee = 1;
    uint256 public burnFee = 1;
    uint256 public totalFee = inviterFee+lpFee+fundFee+holdDFee+burnFee;
    uint256 public tFee = 2;

    uint256 public oneDividendNum = 25;

    IERC20 public usdt = IERC20(0x55d398326f99059fF775485246999027B3197955);
    address[] private lpUser;
    mapping(address => bool) public lpPush;
    mapping(address => uint256) private lpIndex;
    mapping(address => uint256) private lpAmount;
    address[] public _exAddress;
    mapping(address => bool) private _bexAddress;
    mapping(address => uint256) private _exIndex;
    mapping(address => bool) public ammPairs;
    address public lastAddress = address(0);
    uint256 private lpPos = 0;
    uint256 private lpTokenDivThres;
    uint256 private divLpHolderAmount;
    uint256 public lpDivInterval = 7 days;
    uint256 public lastLpDivEndTime = block.timestamp;
    UsdtWrap RECV;

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived
    );

    constructor(address yhRefer) ERC20("YH", "YH") {

        _refer = IYHRefer(yhRefer);
        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        // Create a uniswap pair for this new token
        
        uniswapV2Pair = IUniswapV2Pair(IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), address(usdt)));
        IUniswapV2Pair uniswapV2PairBNB = IUniswapV2Pair(IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH()));
        ammPairs[address(uniswapV2Pair)] = true;
        ammPairs[address(uniswapV2PairBNB)] = true;
        _approve(address(this), address(uniswapV2Router), ~uint256(0));
        excludeFromFees(address(this), true);
        excludeFromFees(address(uniswapV2Router),true);
        excludeFromFees(_airdropAddr, true);
        excludeFromFees(_nftStakeAddr, true);
        excludeFromFees(_daoAddr, true);
        excludeFromFees(_lpRewardAddr, true);
        excludeFromFees(_p2eAddr, true);
        excludeFromFees(_m2eAddr, true);
        excludeFromFees(_proj1Addr, true);
        excludeFromFees(_proj2Addr, true);
        excludeFromFees(_burnAddr, true);
        excludeFromFees(_fundAddr, true);
        excludeFromFees(_holdDAddr, true);
        excludeFromFees(_tFeeAddr, true);
        excludeFromFees(_adpAddr, true);
        RECV = new UsdtWrap(IERC20(address(this)));

        _mint(_airdropAddr, 500000 * 10**18);
        _mint(_nftStakeAddr, 500000 * 10**18);
        _mint(_daoAddr, 1000000 * 10**18);
        _mint(_lpRewardAddr, 1000000 * 10**18);
        _mint(_p2eAddr, 2500000 * 10**18);
        _mint(_m2eAddr, 2500000 * 10**18);
        _mint(_proj1Addr, 500000 * 10**18);
        _mint(_proj2Addr, 500000 * 10**18);
        _mint(_burnAddr, 1000000 * 10**18);

        divLpHolderAmount = 1 * 10**16;
        lpTokenDivThres = 1 * 10**18;
    }

    receive() external payable {}


    function setLpDivInterval(uint256 _interval) external onlyOwner {
        lpDivInterval = _interval;
    } 

    function setlpDivThres(uint256 _thres) public onlyOwner {
        lpTokenDivThres = _thres;
    }

    function setDivLpHolderAmount(uint256 amount) public onlyOwner {
        divLpHolderAmount = amount;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function setAmmPairs(address pair, bool isPair) public onlyOwner {
        ammPairs[pair] = isPair;
    }

    function lpDividendProc(address[] memory lpAddresses)
        private
    {
        for(uint256 i = 0 ;i< lpAddresses.length;i++){
             if(lpPush[lpAddresses[i]] && (uniswapV2Pair.balanceOf(lpAddresses[i]) < divLpHolderAmount||_bexAddress[lpAddresses[i]])){
                _clrLpDividend(lpAddresses[i]);
             }else if(!Address.isContract(lpAddresses[i]) && !lpPush[lpAddresses[i]] && !_bexAddress[lpAddresses[i]]&& uniswapV2Pair.balanceOf(lpAddresses[i]) >= divLpHolderAmount){
                _setLpDividend(lpAddresses[i]);
             }
        }
    }

    function setExAddress(address exa) public onlyOwner {
        require( !_bexAddress[exa]);
        _bexAddress[exa] = true;
        _exIndex[exa] = _exAddress.length;
        _exAddress.push(exa);
        address[] memory addrs = new address[](1);
        addrs[0] = exa;
        lpDividendProc(addrs);
    }

    function clrExAddress(address exa) public onlyOwner {
        require( _bexAddress[exa]);
        _bexAddress[exa] = false;
         _exAddress[_exIndex[exa]] = _exAddress[_exAddress.length-1];
        _exIndex[_exAddress[_exAddress.length-1]] = _exIndex[exa];
        _exIndex[exa] = 0;
        _exAddress.pop();
        address[] memory addrs = new address[](1);
        addrs[0] = exa;
        lpDividendProc(addrs);
    }

    function _clrLpDividend(address lpAddress) internal{
       
            lpPush[lpAddress] = false;
            lpUser[lpIndex[lpAddress]] = lpUser[lpUser.length-1];
            lpIndex[lpUser[lpUser.length-1]] = lpIndex[lpAddress];
            lpIndex[lpAddress] = 0;
            lpUser.pop();
    }

    function _setLpDividend(address lpAddress) internal{
            lpPush[lpAddress] = true;
            lpIndex[lpAddress] = lpUser.length;
            lpUser.push(lpAddress);
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }
    
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        //require(to != address(0), "ERC20: transfer to the zero address");
        bool takeFee = true;
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }
        if (takeFee) { 
            if(ammPairs[to] || ammPairs[from]){
                bool isAdd = false;
                bool isDel = false;
                (isAdd,isDel) = getLPStatus(from,to,amount);
                if(isAdd || isDel){
                    uint256 share = amount.div(100);
                    _takeAdLpFee(from,to,share.mul(3));
                    super._transfer(from, _adpAddr, share.mul(2));
                    amount = amount.sub(share.mul(5));
                }else{
                    uint256 share = amount.div(100);
                    _takeInviterFee(from,to,share.mul(inviterFee)); 
                    super._transfer(from, _fundAddr, share.mul(fundFee));
                    super._transfer(from, _holdDAddr, share.mul(holdDFee));
                    super._transfer(from, _burnAddr, share.mul(burnFee));
                    super._transfer(from, address(this), share.mul(lpFee));
                    amount = amount.sub(share.mul(totalFee));
                }
            }else{
                uint256 share = amount.div(100);
                super._transfer(from, _tFeeAddr, share.mul(tFee));
                amount = amount.sub(share.mul(tFee));
            }
        }
        
        super._transfer(from, to, amount);
        if(lastAddress == address(0)){
            address[] memory addrs = new address[](2);
            addrs[0] = from;
            addrs[1] = to;
            lpDividendProc(addrs);
        }else{
            address[] memory addrs = new address[](3);
            addrs[0] = from;
            addrs[1] = to;
            addrs[2] = lastAddress;
            lastAddress = address(0);
            lpDividendProc(addrs);
        }
        if(ammPairs[to]){
            lastAddress = from;
        }
        if( lastLpDivEndTime.add(lpDivInterval) <= block.timestamp && !_isExcludedFromFees[from]){
            _splitlpToken();
        }
        
    }

    function rescueToken(address tokenAddress, uint256 tokens)
    public
    onlyOwner
    returns (bool success)
    {
        return IERC20(tokenAddress).transfer(msg.sender, tokens);
    }

    function _takeInviterFee(
        address from,
        address to,
        uint256 iAmount
    ) private {
        address cur;
        address receiver;
        if (ammPairs[from]) {
            cur = to;
        } else {
            cur = from;
        }
        uint256 rAmount = iAmount;
        uint256 rate ;
        address ra = _refer.rootAddr();
        for (uint256 i = 0; i < 20; i++) {
            if(i < 5){
                rate = 4;
            }else if (i<10){
                rate = 2;
            }else if (i<20){
                rate = 1;
            }
            cur = _refer.user_referrer(cur);
            if (cur == address(0)|| cur == ra) {
                receiver = ra;
                super._transfer(from, receiver, rAmount);
                return;
            }else{
                receiver = cur;
            }
            uint256 amount = iAmount.mul(rate).div(40);
            super._transfer(from, receiver, amount);
            rAmount = rAmount.sub(amount);
        }
    }

     function _takeAdLpFee(
        address from,
        address to,
        uint256 iAmount
    ) private {
        address cur;
        address reciver;
        if (ammPairs[from]) {
            cur = to;
        } else {
            cur = from;
        }
        uint256 rAmount = iAmount;
        uint256 rate ;
        address ra = _refer.rootAddr();
        for (uint256 i = 0; i < 5; i++) {
            if(i < 5){
                rate = 1;
            }
            cur =  _refer.user_referrer(cur);
            if (cur == address(0)|| cur == ra) {
                reciver = ra;
                super._transfer(from, reciver, rAmount);
                return;
            }else{
                reciver = cur;
            }
            uint256 amount = iAmount.mul(rate).div(5);
            super._transfer(from, reciver, amount);
            rAmount = rAmount.sub(amount);
        }
    }
    
    function _splitlpToken() private {
        uint256 thisAmount = balanceOf(address(this));
        if(thisAmount < lpTokenDivThres) return;
        if(lpPos >= lpUser.length && lpUser.length > 0){
          lpPos = 0;
          lastLpDivEndTime = block.timestamp;
          return;
        }
        if(lpUser.length > 0 ){
                uint256 procMax = oneDividendNum;
                if(lpPos + oneDividendNum > lpUser.length)
                        procMax = lpUser.length - lpPos;
                uint256 procPos = lpPos + procMax;
                for(uint256 i = lpPos;i < procPos && i < lpUser.length;i++){
                    if(uniswapV2Pair.balanceOf(lpUser[i]) < divLpHolderAmount){
                        _clrLpDividend(lpUser[i]);
                    }
                }
        }
        if(lpUser.length == 0) return;      
        uint256 totalAmount = 0;
        uint256 num = lpUser.length >= oneDividendNum ? oneDividendNum:lpUser.length;
        if(lpPos + num > lpUser.length)
                num = lpUser.length - lpPos;
        totalAmount = uniswapV2Pair.totalSupply();
        for(uint256 i = 0; i < _exAddress.length;i++){
            totalAmount = totalAmount.sub(uniswapV2Pair.balanceOf(_exAddress[i]));
        }
        if(totalAmount == 0) return;
        uint256 dAmount;
        for(uint256 i=0;i<num;i++){
            address user = lpUser[(lpPos+i).mod(lpUser.length)];
            if(user != address(0xdead) ){
                if(uniswapV2Pair.balanceOf(user) >= divLpHolderAmount){
                    dAmount = uniswapV2Pair.balanceOf(user).mul(thisAmount).div(totalAmount);
                    if(dAmount>0){
                        super._transfer(address(this),user,dAmount);
                    }
                }
            }
        }
        lpPos = (lpPos+num).mod(lpUser.length);
        if(lpPos == 0){
            lastLpDivEndTime = block.timestamp;
        }
    }

    function getlpsize() public view returns (uint256) {
        return lpUser.length;
    }

    function getLPStatus(address from,address to,uint256 amount)  internal view  returns (bool isAdd,bool isDel){
        IUniswapV2Pair pair;
        amount = amount;
        address token = address(this);
        if(ammPairs[to]){
            pair = IUniswapV2Pair(to);
        }else{
            pair = IUniswapV2Pair(from);
        }
        isAdd = false;
        isDel = false;
        address token0 = pair.token0();
        address token1 = pair.token1();
        (uint r0,uint r1,) = pair.getReserves();
        uint bal1 = IERC20(token1).balanceOf(address(pair));
        uint bal0 = IERC20(token0).balanceOf(address(pair));
        if (ammPairs[to]) {
            if (token0 == token) {
                if (bal1 > r1) {
                    uint change1 = bal1 - r1;
                    isAdd = change1 > 1000;
                }
            } else {
                if (bal0 > r0) {
                    uint change0 = bal0 - r0;
                    isAdd = change0 > 1000;
                }
            }
        }else {
            if (token0 == token) {
                if (bal1 < r1 && r1 > 0) {
                    uint change1 = r1 - bal1;
                    isDel = change1 > 0;
                }
            } else {
                if (bal0 < r0 && r0 > 0) {
                    uint change0 = r0 - bal0;
                    isDel = change0 > 0;
                }
            }
        }
        return (isAdd,isDel);
    }

}
