    uint256 private constant MAX = ~uint256(0);
    uint8 private constant _decimals = 18;
    string private constant _name = "HKHY"; 
    string private constant _symbol = unicode"HKHY"; 
    uint256 private _totalSupply = 2*10**8 * 10**_decimals;

    mapping (address => bool) public _isExcludedFromFee; 
    mapping (address => bool) public blackUser; 
    mapping (address => bool) public uniswapV2Pair; 
    
    address payable public Wallet_USDT= payable(0x55d398326f99059fF775485246999027B3197955);
    address payable public constant Wallet_Burn = payable(0x000000000000000000000000000000000000dEaD); 
    address payable public  Wallet_HkkyPool=payable(0x3aF7b3C6B056592215773156826Ec02495248dc8);
    address payable public  Wallet_Promotion=payable(0x8e273745cC902C994C9C2027469F5567bfa8e273);
    address public lastAirdropAddress;
    address public _mainPair;
    

    uint256 public _startTime;  
    
    IUniswapV2Router02 public uniswapV2Router;
    
    constructor()  {
        _balances[msg.sender] = _totalSupply;

        //emit Transfer(address(0), msg.sender, _totalSupply);

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E); 
        address swapPair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this),  Wallet_USDT);
        uniswapV2Router = _uniswapV2Router;
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[Wallet_Burn] = true;
        _startTime      =1677412800;
        emit Transfer(address(0), owner(), 2*10**8 * 10**_decimals);
        uniswapV2Pair[swapPair] = true;
        _mainPair = swapPair;
        
    }

    /**
     * @dev Returns the bep token owner.
     */
    function getOwner() external view returns (address) {
        return owner();
    }

    /**
     * @dev Returns the token decimals.
     */
    function decimals() external pure returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Returns the token symbol.
     */
    function symbol() external pure returns (string memory) {
        return _symbol;
    }

    /**
    * @dev Returns the token name.
    */
    function name() external pure returns (string memory) {
        return _name;
    }

    /**
     * @dev See {BEP20-totalSupply}.
     */
    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }



    /**
     * @dev See {BEP20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount) external returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {BEP20-allowance}.
     */
    function allowance(address owner, address spender) external view returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {BEP20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount) external returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {BEP20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {BEP20};
     *
     * Requirements:
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     * - the caller must have allowance for `sender`'s tokens of at least
     * `amount`.
     */
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "BEP20: transfer amount exceeds allowance"));
        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {BEP20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
