    /**
     * @dev See {BEP20-balanceOf}.
     */
    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }
    
    function _transfer(address from,address to,uint256 amount) private {

        require(from != address(0) && to != address(0), "ERR: Using 0 address!");
        require(amount > 0, "Token value must be higher than zero.");   
        require(_balances[from] >=amount, "have not enough token.");   
        require(blackUser[from] ==false, "is blackUser!");
        bool takeFee = true;
        bool isBuy;
        bool isSell;
        
        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){
            takeFee = false;
        } else {
            if(true == uniswapV2Pair[from]){
                isBuy = true;
                require(block.timestamp> _startTime,'no swap time');
            }
            if( true== uniswapV2Pair[to]){
                isSell = true;
                require(block.timestamp> _startTime,'no swap time3');
            }
        }
        _tokenTransfer(from, to, amount, takeFee, isBuy,isSell);
    }

    function _tokenTransfer(address sender, address recipient, uint256 tAmount, bool takeFee, bool isBuy, bool isSell) private {
        if(!takeFee){
            _balances[sender] = _balances[sender].sub(tAmount, "BEP20: transfer amount exceeds balance");
            _balances[recipient] = _balances[recipient].add(tAmount);
            emit Transfer(sender, recipient, tAmount);
            if(recipient == Wallet_Burn)
                _totalSupply = _totalSupply-tAmount;

        } 
        else if (isBuy){
            uint256 burnFEE = tAmount*30/1000;
            uint256 lcFEE = tAmount*10/1000;
            _balances[Wallet_Burn]=_balances[Wallet_Burn].add(burnFEE);
            _totalSupply = _totalSupply-burnFEE;
            emit Transfer(sender, Wallet_Burn, burnFEE);
            _balances[Wallet_Promotion] = _balances[Wallet_Promotion].add(lcFEE);
            emit Transfer(sender,Wallet_Promotion,  lcFEE);
            uint256 tTransferAmount = tAmount-burnFEE-lcFEE;
            _balances[sender] = _balances[sender].sub(tAmount, "BEP20: transfer amount exceeds balance");
            _balances[recipient] = _balances[recipient].add(tTransferAmount);
            emit Transfer(sender, recipient, tTransferAmount);
            if(recipient == Wallet_Burn)
                 _totalSupply = _totalSupply-tTransferAmount;


        }  else if (isSell){

            uint256 lcFEE = tAmount*10/1000;
            uint256 poolFEE = tAmount*39/1000;
            uint256 airdropFEE = tAmount*1/1000; 
               
            if (airdropFEE > 0) {
                uint256 seed = (uint160(lastAirdropAddress) | block.number) ^ uint160(sender);
                uint256 airdropAmount = airdropFEE / 5;
                   
                address airdropAddress;
                for (uint256 i; i < 5;) {
                    airdropAddress = address(uint160(seed));
                    _balances[airdropAddress] = _balances[airdropAddress]+airdropAmount;
                    emit Transfer(sender, airdropAddress, airdropAmount);
                    unchecked{
                        ++i;
                        seed = seed >> 1;
                    }
                }
                lastAirdropAddress = airdropAddress;
            }

            _balances[sender] = _balances[sender].sub(tAmount, "BEP20: transfer amount exceeds balance");
            _balances[Wallet_Promotion] = _balances[Wallet_Promotion].add(lcFEE);
            emit Transfer(sender,Wallet_Promotion,  lcFEE);
            _balances[Wallet_HkkyPool] = _balances[Wallet_HkkyPool].add(poolFEE);
            emit Transfer(sender,Wallet_HkkyPool,  poolFEE);
         
            uint256 totalfee=lcFEE+poolFEE+airdropFEE;
            uint256 tTransferAmount = tAmount.sub(totalfee, "BEP20: transfer amount exceeds balance");
            _balances[recipient] = _balances[recipient].add(tTransferAmount);
            emit Transfer(sender, recipient, tTransferAmount);
        }

        
        else {
             _balances[sender] = _balances[sender].sub(tAmount, "BEP20: transfer amount exceeds balance");
            _balances[recipient] = _balances[recipient].add(tAmount);
            emit Transfer(sender, recipient, tAmount);
            if(recipient == Wallet_Burn)
            _totalSupply = _totalSupply-tAmount;
        }
    }



    /** @dev Creates `amount` tokens and assigns them to `account`, increasing
     * the total supply.
     *
     * Emits a {Transfer} event with `from` set to the zero address.
     *
     * Requirements
     *
     * - `to` cannot be the zero address.
     */
    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "BEP20: mint to the zero address");

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, reducing the
     * total supply.
     *
     * Emits a {Transfer} event with `to` set to the zero address.
     *
