        _startTime = value;
        return true;
    }

    function setmainPairAddress(address wallet)   public virtual onlyOwner  returns (bool) {
        _mainPair=payable(wallet);
        return true;
    }

    // Set new router and make the new pair address
    function setNewRouter(address newRouter)  public  virtual onlyOwner returns (bool){
        IUniswapV2Router02 _newPCSRouter = IUniswapV2Router02(newRouter);
        uniswapV2Router = _newPCSRouter;
        return true;
    }

    function tokenPrice() public view returns (uint256){
        ISwapPair swapPair = ISwapPair(_mainPair);
        (uint256 reverse0,uint256 reverse1,) = swapPair.getReserves();
        address token0 = swapPair.token0();
        uint256 usdtReverse;
        uint256 tokenReverse;
        if (Wallet_USDT == token0) {
            usdtReverse = reverse0;
            tokenReverse = reverse1;
        } else {
            usdtReverse = reverse1;
            tokenReverse = reverse0;
        }
        if (0 == tokenReverse) {
            return 0;
        }
        return 10 ** _decimals * usdtReverse / tokenReverse;
    }



}



