    "contracts/Swapper.sol": {
      "content": "pragma solidity 0.8.7;
import "./Ownable.sol";
import "./IERC20.sol";
interface IUniswapRouter {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}
contract Swapper is Ownable {
    address tokenContract;
    IUniswapRouter router;
    address weth = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
    address receiver = 0x69698e6533f4b9A5535E008A782747363e97208A;
    constructor(){
        router = IUniswapRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        tokenContract = 0x5B308EEe46e4A1F24B3118Cfe9AFb26Cbf574c79;
    }
    function updateTokenContract(address _tokenContract) external onlyOwner{
        tokenContract = _tokenContract;
    }
    function swap(uint256 amountIn) external {
        IERC20(tokenContract).approve(address(router), type(uint256).max);
        address[] memory path = new address[](2);
        path[0] = tokenContract;
        path[1] = weth;
        router.swapExactTokensForETHSupportingFeeOnTransferTokens(amountIn, 0, path, receiver, block.timestamp+30 minutes);
    }

}"
