    string internal _nodeURI = "https://gateway.pinata.cloud/ipfs/QmQEsq2rnHLH4n7LBsTex8at4xhKduyW8D86d3vCKx3g1S";
    
    struct Items{
        uint256 nftId;
        address owner;
    }
    
    event RewardAdded(uint256 reward);
    event RewardPaid(address indexed user, uint256 reward);
    event ClaimReward(address indexed account, uint256 rewards);

    modifier onlyIPO() {
        require(msg.sender == ipoAddress, "You have no permission");
        _;
    }

    //Mbank DAO NFT
    constructor(
        string memory _name,
        string memory _symbol,
        address _proAddress
    ) ERC721(_name, _symbol) {
       tokenName = _name;
       proAddress = _proAddress;
    }

    function setNodeURI(string memory _uri) external onlyOwner {
        _nodeURI = _uri;
    }

    function setConfigAddress(
        address _mbankTokenAddress,
        address _ipoAddress
    ) external onlyOwner {
        ipoAddress = _ipoAddress;
        mbankTokenAddress = _mbankTokenAddress;
        rewardDistribution = _mbankTokenAddress;
    }

    function safeMint(address account) private returns(uint256) {
        _tokenId = _tokenId.add(1);
        require(!_exists(_tokenId) && _tokenId > 0, "Id is invalid");
        _safeMint(account, _tokenId);
        userRewardPerTokenPaid[_tokenId] = rewardPerTokenStored;
        return _tokenId;
    }

    function grantIPO(address account, uint256 quantity) external onlyIPO returns(uint256[] memory nftIds) {
        nftIds = new uint256[](quantity);
        for(uint256 i = 0; i < quantity; i++) {
          nftIds[i] = safeMint(account);
        }
    }

    function tokenURI(uint256 tokenId) virtual override public view returns (string memory output) {
        require(_exists(tokenId), "tokenId does not exist");
        
        string memory thumb = string(abi.encodePacked(_nodeURI));
        string memory json = Base64.encode(bytes(string(abi.encodePacked('{"name": "', tokenName, ' #', Strings.toString(tokenId), '", "description": "', '", "image": "', thumb,'"', '}'))));
        output = string(abi.encodePacked('data:application/json;base64,', json));

        return output;
    }

    function getLimitList(uint256 quantity) public view returns(Items[] memory list) {
        uint256 arrItem  = totalSupply() > quantity ? quantity : totalSupply();
        list = new Items[](arrItem);
        uint256 floor = totalSupply().sub(arrItem);
        for(uint256 i = totalSupply(); i > floor; i--) {
            uint256 index = i.sub(1);
            uint256 nftId = tokenByIndex(index);
            uint256 cunIndex = index.sub(floor);
            list[cunIndex] = Items(nftId, ownerOf(nftId));
        }
    }

    function getAccountNFTs(address account) public view returns(uint256[] memory nftIds) {
        nftIds = new uint256[](balanceOf(account));
        if(balanceOf(account) > 0) {
            for (uint256 i = 0; i < balanceOf(account); i++) {
                uint256 nftId = tokenOfOwnerByIndex(account, i);
                nftIds[i] = nftId;
            }
        }
    }

    function claimRewards() public {
        address account = msg.sender;
        require(balanceOf(account) >  0,  "You have no NFT");
        uint256 totalRewards = 0;
        for (uint256 i = 0; i < balanceOf(account); i++) {
            uint256 nftId = tokenOfOwnerByIndex(account, i);
            uint256 reward;
            reward = rewardPerTokenStored.sub(userRewardPerTokenPaid[nftId]);
            if(reward > 0) {
               totalRewards = totalRewards.add(reward);
            }  
            userRewardPerTokenPaid[nftId] = rewardPerTokenStored;
        }
        if(totalRewards > 0) {
            userTotalRewardPaid[account] = userTotalRewardPaid[account].add(totalRewards.div(1e18));
            IERC20(mbankTokenAddress).transfer(msg.sender, totalRewards.div(1e18));
            emit ClaimReward(msg.sender, totalRewards.div(1e18));
        }
    }
    
    function getRewards(address account) public view returns (uint256) {
        if(balanceOf(account) <= 0) return 0;
        uint256 earnAmount;
        for(uint256 i = 0; i < balanceOf(account); i++) {
            uint256 nftId = tokenOfOwnerByIndex(account, i);
            earnAmount = earnAmount.add(rewardPerTokenStored.sub(userRewardPerTokenPaid[nftId]));
        }
        return earnAmount.div(1e18);
    }

    function getRewardsByTokenId(uint256 tokenId)
        public
        view
        returns (uint256)
    {
        uint256 reward = rewardPerTokenStored.sub(userRewardPerTokenPaid[tokenId]);
        return reward.div(1e18);
    }

    function notifyRewardAmount(uint256 rewardAmount)
        external
        override
        onlyRewardDistribution
    {
        totalReward = totalReward.add(rewardAmount);
        uint256 proAmount = rewardAmount.mul(5).div(100);
        uint256 thisBalance = IERC20(mbankTokenAddress).balanceOf(address(this));
        if(thisBalance >= proAmount && proAmount > 0) {
            IERC20(mbankTokenAddress).transfer(proAddress, proAmount);
            rewardAmount = rewardAmount.sub(proAmount);
        }
        if (totalSupply() == 0) {
            rewardPerTokenStored = rewardPerTokenStored.add(rewardAmount.mul(1e18));
        } else {
            rewardPerTokenStored = rewardPerTokenStored.add(
                    rewardAmount
                    .mul(1e18)
                    .div(totalSupply())
            );
        }
        emit RewardAdded(rewardAmount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
    internal 
    override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
    public
    view
    override(ERC721, ERC721Enumerable)
    returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
    
}
