    "contracts/BigGLottery.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol';
import '@openzeppelin/contracts/utils/math/SafeMath.sol';

import "./interfaces/IERC20Extended.sol";
import './PriceOracle.sol';
import './LottoTicket.sol';

contract BigGLottery is Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    PriceOracle public priceOracle;

    bool public isActive = false;
    bool public inRound = false;

    LottoTicket public lottoTicketNFT;
    string private ticketBaseURI = "";

    uint256 public constant MAX_TICKET_COUNT = 10000;
    address private constant BUSD = 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56; // BSC Mainnet

    uint256 public lottoRound = 0;
    uint256 public ticketCount = 1000;
    uint256 public ticketPrice = 10; // initial price is 10$
    uint256 public oneTimeLimit = 5;

    uint256 public winnerRate = 5000;

    uint256 private totalSold = 0;
    uint256 private numAvailableTickets = 0;
    uint256[] private availableTickets;

    address[] public tokens;
    mapping(address => bool) tokenEnabled;
    mapping(address => bool) tokenExists;

    struct Round {
        address ticketNFT;
        uint256 ticketPrice;
        uint256 ticketCount;
        uint256 totalSold;
        uint256 winnerRate;
        address winner;
        uint256 drawnNum;

        mapping(address => uint256[]) soldTickets;
    }
    mapping(uint256 => Round) public roundInfo;

    address public walletA;


    event StatusUpdated(bool active);
    event RoundStarted(uint256 round, address ticketNFT, uint256 ticketCount, uint256 price);
    event RoundFinished(uint256 round, address ticketNFT, uint256 soldCount, uint256 tokenId, address winner, uint256 rate);

    event TicketNFTCreated(address ticketNFT, uint256 count);
    event TicketBaseURIUpdated(string uri);

    event TicketSold(address account, address ticketNFT, uint256 count);
    event OneTimeLimitUpdated(uint256 count);
    event TicketPriceUpdated(uint256 price);
    event TicketCountChanged(uint256 count);

    event WinnerRateUpdated(uint256 rate);

    event PayTokenAvailableChanged(address token, bool enabled);
    event PaymentTokenAdded(address token, bool enabled);

    event PriceOracleUpdated(address oracle);    
    event WalletAddressUpdated(address wallet);


    modifier onlyActive() {
        require(isActive && totalSold < ticketCount, "not active");
        _;
    }

    constructor(PriceOracle _priceOracle) {
        priceOracle = _priceOracle;

        walletA = msg.sender;
    }

    function buyTickets(address _token, uint256 _count) external payable onlyActive nonReentrant {
        require(inRound == true, "Round not started");

        require(_count > 0, "zero count");
        require(_count <= oneTimeLimit, "exceed one-time sale limit");
        require(_count <= numAvailableTickets, "not enough panda");

        require(tokenEnabled[_token] == true, "token is not enabled");

        uint256 fee = calculateTokenAmount(_token, _count);
        if(_token == address(0x0)) {
            require(msg.value >= fee, "Too little sent, please send more eth");

            if(msg.value > fee) {
                payable(msg.sender).transfer(msg.value - fee);
            }
        } else {
            require(IERC20Extended(_token).transferFrom(msg.sender, address(this), fee), "failed to transfer tokens");
        }

        Round storage _round = roundInfo[lottoRound];

        for (uint256 i = 0; i < _count; i++) {
            uint256 randomId =  _randomAvailableTicket(_count, i);
            numAvailableTickets = numAvailableTickets.sub(1);

            lottoTicketNFT.mint(msg.sender, randomId);
            totalSold = totalSold.add(1);

            _round.totalSold = _round.totalSold.add(1);
            _round.soldTickets[msg.sender].push(randomId);
        }

        emit TicketSold(msg.sender, address(lottoTicketNFT), _count);
    }

    function _randomAvailableTicket(uint256 _numToFetch, uint256 _idx) internal returns (uint256) {
        uint256 randomNum =
            uint256(
                keccak256(
                    abi.encode(
                        msg.sender,
                        tx.gasprice,
                        block.number,
                        block.timestamp,
                        blockhash(block.number - 1),
                        _numToFetch,
                        _idx
                    )
                )
            );

        uint256 randomIndex = randomNum % numAvailableTickets;

        uint256 valAtIndex = availableTickets[randomIndex];
        uint256 result;
        if (valAtIndex == 0) {
            // This means the index itself is still an available token
            result = randomIndex;
        } else {
            // This means the index itself is not an available token, but the val at that index is.
            result = valAtIndex;
        }

        uint256 lastIndex = numAvailableTickets - 1;
        if (randomIndex != lastIndex) {
            // Replace the value at randomIndex, now that it's been used.
            // Replace it with the data from the last index in the array, since we are going to decrease the array size afterwards.
            uint256 lastValInArray = availableTickets[lastIndex];
            if (lastValInArray == 0) {
                // This means the index itself is still an available token
                availableTickets[randomIndex] = lastIndex;
            } else {
                // This means the index itself is not an available token, but the val at that index is.
                availableTickets[randomIndex] = lastValInArray;
            }
        }

        return result;
    }

    function _rewardsDividend(address winner) internal {
        uint256 tokenAmt = 0;
        for(uint i = 0; i < tokens.length; i++) {
            if(tokenEnabled[tokens[i]] == false) continue;

            if(tokens[i] == address(0x0)) {
                tokenAmt = address(this).balance;
            } else {
                tokenAmt = IERC20Extended(tokens[i]).balanceOf(address(this));
            }
            if(tokenAmt == 0) continue;

            uint256 winnerReward = tokenAmt.mul(winnerRate).div(10000);
            if(winnerReward > 0) {
                _transferToken(tokens[i], winner, winnerReward);
            }

            _transferToken(tokens[i], walletA, tokenAmt.sub(winnerReward));
        }
    }

    function _transferToken(address _token, address _to, uint256 _amount) internal {
        if(_token == address(0x0)) {
            payable(_to).transfer(_amount);
        } else {
            IERC20Extended(_token).transfer(_to, _amount);
        }
    }

    function holdingTickets(address _user, uint256 _round) external view returns(uint256[] memory) {
        return roundInfo[_round].soldTickets[_user];
    }

    function calculateTokenAmount(address _token, uint256 _count) public view returns(uint256){
        uint256 usdAmt = ticketPrice.mul(_count);

        uint256 decimals = 18;
        if(_token != address(0x0)) {
            decimals = IERC20Extended(_token).decimals();
        }

        uint256 busdPrice = priceOracle.getTokenPrice(BUSD);
        uint256 tokenPrice = priceOracle.getTokenPrice(_token);

        return usdAmt.mul(busdPrice).mul(10**decimals).div(tokenPrice);
    }

    function remainTickets() external view returns(uint256) {
        return numAvailableTickets;
    }


    function startRound() external onlyActive onlyOwner {
        require(inRound == false, "Round already started");

        inRound = true;
        lottoRound = lottoRound.add(1);

        address _ticketNFT;
        bytes memory bytecode = type(LottoTicket).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(lottoRound, ticketCount, ticketBaseURI));
        assembly {
            _ticketNFT := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        LottoTicket(_ticketNFT).initialize(msg.sender, ticketCount, ticketBaseURI);
        emit TicketNFTCreated(_ticketNFT, ticketCount);


        lottoTicketNFT = LottoTicket(_ticketNFT);

        totalSold = 0;
        numAvailableTickets = ticketCount;
        availableTickets = new uint256[](ticketCount);

        Round storage _round = roundInfo[lottoRound];
        _round.ticketNFT = _ticketNFT;
        _round.ticketCount = ticketCount;
        _round.ticketPrice = ticketPrice;
        _round.winnerRate = winnerRate;


        emit RoundStarted(lottoRound, _ticketNFT, ticketCount, ticketPrice);
    }

    function stopRound(uint256 _tokenId) external onlyOwner nonReentrant {
        require(inRound == true, "Round already finished");
        require(_tokenId > 0 && _tokenId < ticketCount, "Invalid ticket");
        require(availableTickets[_tokenId] > 0, "not sold ticket");

        inRound = false;

        address winner = lottoTicketNFT.ownerOf(_tokenId);
        _rewardsDividend(winner);

        Round storage _round = roundInfo[lottoRound];
        _round.winner = winner;
        _round.drawnNum = _tokenId;

        emit RoundFinished(lottoRound, address(lottoTicketNFT), totalSold, _tokenId, winner, winnerRate);
    }

    function setActive(bool _isActive) external onlyOwner {
        require(inRound == false, "Round already started");

        isActive = _isActive;
        emit StatusUpdated(_isActive);
    }

    function addPaymentToken(address _token, bool _enabled) external onlyOwner {
        require(inRound == false, "cannot update in round");
        require(tokenExists[_token] == false, "token already set");

        tokens.push(_token);
        tokenExists[_token] = true;
        tokenEnabled[_token] = _enabled;

        emit PaymentTokenAdded(_token, _enabled);
    }

    function setPayTokenEnabled(address _token, bool _enabled) external onlyOwner {
        require(inRound == false, "cannot update in round");
        require(tokenExists[_token] == true, "token not added");

        tokenEnabled[_token] = _enabled;

        emit PayTokenAvailableChanged(_token, _enabled);
    }

    function setWinnerRate(uint _rate) external onlyOwner {
        require(inRound == false, "cannot update price in round");
        require(_rate < 10000, "Invalid rate");

        winnerRate = _rate;
        emit WinnerRateUpdated(_rate);
    }

    function setTicketPrice(uint _price) external onlyOwner {
        require(inRound == false, "cannot update price in round");

        ticketPrice = _price;
        emit TicketPriceUpdated(_price);
    }

    function setTicketCount(uint _count) external onlyOwner {
        require(inRound == false, "cannot update price in round");
        require(_count < MAX_TICKET_COUNT, "cannot exceed max: 10000");
        require(_count >= 10, "small tickets");

        ticketCount = _count;
        emit TicketCountChanged(_count);
    }

    function setTicketBaseURI(string memory _uri) external onlyOwner {
        require(inRound == false, "cannot update price in round");

        ticketBaseURI = _uri;
        emit TicketBaseURIUpdated(_uri);
    }

    function setOneTimeLimit(uint _count) external onlyOwner {
        require(_count <= 100, "cannot exceed 100");

        oneTimeLimit = _count;
        emit OneTimeLimitUpdated(_count);
    }

    function setWalletA(address _addr) external onlyOwner {
        require(_addr != address(0x0), "Invalid address");
        require(_addr != walletA, "already set");

        walletA = _addr;

        emit WalletAddressUpdated(_addr);
    }

    function setPriceOracle(address _priceOracle) external onlyOwner {
        require(_priceOracle == address(0x0), "invalid address");
        require(inRound == false, "cannot update in round");

        priceOracle = PriceOracle(_priceOracle);
        emit PriceOracleUpdated(_priceOracle);
    }

    receive() external payable {}
}"
