    "contracts/LottoTicket.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import '@openzeppelin/contracts/utils/math/SafeMath.sol';

contract LottoTicket is ERC721URIStorage {
    using SafeMath for uint256;
    using Strings for uint256;

    address public LOTTO_FACTORY;
    address public owner;
    
    uint256 public supply;

    uint256 public totalMinted;
    string  private _tokenBaseURI = "";

    event BaseURIUpdated(string uri);

    constructor() ERC721("BIG G Lotto Ticket", "BGLT") {
        LOTTO_FACTORY = msg.sender;
    }

    function initialize(address _owner, uint256 ticketCount, string memory _uri) external {
        require(msg.sender == LOTTO_FACTORY, 'Lotto Ticket: FORBIDDEN');

        supply = ticketCount;
        _tokenBaseURI = _uri;

        owner = _owner;

        emit BaseURIUpdated(_uri);
    }

    // called once by the factory at time of deployment
    function setTokenBaseURI(string memory _uri) public {
        require(msg.sender == owner, 'Lotto Ticket: FORBIDDEN'); 
        
        _tokenBaseURI = _uri;
        emit BaseURIUpdated(_uri);
    }

    function mint(address toAddr, uint256 tokenId) external {
        require(msg.sender == LOTTO_FACTORY, 'Lotto Ticket: FORBIDDEN'); 

        _safeMint(toAddr, tokenId);
        super._setTokenURI(tokenId, tokenId.toString());

        totalMinted = totalMinted.add(1);
    }

    function tokenURI(uint256 tokenId) public view override(ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }
    
    function _baseURI() internal view override returns (string memory) {
        return _tokenBaseURI;
    }
   
}"
