    "contracts/ViewGangsPower.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
// Authored by Plastic Digits
pragma solidity >=0.8.19;
import "./LocationController.sol";
import "./Gangs.sol";
import "./EntityStoreERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "./LocTemplateResource.sol";

contract ViewGangsPower {
    LocationController public immutable locationController;
    Gangs public immutable gangs;
    IERC20 public immutable bandits;
    EntityStoreERC20 public immutable entityStoreERC20;

    constructor(
        LocationController _locationController,
        Gangs _gangs,
        IERC20 _bandits,
        EntityStoreERC20 _entityStoreERC20
    ) {
        locationController = _locationController;
        gangs = _gangs;
        bandits = _bandits;
        entityStoreERC20 = _entityStoreERC20;
    }

    function viewOnly_localGangsPower(
        LocTemplateResource location
    )
        external
        view
        returns (
            uint256[] memory gangIds,
            uint256[] memory gangBandits,
            uint256[] memory gangPowers
        )
    {
        gangIds = locationController.viewOnly_getAllLocalEntitiesFor(
            location,
            gangs
        );
        gangBandits = new uint256[](gangIds.length);
        gangPowers = new uint256[](gangIds.length);
        for (uint256 i; i < gangIds.length; i++) {
            uint256 gangId = gangIds[i];
            gangBandits[i] = entityStoreERC20.getStoredER20WadFor(
                gangs,
                gangId,
                bandits
            );
            gangPowers[i] = location.gangPower(gangId);
        }
    }
}
