pragma solidity 0.8.20;






/**
 * @title BanterBucks
 * @notice This contract represents a token called "BanterBucks," extending the standard ERC20 token with additional features.
 * It includes mechanisms for token minting with a halving interval, transaction tax, and burning. This is paired with the BanterBucks NFTs to create a supercharged generalational wealth oppertunity with infinte banking protocols funding the treasury. The contract owner can update tax rates, burn rates, DEX addresses, and recover ERC20 tokens sent to the contract.
 * @dev Inherits the ERC20 contract from OpenZeppelin, which provides the standard ERC20 functions, and Ownable, which restricts certain functions to the contract owner until renounced.
 * BanterBucks Tokens used to buy NFTs will be burned. Doing so immediately makes this project significantly deflationary.
 */
contract BanterBucks is ERC20, Ownable, ReentrancyGuard {
    // Define the initial supply and halving interval for token minting.
    uint256 public constant INITIAL_SUPPLY = 360000000000 * 10 ** 18;
    uint256 public constant HALVING_INTERVAL = 21000000;

    // Variables for minting and tax rates.
    uint256 public MINT_PER_BLOCK = 4096 * 10 ** 18;
    uint256 public sellTaxRate = 12;
    uint256 public transferTaxRate = 3;
    uint256 public burnRate = 5;

    // Variables for tracking the start block, the last halving, and the last minted block.
    uint256 public startBlock;
    uint256 public lastHalvingBlock;
    uint256 public lastMintedBlock; // Tracks the last block where tokens were minted.

    // Wallets for specific purposes.
    address public burnAddress = 0x000000000000000000000000000000000000dEaD;
    address public taxCollector;
    address public exchangePool;
    address public recoveryWallet; // Wallet to which recovered tokens are sent for added security before liquidation for the treasury.

    // Mappings to handle tax exemptions and known DEX addresses.
    mapping(address => bool) public isTaxExempt;
    mapping(address => bool) public isDexAddress;

    // Events to log significant contract activities.
    event TaxRateChanged(uint256 newSellTaxRate, uint256 newTransferTaxRate);
    event BurnRateChanged(uint256 newBurnRate);
    event BurnAddressChanged(address newBurnAddress);
    event RecoveredERC20(address token, uint256 amount);
    event TokensMinted(uint256 amount, uint256 blockNumber);
    event TokensBurned(uint256 amount);
    event MintPerBlockChanged(uint256 newMintPerBlock);
    uint256 public constant MAX_TAX_RATE = 25; // Defines the maximum allowable tax rate.

    /**
     * @notice Constructs the BanterBucks token contract.
     * @param _taxCollector The wallet address to which all tax collections will be sent.
     * @param _recoveryWallet The wallet address to which recovered tokens will be sent.
     */
    constructor(
        address _taxCollector,
        address _recoveryWallet
    ) ERC20("BanterBucks", "BanterBucks") Ownable(msg.sender) {
        _mint(msg.sender, INITIAL_SUPPLY);
        taxCollector = _taxCollector;
        recoveryWallet = _recoveryWallet;
        isTaxExempt[_taxCollector] = true;

        startBlock = block.number;
        lastHalvingBlock = block.number;
        lastMintedBlock = block.number; // Initialize the lastMintedBlock to the current block.

        // Set initial DEX addresses
        address[] memory _initialDexAddresses = new address[](5);
        _initialDexAddresses[0] = 0x10ED43C718714eb63d5aA57B78B54704E256024E; // PancakeSwap Router V2
        _initialDexAddresses[1] = 0xC0788A3aD43d79aa53B09c2EaCc313A787d1d607; // ApeSwap Router
        _initialDexAddresses[2] = 0xCDe540d7eAFE93aC5fE6233Bee57E1270D3E330F; // BakerySwap Router
        _initialDexAddresses[3] = 0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3; // BurgerSwap Router
        _initialDexAddresses[4] = 0xbd67d157502A23309Db761c41965600c2Ec788b2; // JulSwap Router

        for (uint256 i = 0; i < _initialDexAddresses.length; i++) {
            isDexAddress[_initialDexAddresses[i]] = true;
        }
    }

    /**
     * @notice Overrides the standard ERC20 transfer function with added tax and burn.
     * @param recipient The address to receive the tokens.
     * @param amount The number of tokens to send.
     * @return Returns true if the operation was successful.
     */
    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        require(
            recipient != address(this),
            "Cannot transfer to the contract address"
        );

        bool isSale = isDexAddress[recipient];
        uint256 taxRate = isSale ? sellTaxRate : transferTaxRate;

        if (isTaxExempt[msg.sender] || isTaxExempt[recipient]) {
            super.transfer(recipient, amount);
        } else {
            uint256 taxAmount = (amount * taxRate) / 100;
            uint256 tokensToBurn = (taxAmount * burnRate) / 100;
            uint256 tokensToCollect = taxAmount - tokensToBurn;

            super.transfer(burnAddress, tokensToBurn);
            super.transfer(taxCollector, tokensToCollect);
            super.transfer(recipient, amount - taxAmount);

            emit TokensBurned(tokensToBurn);
        }

        mint(); // Call the mint function to mint new tokens upon every transfer.

        return true;
    }

    /**
     * @notice Mints new tokens based on the number of blocks since the last mint, with a halving mechanism.
     * @dev This function could be called by any transaction, ensuring that it's triggered consistently.
     */
    function mint() internal {
        uint256 blockCount = block.number - lastMintedBlock; // Calculate the number of blocks since the last mint
        if (blockCount == 0) {
            return; // If no new block has been created, do nothing
        }

        if (block.number - lastHalvingBlock >= HALVING_INTERVAL) {
            lastHalvingBlock = block.number;
            MINT_PER_BLOCK /= 2;
            emit MintPerBlockChanged(MINT_PER_BLOCK);
        }

        uint256 amount = MINT_PER_BLOCK * blockCount; // Calculate the total amount to mint
        _mint(address(this), amount);
        _transfer(address(this), exchangePool, amount); // Transfer the minted tokens to the ExchangePool

        emit TokensMinted(amount, block.number);

        lastMintedBlock = block.number; // Update the last minted block number
    }

    // Owner-only functions for contract management.

    function setExchangePool(
        address _exchangePool
    ) external onlyOwner nonReentrant {
        require(
            _exchangePool != address(0),
            "Exchange pool cannot be the zero address"
        );

        // If changing an existing exchange pool revoking the prior tax-exempt status
        if (exchangePool != address(0)) {
            isTaxExempt[exchangePool] = false;
        }

        exchangePool = _exchangePool;
        isTaxExempt[_exchangePool] = true; // Automatically exempt new exchange pool from taxes
    }

    function setTaxCollector(
        address newWallet
    ) external onlyOwner nonReentrant {
        require(
            newWallet != address(0),
            "Tax collector cannot be the zero address"
        );
        taxCollector = newWallet;
    }

    function setRecoveryWallet(
        address newWallet
    ) external onlyOwner nonReentrant {
        require(
            newWallet != address(0),
            "Recovery wallet cannot be the zero address"
        );
        recoveryWallet = newWallet;
    }

    function setTaxExemptStatus(
        address user,
        bool status
    ) external onlyOwner nonReentrant {
        isTaxExempt[user] = status;
    }

    function setDexAddress(
        address dexAddress,
        bool status
    ) external onlyOwner nonReentrant {
        isDexAddress[dexAddress] = status;
    }

    /**
     * @notice Sets the tax rates for selling and transferring, with a maximum limit.
     * @param newSellTaxRate The new tax rate for selling.
     * @param newTransferTaxRate The new tax rate for transfers.
     */
    function setTaxRates(
        uint256 newSellTaxRate,
        uint256 newTransferTaxRate
    ) external onlyOwner nonReentrant {
        require(
            newSellTaxRate <= MAX_TAX_RATE,
            "Sell tax rate exceeds maximum limit"
        );
        require(
            newTransferTaxRate <= MAX_TAX_RATE,
            "Transfer tax rate exceeds maximum limit"
        );
        sellTaxRate = newSellTaxRate;
        transferTaxRate = newTransferTaxRate;
        emit TaxRateChanged(newSellTaxRate, newTransferTaxRate);
    }

    function setBurnRate(uint256 newBurnRate) external onlyOwner nonReentrant {
        burnRate = newBurnRate;
        emit BurnRateChanged(newBurnRate);
    }

    function setBurnAddress(
        address newBurnAddress
    ) external onlyOwner nonReentrant {
        require(
            newBurnAddress != address(0),
            "Burn address cannot be the zero address"
        );
        burnAddress = newBurnAddress;
        emit BurnAddressChanged(newBurnAddress);
    }

    /**
     * @notice Allows the owner to recover ERC20 tokens sent to this contract's address.
     * @param tokenAddress The token contract address.
     * @param tokenAmount The number of tokens to recover.
     * These tokens will be liquidated and the returns added to the treasury value
     */
    function recoverERC20(
        address tokenAddress,
        uint256 tokenAmount
    ) external onlyOwner nonReentrant {
        IERC20(tokenAddress).transfer(recoveryWallet, tokenAmount);
        emit RecoveredERC20(tokenAddress, tokenAmount);
    }
}
