contract SaveRugDAO is Ownable, ERC20Burnable {
    using Address for address;
    using SafeMath for uint256;
    bytes32 public immutable merkleRootRound1;
    bytes32 public immutable merkleRootRound2;
    bytes32 public immutable merkleRootRound3;
    bytes32 public immutable merkleRootRound4;
    bytes32 public immutable merkleRootRound5;
    bytes32 public merkleRootMissionRound;

    uint256 public MAX_SUPPLY = 1 * 10**12 * 10**18;

    uint256 public AMOUNT_AIRDROP = MAX_SUPPLY / 100 * 60;

    // LP
    uint256 public AMOUNT_LP = MAX_SUPPLY / 100 * 10;
    address public ADDR_LP = 0x7b5cc4f31c3d6D24D19A9B93B17221C8d0337945;

    // Single token incentives
    uint256 public AMOUNT_SINGLE = MAX_SUPPLY / 100 * 20;
    address public ADDR_SINGLE = 0xb157d6e0A8a1b337ce77884A98253900e4408DF4; 
    
    // LP Staking incentives
    uint256 public AMOUNT_STAKING = MAX_SUPPLY - (AMOUNT_AIRDROP + AMOUNT_LP + AMOUNT_SINGLE);
    address public ADDR_STAKING = 0x86560a50AFB8bc3f4B95F875f91F1a5c81B29d2B;

    address ADDR_FUND;
    address ADDR_MAINTANCE;
    uint256 transferFeeRate = 1;
    bool public regularRoundclaimable = false;
    bool public missionRoundclaimable = false;

    mapping(address => bool) internal _transactionFee;

    constructor(string memory name, string memory symbol, address a1, address a2, 
        bytes32 _merkleRoot1,
        bytes32 _merkleRoot2,
        bytes32 _merkleRoot3,
        bytes32 _merkleRoot4,
        bytes32 _merkleRoot5
    ) ERC20(name, symbol) {
        _mint(ADDR_STAKING, AMOUNT_STAKING);
        _mint(ADDR_LP, AMOUNT_LP);
        _mint(ADDR_SINGLE, AMOUNT_SINGLE);
        ADDR_FUND = a1;
        ADDR_MAINTANCE = a2;
        merkleRootRound1 = _merkleRoot1;
        merkleRootRound2 = _merkleRoot2;
        merkleRootRound3 = _merkleRoot3;
        merkleRootRound4 = _merkleRoot4;
        merkleRootRound5 = _merkleRoot5;
    }

    mapping(address => bool) public hasClaimed;
    mapping(address => bool) public missionClaimed;

    event Claim(address indexed to, uint256 amount);

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        if (
            transferFeeRate > 0 &&
            _transactionFee[recipient] == true &&
            recipient != address(0) &&
            ADDR_FUND != address(0) &&
            ADDR_MAINTANCE != address(0)
        ) {
            uint256 _fee = amount.mul(transferFeeRate).div(100);
            uint256 _fee1 = _fee.div(2);
            uint256 _fee2 = _fee.sub(_fee1);
            super._transfer(sender, ADDR_FUND, _fee1);
            super._transfer(sender, ADDR_MAINTANCE, _fee2);
            amount = amount.sub(_fee);
        }

        super._transfer(sender, recipient, amount);
    }

    function addTransferFeeAddress(address _transferFeeAddress)
        public
        onlyOwner
    {
        _transactionFee[_transferFeeAddress] = true;
    }
    
    function removeTransferFeeAddress(address _transferFeeAddress)
        public
        onlyOwner
    {
        _transactionFee[_transferFeeAddress] = false;
    }

    function claim(address to, uint256 amount, bytes32[] calldata proof) external {
        uint256 transferAmount = amount * 10**25;
        require(_msgSender() == to, "cannot claim for other address");
        require(totalSupply() + transferAmount < MAX_SUPPLY, "cannot exceed max supply");
        require(regularRoundclaimable == true, "regular round is not ready");
        if (hasClaimed[to]) revert("Already Claimed");
        bool isValidLeaf = regularRoundVerifySender(proof, to, amount);
        if (!isValidLeaf) revert("Not in regular round");
        hasClaimed[to] = true;
        _mint(to, transferAmount);
        emit Claim(to, transferAmount);
    }

    function setRegularRoundClaimbale(bool flag) onlyOwner external{
        regularRoundclaimable = flag;
    }

    function setMissionRoundClaimbale(bool flag) onlyOwner external{
        missionRoundclaimable = flag;
    }

    function regularRoundVerifySender(bytes32[] calldata proof, address to, uint256 amount) internal view returns (bool) {
        bytes32 leaf = keccak256(abi.encodePacked(to, amount));
        bool v1 = MerkleProof.verify(proof, merkleRootRound1, leaf);
        bool v2 = MerkleProof.verify(proof, merkleRootRound2, leaf);
        bool v3 = MerkleProof.verify(proof, merkleRootRound3, leaf);
        bool v4 = MerkleProof.verify(proof, merkleRootRound4, leaf);
        bool v5 = MerkleProof.verify(proof, merkleRootRound5, leaf);
        return v1 || v2 || v3 || v4 || v5;
    }

    function MissionRoundClaim(address to, uint256 amount, bytes32[] calldata proof) external {
        uint256 transferAmount = amount * 10**25;
        require(_msgSender() == to, "cannot claim for other address");
        require(totalSupply() + transferAmount < MAX_SUPPLY, "cannot exceed max supply");
        require(missionRoundclaimable == true, "mission round is not ready");
        if (missionClaimed[to]) revert("Already Claimed Mission Round");
        bytes32 leaf = keccak256(abi.encodePacked(to, amount));
        bool isValidLeaf = MerkleProof.verify(proof, merkleRootMissionRound, leaf);
        if (!isValidLeaf) revert("Not in mission round");
        missionClaimed[to] = true;
        _mint(to, transferAmount);
        emit Claim(to, transferAmount);
    }

    function setMissionRoundMerkleTree(bytes32 _merkleRoot) onlyOwner external{
        merkleRootMissionRound = _merkleRoot;
    }

   function emergencyTransferETH(address to) onlyOwner external {
        uint256 amount = address(this).balance;
        if (amount > 0) {
            payable(to).transfer(amount);
        }
    }

    function emergencyTransferSourceToken(IERC20 tokenAddress, address to) onlyOwner external {
        uint256 amount = tokenAddress.balanceOf(address(this));
        SafeERC20.safeTransfer(tokenAddress, to, amount);
    }
}
