pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";

import "@openzeppelin/contracts@4.9.0/access/Ownable.sol";
import "@openzeppelin/contracts@4.9.0/security/Pausable.sol";

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";


pragma solidity 0.8.8;

interface IBiBiShareHolder {

    function getBanlance() external view returns(uint256);

    function addBonus(uint256 bonusAmount) external;

    function withdrawReward(address to, uint256 amount) external;

}

contract BiBiToTheSaturn is VRFConsumerBaseV2, Pausable, Ownable{

    struct Pool {
        uint256 totalAmount;
        uint256 bonusAmount;
        uint256 accPerShare;
        address bibiToken;
        uint256 playBaseAmount;
        IBiBiShareHolder bibiShareHolder;
    }

    struct UserBiBi {
        uint256 amount;
        uint256 rewardDebt;
        uint256 lastNumber;
        uint8[3] lastRandomNumbers;
    }

    uint256 public id;
    Pool public pool;
    mapping(address => UserBiBi) public userBiBiMap;
    mapping(address => bool) public userLockStatus;

    VRFCoordinatorV2Interface COORDINATOR;
    uint64 s_subscriptionId;
    bytes32 s_keyHash;
    uint32 callbackGasLimit = 500000;
    uint16 requestConfirmations = 3;
    uint32 numWords = 3;
    mapping(uint256 => address) public chianLinkUser;


    constructor(
        uint64 subscriptionId
        ,address vrfCoordinator
        ,bytes32 keyHash
        ,address bibiToken
        ,uint256 playBaseAmount
        ,address biBiShareHolder
    ) VRFConsumerBaseV2(vrfCoordinator) {
        COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator);
        s_subscriptionId = subscriptionId;
        s_keyHash = keyHash;

        pool.bibiToken = bibiToken;
        pool.playBaseAmount = playBaseAmount;
        pool.bibiShareHolder = IBiBiShareHolder(biBiShareHolder);
    }

    function setPlayBaseAmount(uint256 amount) external onlyOwner {
        pool.playBaseAmount = amount;
    }

    function setBiBiShareHolder(address bibiShareHolder) external onlyOwner {
        pool.bibiShareHolder = IBiBiShareHolder(bibiShareHolder);
    }

    function getUserLastData(address user) external view returns(uint256 lastNumber, uint8[3] memory lastRandomNumbers) {
        UserBiBi memory userBiBi = userBiBiMap[user];

        lastNumber = userBiBi.lastNumber;
        lastRandomNumbers = userBiBi.lastRandomNumbers;
    }

    function getTotalAmount() external view returns(uint256) {
        uint256 thisBalance = IERC20(pool.bibiToken).balanceOf(address(this));
        return pool.bibiShareHolder.getBanlance() +thisBalance;
    }

    function requestRandomWords(address user) private {
        uint256 s_requestId = COORDINATOR.requestRandomWords(
            s_keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );

        chianLinkUser[s_requestId] = user;
    }

    function retryRequestRandomWords(address user) external onlyOwner {
        requestRandomWords(user);
    }

    function addBonus(uint256 bonusAmount) private {
        if(pool.totalAmount > 0) {
            pool.bonusAmount += bonusAmount;
            pool.accPerShare += bonusAmount*(1e16)/pool.totalAmount;
        }
    }

    function toTheSaturn(uint256 number) external whenNotPaused {
        require(!userLockStatus[_msgSender()], "BiBiToTheMars: Please wait");

        userLockStatus[_msgSender()] = true;

        uint256 playTotalAmount = number*pool.playBaseAmount;
        uint256 bonusAmount = playTotalAmount/20;
        TransferHelper.safeTransferFrom(pool.bibiToken, _msgSender(), address(pool.bibiShareHolder), bonusAmount);
        TransferHelper.safeTransferFrom(pool.bibiToken, _msgSender(), address(this), playTotalAmount-bonusAmount);
        addBonus(playTotalAmount/10);
        pool.bibiShareHolder.addBonus(bonusAmount);

        userBiBiMap[_msgSender()].lastNumber = number;
        id +=number;

        requestRandomWords(_msgSender());
    }

    function queryBonus(address user) external view returns(uint256) {
        UserBiBi memory userBiBi = userBiBiMap[user];
        uint256 bonusAmount = ((userBiBi.amount*pool.accPerShare)-userBiBi.rewardDebt)/(1e16);
        if(bonusAmount > userBiBi.amount) {
            return userBiBi.amount;
        }
        return bonusAmount;
    }

    function withdrawBonus() external whenPaused {
        UserBiBi storage userBiBi = userBiBiMap[_msgSender()];
        uint256 bonusAmount = ((userBiBi.amount*pool.accPerShare)-userBiBi.rewardDebt)/(1e16);
        if(bonusAmount > userBiBi.amount) {
            bonusAmount = userBiBi.amount;
        }

        userBiBi.amount = 0;
        userBiBi.rewardDebt = 0;

        TransferHelper.safeTransfer(pool.bibiToken, _msgSender(), bonusAmount);
    }

    function withdrawAll(address user, uint256 withdrawBonusAmount, uint256 addBonusAmount, UserBiBi storage userBiBi) private {
        pool.totalAmount -= userBiBi.amount;
        pool.bonusAmount -= withdrawBonusAmount;

        addBonus(addBonusAmount);

        userBiBi.amount = 0;
        userBiBi.rewardDebt = 0;

        TransferHelper.safeTransfer(pool.bibiToken, user, withdrawBonusAmount - addBonusAmount);
    }

    function updateStakeAmount(address user, uint256 userTotalReward) private {
        UserBiBi storage userBiBi = userBiBiMap[user];

        uint256 buyAmount = userBiBi.lastNumber*pool.playBaseAmount;

        if(userTotalReward > 0) {
            uint256 bonusAmount = ((userBiBi.amount*pool.accPerShare)-userBiBi.rewardDebt)/(1e16);
            uint256 withdrawAmount = userTotalReward-buyAmount;

            if(withdrawAmount > userBiBi.amount) {
                withdrawAll(user, bonusAmount, bonusAmount, userBiBi);
                return;
            }

            if((bonusAmount+withdrawAmount) > userBiBi.amount) {
                uint256 withdrawBonusAmount = userBiBi.amount - withdrawAmount;
                withdrawAll(user, bonusAmount, bonusAmount-withdrawBonusAmount, userBiBi);
                return;
            }

            pool.totalAmount -= bonusAmount+withdrawAmount;
            pool.bonusAmount -= bonusAmount;

            userBiBi.amount -= bonusAmount+withdrawAmount;
            userBiBi.rewardDebt = userBiBi.amount*pool.accPerShare;

            TransferHelper.safeTransfer(pool.bibiToken, user, bonusAmount);

            return;
        }

        pool.totalAmount += buyAmount;
        userBiBi.amount += buyAmount;
        userBiBi.rewardDebt += buyAmount*pool.accPerShare;
    } 

    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
        address user = chianLinkUser[requestId];
        require(userLockStatus[user], "BiBiToTheMars: Please wait");

        uint8 randomNum0 = uint8(randomWords[0]%6);
        uint8 randomNum1 = uint8(randomWords[1]%6);
        uint8 randomNum2 = uint8(randomWords[2]%6);

        UserBiBi storage userBiBi = userBiBiMap[user];
        userBiBi.lastRandomNumbers=[randomNum0, randomNum1, randomNum2];

        uint256 userTotalReward = 0;
        if((randomNum0 <3) &&(randomNum1 <3) && (randomNum2 <3)) {
            userTotalReward = pool.playBaseAmount*userBiBi.lastNumber*5;
        }

        if(userTotalReward > 0) {
            TransferHelper.safeTransfer(pool.bibiToken, user, userTotalReward);
        }

        updateStakeAmount(user, userTotalReward);

        userLockStatus[user] = false;
        delete chianLinkUser[requestId];
    }

    function queryDevBonus() public view returns(uint256) {
        uint256 balance = IERC20(pool.bibiToken).balanceOf(address(this));
        return balance-pool.bonusAmount;
    }

    function withdrawDevBonus(uint256 amount) external onlyOwner {
        require(amount <= queryDevBonus(), "BiBiShareHolder: Insufficient balance");

        TransferHelper.safeTransfer(pool.bibiToken, _msgSender(), amount);
    }

    function safeTransferToken(address token, address to, uint256 value) external onlyOwner {
        TransferHelper.safeTransfer(token, to, value);
    }

    function safeTransferETH(address to, uint value) external onlyOwner {
        TransferHelper.safeTransferETH(to, value);
    }

    function pause() external virtual onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() external virtual onlyOwner whenPaused {
        _unpause();
    }

}"
