      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import './interface/IUniswapV2Router02.sol';
import './interface/IUniswapV2Factory.sol';
import './interface/IUniswapV2Pair.sol';
import "./utils/Constants.sol";

contract MCA is ERC20 {
    using SafeMath for uint256;

    IUniswapV2Router02 private router;

    address private pair;

    address private addLiquidityAddress;

    address private feeAddress;

    address private admin;

    address private poolAdddress;

    uint256 private beDestroyTotal;

    address private destroyAddress;

    bool private isPaused;

    uint256 private destroyTotal;

    mapping(address => bool) private whitelist;

    event SwapToWeight(
        uint256 amountIn,
        address[] path
    );

    modifier onlyAdmin() {
        require(msg.sender == admin, "101");
        _;
    }

    constructor(
        address _lq,
        address _feeAddress,
        address _poolAdddress,
        address _destroyAddress)
        ERC20("Mecca Token", "Mecca Token")
    {
        _mint(_lq, 21000000 * 10 ** 18);
        router = IUniswapV2Router02(Constants.routerAddress);
        pair = IUniswapV2Factory(router.factory()).createPair(address(this), Constants.usdtAddress);
        addLiquidityAddress = _lq;
        feeAddress = _feeAddress;
        poolAdddress = _poolAdddress;
        destroyAddress = _destroyAddress;
        admin = msg.sender;
        isPaused = true;
        whitelist[Constants.routerAddress] = true;
        whitelist[pair] = true;
        whitelist[addLiquidityAddress] = true;
        whitelist[admin] = true;
        whitelist[feeAddress] = true;
        whitelist[poolAdddress] = true;
        whitelist[destroyAddress] = true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "ERC20: transfer amount zero");
        if (
         address(this) != from && address(this) != to
         && addLiquidityAddress != from && addLiquidityAddress != to
         && (pair == from || pair == to)
        ) {
            if (pair == to && whitelist[from] == false) {
                beDestroyTotal += amount;
                uint256 fee = amount.mul(15).div(100);
                super._transfer(from, address(this), fee);
                swapToWeight();
                super._transfer(from, to, amount.sub(fee));
            } else {
                if (isPaused) {
                    require(whitelist[to] == true, "100");
                }
                super._transfer(from, to, amount);
            }
        } else {
            if (from == destroyAddress && beDestroyTotal > 0) {
                super._transfer(pair, Constants.burnAddress, beDestroyTotal);
                beDestroyTotal = 0;
                IUniswapV2Pair(pair).sync();
            }
            super._transfer(from, to, amount);
        }
    }

    function destroy(uint256 amount) public onlyAdmin {
        _transfer(address(this), Constants.burnAddress, amount);
        destroyTotal += amount;
    }

    function destroyTotalOf() public view returns (uint256){
        return destroyTotal;
    }

    function swapToWeight() private {
        uint256 weightFee = balanceOf(address(this));
        if (weightFee > 0) {
            address[] memory path = new address[](2);
            path[0] = address(this);
            path[1] = Constants.usdtAddress;
            _approve(address(this), address(router), weightFee);
            router.swapExactTokensForTokensSupportingFeeOnTransferTokens(weightFee, 0, path, feeAddress, block.timestamp);
            emit SwapToWeight(weightFee, path);
        }
    }

     function BeDestroySetZron() public onlyAdmin {
        beDestroyTotal = 0;
    }

    function setPoolAdddress(address _address) public onlyAdmin {
        whitelist[_address] = true;
        poolAdddress = _address;
    }

    function addWhitelist(address _address) public onlyAdmin{
        whitelist[_address] = true;
    }

    function removeWhitelist(address _address) public onlyAdmin{
        whitelist[_address] = false;
    }

    function setAdmin(address _address) public onlyAdmin {
        admin = _address;
    }

    function setAddLiquidityAddress(address _address) public onlyAdmin {
        addLiquidityAddress = _address;
    }

    function onPaused() public onlyAdmin {
        isPaused = true;
    }

    function offPaused() public onlyAdmin {
        isPaused = false;
    }
}
"
