    IPancakePair public pair;
    mapping(address => bool) public automatedMarketMakerPairs;
    address[] public pairs;

    //0 normal, 1 white, 2 exchange
    mapping(address => uint) public extConfig;

    //0 white only, 1 open
    uint public buySTATE;
    uint public sellSTATE;

    uint public buyCommunity = 50;
    uint public buyLP = 10;
    uint public sellFoundation = 30;
    uint public sellCommunity = 40;
    uint public sellLP = 15;
    uint public sellBurn = 5;
    address public foundation;
    address public community;
    address public lpReceiver;

    TokenGuardian public tokenGuardian;

    bool private inSwap;

    enum TransferType {
        Normal,
        BuyFromLP,
        SellToLp
    }

    event Sell(address indexed addr, uint256 amount);

    /**
     * @dev Mints `initialSupply` amount of token and transfers them to `owner`.
     *
     * See {ERC20-constructor}.
     */
    constructor(string memory _name, string memory _symbol, uint256 _initialSupply, address _receiver, address _foundation, address _community, address _lpReceiver, address _USDT, address _router) ERC20(_name, _symbol) {
        _mint(_receiver, _initialSupply);
        ethxSetExtConfig(_receiver, 1);
        ethxSetFoundation(_foundation);
        ethxSetCommunity(_community);
        ethxSetLpReceiver(_lpReceiver);
        ethxSetRouter(_USDT, _router);
    }

    function ethxSetRouter(address _USDT, address _router) private onlyOwner {
        USDT = IERC20(_USDT);
        router = IPancakeRouter(_router);

        address _pair = IPancakeFactory(router.factory()).getPair(address(_USDT), address(this));
        if (_pair == address(0)) {
            _pair = IPancakeFactory(router.factory()).createPair(address(_USDT), address(this));
        }
        pair = IPancakePair(_pair);

        automatedMarketMakerPairs[_pair] = true;

        pairs.push(_pair);

        super._approve(address(this), address(router), ~uint256(0));
        USDT.approve(address(router), ~uint256(0));
        tokenGuardian = new TokenGuardian(_USDT);
    }

    function ethxSetPair(address _pair, bool _value) public onlyOwner {
        automatedMarketMakerPairs[_pair] = _value;
        if (_value) {
            pairs.push(_pair);
        }
    }

    function _transfer(address from, address to, uint256 amount) internal virtual override {
        TransferType _transferType = _getTransferType(from, to);
        uint s1 = extConfig[from];
        uint s2 = extConfig[to];

        uint256 fee = 0;

        if (_transferType == TransferType.BuyFromLP && !inSwap) {
            require(buySTATE > 0 || s1 > 0 || s2 > 0, "Cannot be buy if not in the whitelist");
            if (s2 == 0) {
                if (buyCommunity > 0) {
                    uint256 buyCommunityFee = amount * buyCommunity / 1000;
                    if (buyCommunityFee > 0) {
                        super._transfer(from, community, buyCommunityFee);
                        fee += buyCommunityFee;
                    }
                }
                if (buyLP > 0) {
                    uint buyLPFee = amount * buyLP / 1000;
                    if (buyLPFee > 0) {
                        fee += buyLPFee;
                        super._transfer(from, address(this), buyLPFee);
                    }
                }
            }
        }
        if (_transferType == TransferType.SellToLp && !inSwap) {
            require(sellSTATE > 0 || s1 > 0 || s2 > 0, "Cannot be sell if not in the whitelist");
            if (s1 == 0) {
                if (sellFoundation > 0) {
                    uint256 sellFoundationFee = amount * sellFoundation / 1000;
                    if (sellFoundationFee > 0) {
                        super._transfer(from, foundation, sellFoundationFee);
                        fee += sellFoundationFee;
                    }
                }
                if (sellCommunity > 0) {
                    uint256 sellCommunityFee = amount * sellCommunity / 1000;
                    if (sellCommunityFee > 0) {
                        super._transfer(from, community, sellCommunityFee);
                        fee += sellCommunityFee;
                    }
                }
                if (sellBurn > 0) {
                    uint256 sellBurnFee = amount * sellBurn / 1000;
                    if (sellBurnFee > 0) {
                        super._transfer(from, address(0x000000000000000000000000000000000000dEaD), sellBurnFee);
                        fee += sellBurnFee;
                    }
                }
                if (sellLP > 0) {
                    uint sellLPFee = amount * sellLP / 1000;
                    if (sellLPFee > 0) {
                        fee += sellLPFee;
                        super._transfer(from, address(this), sellLPFee);
                    }
                }
                uint256 balance = balanceOf(address(this));
                uint256 pairBalance = balanceOf(address(pair));
                if (balance > pairBalance / 1000) {
                    balance = pairBalance / 1000;
                }
                if (balance > 0) {
                    ethxSwapEthx2USDT(balance, address(tokenGuardian));
                }
                emit Sell(from, amount);
            }
        }

        super._transfer(from, to, amount.sub(fee));
    }

    function ethxSwapEthx2USDT(uint256 _amount, address _to) internal {
        inSwap = true;
        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(_amount / 2, 0, getUSDTPath(), _to, block.timestamp + 60);
        USDT.transferFrom(address(tokenGuardian), address(this), USDT.balanceOf(address(tokenGuardian)));
        router.addLiquidity(
            address(this),
            address(USDT),
            _amount / 2,
            USDT.balanceOf(address(this)),
            0,
            0,
            lpReceiver,
            block.timestamp
        );
        inSwap = false;
    }

    function getUSDTPath() public view returns (address[] memory) {
        address[] memory _path = new address[](2);
        _path[0] = address(this);
        _path[1] = address(USDT);
        return _path;
    }

    function ethxSetFoundation(address _receiver) public onlyOwner {
        foundation = _receiver;
        ethxSetExtConfig(_receiver, 1);
    }

    function ethxSetCommunity(address _receiver) public onlyOwner {
        community = _receiver;
        ethxSetExtConfig(_receiver, 1);
    }

    function ethxSetLpReceiver(address _receiver) public onlyOwner {
        lpReceiver = _receiver;
        ethxSetExtConfig(_receiver, 1);
    }

    function _isLp(address _addr) internal view returns (bool) {
        return automatedMarketMakerPairs[_addr];
    }

    function _getTransferType(address _from, address _to) internal view returns (TransferType) {
        if (_isLp(_from)) {
            return TransferType.BuyFromLP;
        }

        if (_isLp(_to)) {
            return TransferType.SellToLp;
        }

        return TransferType.Normal;
    }

    function ethxSetBuyState(uint _buySTATE) public onlyOwner {
        buySTATE = _buySTATE;
    }

    function ethxSetSellState(uint _sellSTATE) public onlyOwner {
        sellSTATE = _sellSTATE;
    }

    function ethxSetBuyCommunity(uint _buyCommunity) public onlyOwner {
        buyCommunity = _buyCommunity;
    }

    function ethxSetBuyLP(uint _buyLP) public onlyOwner {
        buyLP = _buyLP;
    }

    function ethxSetSellFoundation(uint _sellFoundation) public onlyOwner {
        sellFoundation = _sellFoundation;
    }

    function ethxSetSellCommunity(uint _sellCommunity) public onlyOwner {
        sellCommunity = _sellCommunity;
    }

    function ethxSetSellLP(uint _sellLP) public onlyOwner {
        sellLP = _sellLP;
    }

    function ethxSetSellBurn(uint _sellBurn) public onlyOwner {
        sellBurn = _sellBurn;
    }

    function ethxSetExtConfig(address account_, uint state_) public onlyOwner {
        extConfig[account_] = state_;
    }

    function ethxRescueCoin(
        address _token,
        address payable _to,
        uint256 _amount
    ) public onlyOwner {
        if (_token == address(0)) {
            (bool success,) = _to.call{gas: 23000, value: _amount}("");
            require(success, "transferETH failed");
        } else {
            IERC20(_token).transfer(_to, _amount);
        }
    }

    function pairsNums() public view returns (uint256) {
        return pairs.length;
    }

    // 1e18 units PJ token = how many units quote token
    function getPrice() public view returns (uint256) {
        address _token0 = pair.token0();
        (uint256 _reserve0, uint256 _reserve1,) = pair.getReserves();
        (uint256 _main, uint256 _quote) = address(USDT) == _token0
            ? (_reserve1, _reserve0)
            : (_reserve0, _reserve1);
        return _main == 0 ? 0 : _quote.mul(1e18).div(_main);
    }

    // 1e18 units LP token value = how many units quote token
    function getLpPrice() public view returns (uint256) {
        uint256 _total = pair.totalSupply();
        address _token0 = pair.token0();
        (uint256 _reserve0, uint256 _reserve1,) = pair.getReserves();
        uint256 _quote = address(USDT) == _token0 ? _reserve0 : _reserve1;
        return _total == 0 ? 0 : _quote.mul(2).mul(1e18).div(_total);
    }

    function getLpAddress() public view returns (address) {
        return address(pair);
    }
}
