     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

interface IFactory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}

interface IRouter {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);
    function addLiquidityETH(address token, uint256 amountTokenDesired, uint256 amountTokenMin, uint256 amountETHMin, address to, uint256 deadline) external payable returns (uint256 amountToken, uint256 amountETH, uint256 liquidity);
    function swapExactTokensForETHSupportingFeeOnTransferTokens(uint256 amountIn, uint256 amountOutMin, address[] calldata path, address to, uint256 deadline) external;
}

/**
 * @dev This is the main part of the smart contract..
 * You need to define variables like initial status, wallets and percentages.     
 *
 * Tokens usually opt for a value of 18 decimals, imitating the relationship between
 * Ether and Wei. This is the value this {ERC20} uses, unless this value is overwritten.
 *
 * ABOUT PERCENTAGES: 
 * For the params of MaxHold, MaxBuy, MaxSell or MaxTx we use the following formula:
 * 
 * MaxHold = 1% = 1000     
 * MaxHold = 0.1% = 100
 * MaxHold = 0.01% = 10
 * MaxHold = 0.001% = 1
 * 
 * So, this is formula is an example to set a 1% of total supply as MaxHold.
 * totalSupply * MaxHold / 100000 = 1% of total supply
 * totalSupply * MaxHold / 100000 = 0.1% of total supply
 * 
 * NOTE: The Max's cant be higher than 1% and the Min's cant be lower than 0.001%.
 */
contract BRAZUCAR is ERC20, Ownable {
    
    using Address for address payable;

    IRouter public router;
    address public pair;

    /** Trading **/
    bool private _internalLock = false;
    bool public providingLiquidity = false;
    bool public tradingEnabled = false;    
    uint256 public genesisblock;
    uint256 private deadline = 0;

    /** Antibots & Antiwhale **/            
    uint256 public maxHold = 2000;  // 2% of total supply
    uint256 public maxBuy = 1100; // 1.1% of total supply
    uint256 public maxSell = 1100; // 1.1% of total supply
    uint256 public maxTx = 1100; // 1.1% of total supply
    
    /** Liquidity **/
    uint256 public tokenLiquidityThreshold;     

    /** Wallets **/
    address public marketingWallet = 0x2d22077A4De3965fd31062a11356c5e532F18AEE;    
    address public physicalProjectWallet = 0x771548bB066A57eBD0EFa83cC22a9C0Ec0913c22; 
    address public teamWallet = 0x99b8A178508Ee640BcEf73AdD8d86D60E26b836A;
    address private devWallet = 0xffadCE9ee761cDDBB7a40DE328f9245bEE9f099F;    
	address public constant deadWallet = 0x000000000000000000000000000000000000dEaD;

    /** Taxes **/
    struct Taxes {
        uint256 marketing;
        uint256 team;
        uint256 physicalProject;
        uint256 liquidity;
        uint256 dev;       
    }

    Taxes public taxes = Taxes(3, 2, 3, 0, 2);
    Taxes public sellTaxes = Taxes(3, 4, 4, 0, 4); // at launch only - after launch default taxes

    uint256 private launchtax = 990;

    /** Exemptions **/
    mapping(address => bool) public exemptFee;

    /** Modifiers **/
    modifier lockSwap() {
        if (!_internalLock) {
            _internalLock = true;
            _;
            _internalLock = false;
        }
    }

    constructor(address _routerAddress, uint256 _initialSupply, string memory _name, string memory _symbol) ERC20(_name, _symbol) {

        _mint(msg.sender, _initialSupply * 1e18);
                        
        router = IRouter(_routerAddress);      
        pair = IFactory(router.factory()).createPair(address(this), router.WETH());

        /** Exemptions */
        exemptFee[msg.sender] = true; 
        exemptFee[address(this)] = true;
        exemptFee[marketingWallet] = true;
        exemptFee[teamWallet] = true;
        exemptFee[devWallet] = true;
        exemptFee[physicalProjectWallet] = true;
        exemptFee[deadWallet] = true;   
        exemptFee[0x407993575c91ce7643a4d4cCACc9A98c36eE1BBE] = true; // PinkLock's lockup address
        exemptFee[0x5D00661EA3c9b8f095520573b9B940B6fEbcfD8b] = true; // PinkSale's Multi-sender for distribute ether or tokens to multiple addresses

        /** Liquidity */
        tokenLiquidityThreshold = totalSupply() * 1 / 10000; // 0.01% of total supply

    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = allowance(sender, _msgSender());
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        _approve(sender, _msgSender(), currentAllowance - amount);
        return true;
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal override {
        require(amount > 0, "Transfer amount must be greater than zero");

        if (!exemptFee[sender] && !exemptFee[recipient]) {
            require(tradingEnabled, "Trading not enabled.");

            // Check amount is not exceeding MaxTx
            uint256 maxTxAmount = totalSupply() * maxTx / 100000;
            require(amount <= maxTxAmount, "Transfer amount exceeds the maxTx.");                                                   

            // Check amount is not exceeding MaxHold
            if(recipient != pair) {
                uint256 maxHoldAmount = totalSupply() * maxHold / 100000;
                require(balanceOf(recipient) + amount <= maxHoldAmount, "Transfer amount exceeds the maxHold.");
            }    
 
        }                          

        // Calculate Fees
        uint256 feeswap;
        uint256 fee;
        Taxes memory currentTaxes;

        bool useLaunchFee = !exemptFee[sender] &&
            !exemptFee[recipient] &&
            block.number < genesisblock + deadline;
        
        if (_internalLock || exemptFee[sender] || exemptFee[recipient])
            fee = 0;     
        //Sell                   
        else if (recipient == pair && !useLaunchFee) {
            // Check amount is not exceeding MaxSell
            uint256 maxSellAmount = totalSupply() * maxSell / 100000;
            require(amount <= maxSellAmount, "Transfer amount exceeds the maxSell.");
            // Calculate fees
            feeswap = sellTaxes.marketing + sellTaxes.team + sellTaxes.physicalProject + sellTaxes.liquidity + sellTaxes.dev;
            currentTaxes = sellTaxes;   
        //Buy
        } else if (sender == pair && !useLaunchFee) {
            // Check amount is not exceeding MaxBuy
            uint256 maxBuyAmount = totalSupply() * maxBuy / 100000;
            require(amount <= maxBuyAmount, "Transfer amount exceeds the maxBuy.");          
            // Calculate fees  
            feeswap = taxes.marketing + taxes.team + taxes.physicalProject + taxes.liquidity + taxes.dev;
            currentTaxes = taxes;
        //All        
        } else if (!useLaunchFee) {
            // Check amount is not exceeding MaxBuy
            uint256 maxTxAmount = totalSupply() * maxTx / 100000;
            require(amount <= maxTxAmount, "Transfer amount exceeds the maxTx.");
            // Calculate fees  
            feeswap = taxes.marketing + taxes.team + taxes.physicalProject + taxes.liquidity + taxes.dev;
            currentTaxes = taxes;
        //Launch
        } else if (useLaunchFee) {
            feeswap = launchtax;
        }

        fee = ((amount * feeswap) / 100);

        // Send fees if threshold has been reached
        // Don't do this on buys, breaks swap
        if (providingLiquidity && sender != pair) tradingFees(feeswap, currentTaxes);

        // Rest to recipient
        super._transfer(sender, recipient, amount - fee);
        if (fee > 0) {
            //send the fee to the contract
            if (feeswap > 0) {
                uint256 feeAmount = (amount * feeswap) / 100;
                super._transfer(sender, address(this), feeAmount);
            }
        }
    }

    function tradingFees(uint256 feeswap, Taxes memory swapTaxes) private lockSwap {

        if(feeswap == 0){
            return;
        }	

        uint256 contractBalance = balanceOf(address(this));
        if (contractBalance >= tokenLiquidityThreshold) {
            if (tokenLiquidityThreshold > 1) {
                contractBalance = tokenLiquidityThreshold;
            }

            // Split the contract balance into halves
            uint256 denominator = feeswap * 2;
            uint256 tokensToAddLiquidityWith = (contractBalance * swapTaxes.liquidity) / denominator;
            uint256 toSwap = contractBalance - tokensToAddLiquidityWith;
            uint256 initialBalance = address(this).balance;

            swapTokensForETH(toSwap);

            uint256 deltaBalance = address(this).balance - initialBalance;
            uint256 unitBalance = deltaBalance / (denominator - swapTaxes.liquidity);
            uint256 nativeCoinToAddLiquidityWith = (unitBalance * swapTaxes.liquidity);

            if (nativeCoinToAddLiquidityWith > 0) {                
                addLiquidity(tokensToAddLiquidityWith, nativeCoinToAddLiquidityWith);
            }

            uint256 marketingAmount = (unitBalance * 2 * swapTaxes.marketing);
            if (marketingAmount > 0) {
                payable(marketingWallet).sendValue(marketingAmount);
            }

            uint256 teamAmount = (unitBalance * 2 * swapTaxes.team);
            if (teamAmount > 0) {
                payable(teamWallet).sendValue(teamAmount);
            }

            uint256 devAmount = (unitBalance * 2 * swapTaxes.dev);
            if (devAmount > 0) {
                payable(devWallet).sendValue(devAmount);
            }

            uint256 physicalProjectAmount = (unitBalance * 2 * swapTaxes.physicalProject);
            if (physicalProjectAmount > 0) {
                payable(physicalProjectWallet).sendValue(physicalProjectAmount);
            }

        }
    }

    function swapTokensForETH(uint256 tokenAmount) private {
        // generate the pancake pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        _approve(address(this), address(router), tokenAmount);

        // make the swap
        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // Approve token transfer to cover all possible scenarios
        _approve(address(this), address(router), tokenAmount);

        // Add the liquidity
        router.addLiquidityETH{ value: ethAmount }(
            address(this),
            tokenAmount,
            0, // Slippage is unavoidable
            0, // Slippage is unavoidable
            owner(),
            block.timestamp
        );
    }

    function updateLiquidityProvide(bool state) external onlyOwner {
        // Update liquidity providing state
        providingLiquidity = state;
    }

    // Change this as soon as you know the total supply and before the deployer
    function updateLiquidityTreshhold(uint256 new_amount) external onlyOwner {
        require(new_amount >= 100,"Swap threshold amount should be lower or equal to 0.01% of tokens");
        require(new_amount <= 10000,"Swap threshold amount should be lower or equal to 1% of tokens");
        // Update the treshhold
        tokenLiquidityThreshold = new_amount * 10**decimals();
    }

    function setZeroBuyTax() external onlyOwner {
        taxes = Taxes(0, 0, 0, 0, 0);
    }

    function setAllZeroTax() external onlyOwner {
        sellTaxes= Taxes(0, 0, 0, 0, 0);
        taxes = Taxes(0, 0, 0, 0, 0);
    }

    function setDefaultTax() external onlyOwner {
        taxes = Taxes(3, 2, 3, 0, 2);
        sellTaxes= Taxes(3, 2, 3, 0, 2);
    }

    function setDoubleSellTax() external onlyOwner {
        taxes = Taxes(0, 0, 0, 0, 0);
        sellTaxes= Taxes(6, 4, 6, 0, 4);
    }

    function setBuyTaxesPerWallet(
        uint256 _marketing,
        uint256 _team,
        uint256 _physicalProject,
        uint256 _liquidity
    ) external onlyOwner {
        require((_marketing + _team + _physicalProject + _liquidity + 2) <= 25, "Must keep fees at 25% or less");
        taxes.marketing = _marketing;
        taxes.team = _team;
        taxes.physicalProject = _physicalProject;
        taxes.liquidity = _liquidity;
    }

    function setSellTaxesPerWallet(
        uint256 _marketing,
        uint256 _team,
        uint256 _physicalProject,
        uint256 _liquidity
    ) external onlyOwner {
        require((_marketing + _team + _physicalProject + _liquidity + 2) <= 25, "Must keep fees at 25% or less");
        sellTaxes.marketing = _marketing;
        sellTaxes.team = _team;
        sellTaxes.physicalProject = _physicalProject;
        sellTaxes.liquidity = _liquidity;
    }

    function setMaxTx(uint256 _maxTx) external onlyOwner {                
        require(_maxTx <= 100000, "Max Tx must be at most 100% of total supply");
        require(_maxTx >= 1,  "Max Tx must be at less 0.001% of total supply");
        maxTx = _maxTx;
    }
    
    function setMaxHold(uint256 _maxHold) external onlyOwner {                
        require(_maxHold <= 100000, "Max hold must be at most 100% of total supply");
        require(_maxHold >= 1,  "Max hold must be at less 0.001% of total supply");
        maxHold = _maxHold;
    }

    function setMaxBuy(uint256 _maxBuy) external onlyOwner {                
        require(_maxBuy <= 100000, "Max buy must be at most 100% of total supply");
        require(_maxBuy >= 1,  "Max buy must be at less 0.001% of total supply");
        maxBuy = _maxBuy;
    }

    function setMaxSell(uint256 _maxSell) external onlyOwner {                
        require(_maxSell <= 100000, "Max sell must be at most 100% of total supply");
        require(_maxSell >= 1,  "Max sell must be at less 0.001% of total supply");
        maxSell = _maxSell;
    }

    function enableTrading() external onlyOwner {
        require(!tradingEnabled, "Trading is already enabled");
        tradingEnabled = true;
        providingLiquidity = true;
        genesisblock = block.number;
    }

    function updateDeadline(uint256 _deadline) external onlyOwner {
        require(!tradingEnabled, "Can't change when trading has started");
        require (_deadline < 5,"Deadline should be less than 5");
        deadline = _deadline;
    }

    function updateMarketingWallet(address _wallet) external onlyOwner {
        marketingWallet = _wallet;
    }

    function updateTeamWallet(address _wallet) external onlyOwner {
        teamWallet = _wallet;
    }

    function updateDevWallet(address _wallet) external onlyOwner {
        devWallet = _wallet;
    }

    function updatePhysicalProjectWallet(address _wallet) external onlyOwner {
        physicalProjectWallet = _wallet;
    }

    function updateExemptFee(address _address, bool state) external onlyOwner {
        exemptFee[_address] = state;
    }

    function bulkExemptFee(address[] memory accounts, bool state) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            exemptFee[accounts[i]] = state;
        }
    }

    function rescueETH(uint256 weiAmount) external onlyOwner {
        payable(devWallet).transfer(weiAmount);
    }

    function rescueERC20(address tokenAdd, uint256 amount) external onlyOwner {
        IERC20(tokenAdd).transfer(devWallet, amount);
    }

    // Fallbacks
    receive() external payable {}
}
