interface IWrap {
	function withdraw() external;
    function transferOwnership(address newOwner) external;
}

contract ATM is Ownable, IERC20Metadata {
    using Address for address;
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    string private constant _name = "ATM";
    string private constant _symbol = "ATM";
    uint256 private constant _totalSupply = 3700000 * 1e18;
    uint256 public maxTxAmount = 1e18;
    address public wrap;

    /// @notice The EIP-712 typehash for the contract's domain
    bytes32 public constant DOMAIN_TYPEHASH = keccak256("EIP712Domain(string name,uint256 chainId,address verifyingContract)");

    /// @notice The EIP-712 typehash for the permit struct used by the contract
    bytes32 public constant PERMIT_TYPEHASH = keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");

    bytes32 public immutable DOMAIN_SEPARATOR;

    /// @notice A record of states for signing / validating signatures
    mapping (address => uint256) public nonces;
    
    mapping(address => bool) public isOtherSwapPair;

    uint256 private constant RATE_PERCISION = 10000;
    uint256 public buyFeeRate;
    uint256 public sellFeeRate;
    uint256 public liquidFeeRate;
    address public feeTo;
    address public team;

    IUniswapV2Router02 public uniswapV2Router;
    ISwapFactory public immutable didSwapFactory;
    address public immutable usdt;
    address public token;
    bool inSwapAndLiquify;

    // mainnet
    ISwapFactory public constant pancakeSwapFactory = ISwapFactory(address(0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73));

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor(
        address _initHolder,
        address _didSwapFactory,
        address _usdt,
        address _token,
        address _wrap,
        address _feeTo,
        address _team,
        address _router
    ){
        DOMAIN_SEPARATOR = keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes(_name)), ChainId.get(), address(this)));
        require(_didSwapFactory != address(0),"did swap factory can not be address 0");
        didSwapFactory = ISwapFactory(_didSwapFactory);
        usdt = _usdt;
        token = _token;
        feeTo = _feeTo;
        wrap = _wrap;
        team = _team;
        uniswapV2Router = IUniswapV2Router02(_router);

        address holder = _initHolder == address(0) ? msg.sender : _initHolder;
        _balances[holder] = _totalSupply;
        _approve(address(this), address(uniswapV2Router), ~uint256(0));
        IERC20(_usdt).approve(address(uniswapV2Router), ~uint256(0));
        emit Transfer(address(0), holder, _totalSupply);
    }

    function name() public pure override returns (string memory) {
        return _name;
    }

    function symbol() public pure override returns (string memory) {
        return _symbol;
    }

    function decimals() public pure override returns (uint8) {
        return 18;
    }

    function totalSupply() public pure override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function initFee() public onlyOwner {
        liquidFeeRate = 500;
        buyFeeRate = 100;
        sellFeeRate = 100;
    }

    function setWrap(address _wrap) public onlyOwner {
		wrap = _wrap;
	}

    function transferWrapOwnership(address _newWrapOwner) public onlyOwner {
		IWrap(wrap).transferOwnership(_newWrapOwner);
	}

    function setMaxTx(uint256 _maxTx) external onlyOwner {
        maxTxAmount = _maxTx;
    }

    function permit(address owner, address spender, uint amount, uint deadline, uint8 v, bytes32 r, bytes32 s) external {
        require(block.timestamp <= deadline, "ERC20permit: expired");
        bytes32 structHash = keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, amount, nonces[owner]++, deadline));
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, structHash));
        address signatory = ecrecover(digest, v, r, s);
        require(signatory != address(0), "ERC20permit: invalid signature");
        require(signatory == owner, "ERC20permit: unauthorized");

        _approve(owner, spender, amount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(sender, _msgSender(), currentAllowance - amount);
        }

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        // require(sender != address(0), "ERC20: transfer from the zero address");
        require(_balances[sender] >= amount, "ERC20: transfer amount exceeds balance");

        uint256 recipientAmount = amount;
        bool isBuy = isSwapPair(sender);
        bool isSell = isSwapPair(recipient);
       
        if(recipient != address(0) 
            && (isBuy || isSell) && sender != address(this)
        ){
            uint256 feeRate = isBuy ? buyFeeRate : sellFeeRate;
            uint256 feeAmount = amount.mul(feeRate) / RATE_PERCISION;
            recipientAmount -= feeAmount;
            _takeFee(sender, feeTo, feeAmount);

            if(isPancakePair(recipient)) {
                uint256 liquidAmount = amount.mul(liquidFeeRate) / RATE_PERCISION;
                recipientAmount -= liquidAmount;
                _takeFee(sender, address(this), liquidAmount);
                uint256 contractTokenBalance = balanceOf(address(this));
                if(!inSwapAndLiquify && contractTokenBalance >= maxTxAmount) {
                    // 卖触发加池
                    swapAndLiquify(contractTokenBalance);
                } 
            } 
        }
        
        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(recipientAmount);
        emit Transfer(sender, recipient, recipientAmount);
    }

    function _takeFee(address _from,address _to,uint256 _fee) internal {
        if(_fee > 0){
            _balances[_to] = _balances[_to].add(_fee);
            emit Transfer(_from, _to, _fee);
        }
    }

    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);
        uint256 initialBalance = IERC20(usdt).balanceOf(address(this));
        swapTokensForUsdt(half); 
        uint256 newBalance = IERC20(usdt).balanceOf(address(this)).sub(initialBalance);
        addLiquidity(otherHalf, newBalance);
    }

    function swapTokensForUsdt(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdt;

        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, 
            path,
            wrap,
            block.timestamp
        );     
        IWrap(wrap).withdraw(); 
    }

    function addLiquidity(uint256 tokenAmount, uint256 usdtAmount) private {
        uniswapV2Router.addLiquidity(
            address(this),
            usdt,
            tokenAmount,
            usdtAmount,
            0,
            0,
            team,
            block.timestamp
        );
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function isSwapPair(address pair) public view returns(bool){
        if(pair == address(0)){
            return false;
        }
        return didSwapFactory.getPair(address(this), token) == pair || isOtherSwapPair[pair];
    }

    function isPancakePair(address pair) public view returns(bool){
         if(pair == address(0)){
            return false;
        }
        return pancakeSwapFactory.getPair(address(this), usdt) == pair;
    }

    function addOtherSwapPair(address _swapPair) external onlyOwner {
        require(_swapPair != address(0),"_swapPair can not be address 0");
        isOtherSwapPair[_swapPair] = true;
    }

    function removeOtherSwapPair(address _swapPair) external onlyOwner {
        require(_swapPair != address(0),"_swapPair can not be address 0");
        isOtherSwapPair[_swapPair] = false;
    }

    function setBuyFeeRate(uint256 _rate) external onlyOwner {
        require(_rate < 5000,"rate too large");
        buyFeeRate = _rate;
    }

    function setSellFeeRate(uint256 _rate) external onlyOwner {
        require(_rate < 5000,"rate too large");
        sellFeeRate = _rate;
    }

    function setFeeTo(address _feeTo) external onlyOwner {
        feeTo = _feeTo;
    }

    function setTeam(address _team) external onlyOwner {
        team = _team;
    }
}
