pragma solidity ^0.8.0;

contract Erc721NFT is ERC721, Ownable {
    uint256 private tokenid;

    string private _baseURIextended;

    uint256 public constant tokenidMax = 1000;

    constructor() ERC721("LONGNFT", "LONGNFT") {
    }

    function mint(address _to) public onlyOwner {
        require(tokenid <= tokenidMax, "mint out");

        _mint(_to, tokenid);
        tokenid = tokenid + 1;
    }

    function batchmint(uint256 tokenIds, address _to) public onlyOwner {
        for (uint256 i = 0; i < tokenIds; i++) {
            mint(_to);
        }
    }

    function matchmint(
        address[] memory addressList,
        uint256[] memory amountList
    ) public onlyOwner {
        if (addressList.length > 0) {
            for (uint256 i = 0; i < addressList.length; i++) {
                batchmint(amountList[i], addressList[i]);
            }
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override(ERC721) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function setBaseURI(string memory baseURI_) external onlyOwner {
        _baseURIextended = baseURI_;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseURIextended;
    }

    function totalSupply() public view virtual returns (uint256) {
        return tokenid;
    }
}
