    "contracts/BinamarsManager.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.7.6;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interface/IBinamarsRouter.sol";

contract BinamarsManager is Ownable {
    using SafeMath for uint256;

    uint256 public latestTokenId;
    IBinamarsRouter public router;

    constructor(address _router) {
        router = IBinamarsRouter(_router);
    }

    modifier onlySpawner() {
        require(
            router.spawners(_msgSender()),
            "Call must come from Spawner."
        );
        _;
    }

    function priceEgg() public view returns (uint256) {
        return router.priceEgg();
    }

    function feeSpawnEggToBina() public view returns (uint256) {
        return router.feeSpawn();
    }
}
"
