contract KCCToken is IERC20, Ownable {
    using SafeMath for uint256;
    mapping(address => uint256) private _rOwned;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) isDividendExempt;
    mapping(address => bool) private _updated;

    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal;
    uint256 private _rTotal;

    string private _name;
    string private _symbol;
    uint256 private _decimals;
 
    uint256 private _fee0 = 1; //ref
    uint256 private _fee1 = 1; //dev
    address private _fee1Addr = 0x6E4a59Fbd2e4b53Cbe1096D773cd5CC7E23B9867;

    uint256 private _fee2 = 1; //mt
    address private _fee2Addr = 0xE2DbDA8A226aB85A01400733B5D93E8ea862D0bb;

    uint256 private _fee3 = 1; //node1
    address private _fee3Addr = 0x389dAa160a8804D189bEe5929C5974BC6F3995eC;

    uint256 private _fee4 = 1; //node2
    address private _fee4Addr = 0x4CcE8C3EcC1bD0Ba9d26C8d056B4CA33339f7098;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public uniswapV2Pair;
    //USDT 
    IERC20 public usdt = IERC20(0x55d398326f99059fF775485246999027B3197955);

    uint256 public currentIndex;
    uint256 public currentTotal;
    uint256 public distributorGas = 300000;
    uint256 public _lpFee = 1;
    uint256 public minPeriod = 1 days;
    uint256 public LPFeefenhong;
    uint256 public minFh = 1 * 10 ** 16;
    mapping(address => bool) public whitelist;
    mapping(address => bool) public blacklist;
    
    uint256 public maxRate = 990; //1000
    bool public onlyWhiteBuy = true;
    bool public onlyWhiteSell = true;

    address[] public shareholders;
    mapping(address => uint256) public shareholderIndexes;

    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;
    bool public tradeEnabled = true;

    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor(address owner1,address owner2) {
        _name = "KCC Token";
        _symbol = "KCC";
        _decimals = 18;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), address(usdt));
        uniswapV2Router = _uniswapV2Router;
 
        _tTotal = 10000000000 * 10 ** _decimals;
        _rTotal = (MAX - (MAX % _tTotal));
        uint256 amount50 = _rTotal.div(100).mul(50);
        _rOwned[owner1] = amount50;
        _rOwned[owner2] = amount50;

        _isExcludedFromFee[owner1] = true;
        _isExcludedFromFee[owner2] = true;
        _isExcludedFromFee[_fee1Addr] = true;
        _isExcludedFromFee[_fee2Addr] = true;
        _isExcludedFromFee[_fee3Addr] = true;
        _isExcludedFromFee[_fee4Addr] = true;
        _isExcludedFromFee[address(this)] = true;

        isDividendExempt[address(this)] = true;
        isDividendExempt[address(0)] = true;

        whitelist[owner1] = true;
        whitelist[owner2] = true;
        whitelist[uniswapV2Pair] = true;
        whitelist[_fee1Addr] = true;
        whitelist[_fee2Addr] = true;
        whitelist[_fee3Addr] = true;
        whitelist[_fee4Addr] = true;
        whitelist[address(0)] = true;
        whitelist[address(0xdead)] = true;
        whitelist[address(this)] = true;

        _owner = msg.sender;
        uint256 amount50t = _tTotal.div(100).mul(50);
        emit Transfer(address(0), owner1, amount50t);
        emit Transfer(address(0), owner2, amount50t);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function setFee(uint256 _fee, uint _index) public onlyOwner returns (bool)  {
        if (_index == 0) {
            _fee0 = _fee;
        } else if (_index == 1) {
            _fee1 = _fee;
        } else if (_index == 2) {
            _fee2 = _fee;
        } else if (_index == 3) {
            _fee3 = _fee;
        } else if (_index == 4) {
            _fee4 = _fee;
        } else {
            _lpFee = _fee;
        }
        return true;
    }

    function setMaxRate(uint256 _maxRate) external onlyOwner {
        maxRate = _maxRate;
    }

    function setMinFh(uint256 _minfh) external onlyOwner {
        minFh = _minfh;
    }

    function setFeeReceive(address _addr, uint _index) public onlyOwner returns (bool)  {
        if (_index == 1) {
            _fee1Addr = _addr;
        } else if (_index == 2) {
            _fee2Addr = _addr;
        } else if (_index == 3) {
            _fee3Addr = _addr;
        } else if (_index == 4) {
            _fee4Addr = _addr;
        } 
        return true;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint256) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function editWhitelists(address[] memory _addrs,bool _flag) external onlyOwner {
        for(uint256 i=0; i<_addrs.length; i++){
          address addr = _addrs[i];
          whitelist[addr]=_flag;
        }
    }

    function editBlacklist(address[] memory _addrs,bool _flag) external onlyOwner {
        for(uint256 i=0; i<_addrs.length; i++){
          address addr = _addrs[i];
          blacklist[addr]=_flag;
        }
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance")
        );
        return true;
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }

   

    function setMinPeriod(uint256 _minute) public onlyOwner{
        minPeriod = _minute.mul(60);
    }

    function setOnlyWhite(bool _flag,bool _isBuy) public onlyOwner{
        if (_isBuy) {
            onlyWhiteBuy = _flag;
        } else {
            onlyWhiteSell = _flag;
        }
    }

    function setCurrentTotal (uint256 _total) public onlyOwner returns (bool) {
        currentTotal = _total;
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool){
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool){
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function tokenFromReflection(uint256 rAmount) public view returns (uint256) {
        require(
            rAmount <= _rTotal,
            "Amount must be less than total reflections"
        );
        uint256 currentRate = _getRate();
        return rAmount.div(currentRate);
    }

    function setDistributorGas(uint256 _distributorGas) public onlyOwner returns(bool) {
        distributorGas = _distributorGas;
        return true;
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    receive() external payable {}

    function _getRate() private view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }
    function setTradeEnabled(bool _enabled) public onlyOwner {
        tradeEnabled = _enabled;
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function claimTokens() public onlyOwner {
        payable(_owner).transfer(address(this).balance);
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        if (!tradeEnabled) { //all
             revert("Can't transfer now");
        }
        bool takeFee = false;
        address account = from == uniswapV2Pair?to:from;
        if (from == uniswapV2Pair || to == uniswapV2Pair) {
            takeFee = true;
            if (to == uniswapV2Pair) { //sell
                if (onlyWhiteSell) {
                    require(whitelist[account],"BEP20: not a whitelist");
                }
                if (!whitelist[from]) {
                    uint256 maxAmount = balanceOf(from).mul(maxRate).div(1000);
                    require (maxAmount >= amount,"BEP20:Can't transfer in full");
                }
            } else {
                if (onlyWhiteBuy) {
                    require(whitelist[account],"BEP20: not a whitelist");
                }
            }
        } else {
            if (!whitelist[from]) {
                uint256 maxAmount = balanceOf(from).mul(maxRate).div(1000);
                require (maxAmount >= amount,"BEP20:Can't transfer in full");
            }
        }
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }

        require(!blacklist[account],"BEP20: blacklist");

        _tokenTransfer(from, to, amount, takeFee);

        if (!isDividendExempt[from] && from != uniswapV2Pair) setShare(from);
        if (!isDividendExempt[to] && to != uniswapV2Pair) setShare(to);
        if (from != address(this)) {
            _beforeProcess();
        }
    }

    function _beforeProcess () public {
        if(LPFeefenhong.add(minPeriod) <= block.timestamp) {
            LPFeefenhong = block.timestamp;
            currentIndex = 0;
            currentTotal = balanceOf(address(this));

        }
        process(distributorGas);
    }

    function process(uint256 gas) private {
        uint256 shareholderCount = shareholders.length;
        uint256 currentRate = _getRate();
        if (shareholderCount == 0||currentIndex >= shareholderCount) return;
        
        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();
        uint256 iterations = 0;
        while (gasUsed < gas && iterations < shareholderCount && currentIndex < shareholderCount) {
            uint256 amount = currentTotal.mul(IERC20(uniswapV2Pair).balanceOf(shareholders[currentIndex])).div(IERC20(uniswapV2Pair).totalSupply());
            if (amount < minFh) {
                currentIndex++;
                iterations++;
                return;
            }
            if (currentTotal < amount) return;
            distributeDividend(shareholders[currentIndex], amount, currentRate);
            gasUsed = gasUsed.add(gasLeft.sub(gasleft()));
            gasLeft = gasleft();
            currentIndex++;
            iterations++;
        }
    }

    function distributeDividend(address shareholder, uint256 amount, uint256 currentRate) internal {
        uint256 rAmount = amount.mul(currentRate);
        _rOwned[address(this)] = _rOwned[address(this)].sub(rAmount);
        _rOwned[shareholder] = _rOwned[shareholder].add(rAmount);
        emit Transfer(address(this), shareholder, amount);
    }

    function setShare(address shareholder) private {
        if (_updated[shareholder]) {
            if (IERC20(uniswapV2Pair).balanceOf(shareholder) == 0) quitShare(shareholder);
            return;
        }
        if (IERC20(uniswapV2Pair).balanceOf(shareholder) == 0) return;
        addShareholder(shareholder);
        _updated[shareholder] = true;
    }

    function addShareholder(address shareholder) internal {
        shareholderIndexes[shareholder] = shareholders.length;
        shareholders.push(shareholder);
    }

    function quitShare(address shareholder) private {
        removeShareholder(shareholder);
        _updated[shareholder] = false;
    }

    function removeShareholder(address shareholder) internal {
        shareholders[shareholderIndexes[shareholder]] = shareholders[shareholders.length - 1];
        shareholderIndexes[shareholders[shareholders.length - 1]] = shareholderIndexes[shareholder];
        shareholders.pop();
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        bool takeFee
    ) private {
        uint256 currentRate = _getRate();
        uint256 rAmount = tAmount.mul(currentRate);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        uint256 rate;

        if (takeFee) {
            _takeTransfer(
                sender,
                address(this),
                tAmount.div(100).mul(_lpFee),
                currentRate
            );
             _takeTransfer(
                sender,
                uniswapV2Pair,
                tAmount.div(100).mul(_fee0),
                currentRate
            );
            _takeTransfer(
                sender,
                _fee1Addr,
                tAmount.div(100).mul(_fee1),
                currentRate
            );
             _takeTransfer(
                sender,
                _fee2Addr,
                tAmount.div(100).mul(_fee2),
                currentRate
            );
             _takeTransfer(
                sender,
                _fee3Addr,
                tAmount.div(100).mul(_fee3),
                currentRate
            );
             _takeTransfer(
                sender,
                _fee4Addr,
                tAmount.div(100).mul(_fee4),
                currentRate
            );
            rate = _lpFee + _fee0 + _fee1 + _fee2 + _fee3 + _fee4;
        }

        uint256 recipientRate = 100 - rate;
        _rOwned[recipient] = _rOwned[recipient].add(
            rAmount.div(100).mul(recipientRate)
        );
        emit Transfer(sender, recipient, tAmount.div(100).mul(recipientRate));
    }

    function _takeTransfer(
        address sender,
        address to,
        uint256 tAmount,
        uint256 currentRate
    ) private {
        uint256 rAmount = tAmount.mul(currentRate);
        _rOwned[to] = _rOwned[to].add(rAmount);
        emit Transfer(sender, to, tAmount);
    }
}
