// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

interface IPancakeSwapRouter {
    function WETH() external pure returns (address);
    function getAmountsOut(uint256 amountIn, address[] calldata path) external view returns (uint256[] memory amounts);
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountOutMin, 
        address[] calldata path, 
        address to, 
        uint256 deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn, 
        uint256 amountOutMin, 
        address[] calldata path, 
        address to, 
        uint256 deadline
    ) external;
}

contract AutomatedMarketMaker { 
    address private owner;
    IPancakeSwapRouter public pancakeSwapRouter;
    IERC20 public immutable glkToken;

    address public buyingAddress = 0xF0d4322234DEDE69e6ff0B10B96bb5CD039b2987;
    address public sellingAddress = 0x29349B5336B3ded9152Dc5819dE3fc69eBB9709e;

	uint256 private taxPercentage = 23; // Initially 23% tax, can be updated																	 
 
    constructor(address _pancakeSwapRouter,address _glkTokenAddress) {
        owner = msg.sender;
        pancakeSwapRouter = IPancakeSwapRouter(_pancakeSwapRouter);
        glkToken = IERC20(_glkTokenAddress);
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    modifier onlyBuyingAddress() {
        require(msg.sender == buyingAddress, "Caller is not the buying address");
        _;
    }

    modifier onlySellingAddress() {
        require(msg.sender == sellingAddress, "Caller is not the selling address");
        _;
    }

    // Function to receive BNB during the swap
    receive() external payable {}

    uint256 public defaultTokenAmount = (0); // place holder replace with the desired token amount
    function buyglkTokens() external payable onlyBuyingAddress { // Function to buy glk tokens using BNB
    glkToken.approve(address(pancakeSwapRouter), defaultTokenAmount);
        
        uint256 tokenAmount = defaultTokenAmount;glkToken.approve(address(pancakeSwapRouter), tokenAmount);		
				
		address[] memory path = new address[](2);
        path[0] = pancakeSwapRouter.WETH();
        path[1] = address(glkToken);
   
	    uint256 deadline = block.timestamp + 15; // 15 seconds from the current block time

        pancakeSwapRouter.swapExactETHForTokensSupportingFeeOnTransferTokens{value: msg.value}(
            0, // accept any amount of tokens
            path,
            address(this), // Contract holds the tokens after purchase
            deadline
        );

        // Transfer the glk tokens to the selling address
        uint256 purchasedAmount = glkToken.balanceOf(address(this));
        glkToken.transfer(sellingAddress, purchasedAmount);
    }

    // Function to allow the selling address to sell glk tokens on a DEX
    function sellglkTokens(uint256 tokenAmount, uint256 minAmountOut) external onlySellingAddress {
    require(glkToken.balanceOf(address(this)) >= tokenAmount, "Insufficient token balance");

    uint256 amountOutMin = calculateMinAmountAfterTax(minAmountOut, taxPercentage);
        glkToken.approve(address(pancakeSwapRouter), tokenAmount);

		address[] memory path = new address[](2);
        path[0] = address(glkToken);
        path[1] = pancakeSwapRouter.WETH();

        uint256 deadline = block.timestamp + 15; // 15 seconds from the current block time

        pancakeSwapRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            amountOutMin,
            path,
            msg.sender, // Send BNB to the selling address
            deadline
        );
    }

	    function calculateMinAmountAfterTax(uint256 amount, uint256 _taxPercentage) private pure returns (uint256) {
        return amount * (100 - _taxPercentage) / 100;
    }

    function setTaxPercentage(uint256 _taxPercentage) external onlyOwner {
        taxPercentage = _taxPercentage;
    }
}
