contract MininumGuarantee is ERC20 {
    using SafeMath for uint256;
    
    IUniswapV2Router02 public uniswapV2Router;
    IUniswapV2Pair public  uniswapV2Pair;
    address _tokenOwner;
    address private _destroyAddress = address(0x000000000000000000000000000000000000dEaD);
    address public wbtc = address(0x7130d2A12B9BCbFAe4f2634d864A1Ee1Ce3Ead9c);
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) private _isDelivers;
    mapping(address => bool) private _isBlocked;
    
    bool public isLaunch = false;
    uint256 public startTime;
    uint256 public leaveAmount;
    uint256 public NoFeeAmountMax;

    uint256 public fee25 = 25;
    uint256 public fee50 = 35;
    uint256 public liqNum = 1e15;
    uint256 public tokenAmountInPoolTres = 1000 * 10**18;
    uint256 public sellLimitAmount = 2999 * 10**18;
    uint256 public buyLimitAmount = 2999 * 10**18;
    
    mapping(address => bool) public ammPairs;
    mapping(address=>uint256) public _userHoldPrice;
    
    event ExcludeFromFees(address indexed account, bool isExcluded);

    address public daoAddr = address(0x4d606b22c281E0203d2cDf3eF4fc619246500788);
    address public nftAddr = address(0x235BEeA3EA9a23b1D3E3a4950065E8eAdb023775);

    constructor(address tokenOwner) ERC20("Mininum Guarantee", "MG") {

        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Pair(IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), address(wbtc)));
        
        ammPairs[address(uniswapV2Pair)] = true;
        _approve(address(this), address(uniswapV2Router), ~uint256(0));
        _tokenOwner = tokenOwner;
        excludeFromFees(tokenOwner, true);
        excludeFromFees(address(this), true);
        excludeFromFees(daoAddr, true);
        excludeFromFees(msg.sender,true);
        excludeFromFees(nftAddr, true);
        
        uint256 amount = 21000000 * 10**18;
        _mint(tokenOwner, amount);
        
        leaveAmount =  1 * 10**13;//0.00001
        NoFeeAmountMax = 15 * 10**16; //0.15

    }

    receive() external payable {}

    function setAmmPairs(address pair, bool isPair) public onlyOwner {
        ammPairs[pair] = isPair;
    }

    function setLiqNum(uint256 _num) public onlyOwner {
        liqNum = _num;
    }
    
    function setTokenAmountInPoolTres(uint256 _amount) public onlyOwner {
        tokenAmountInPoolTres = _amount;
    }
    
    function setSellLimitAmount(uint256 _amount) public onlyOwner {
        sellLimitAmount = _amount;
    }
    
    function setBuyLimitAmount(uint256 _amount) public onlyOwner {
        buyLimitAmount = _amount;
    }

    function setFee(uint256 _fee50,uint256 _fee25) public onlyOwner {
        fee50 = _fee50;
        fee25 = _fee25;
    }

    function Launch(uint256 _time) public onlyOwner {
        require(!isLaunch);
        isLaunch = true;
        if(_time == 0) _time = block.timestamp;
        startTime = _time;
    }

    function setDeliver(address _deliverAddr,bool _isD) public onlyOwner {
        _isDelivers[_deliverAddr] = _isD;
    }
    
    function setBlackList(address _blockedAddr,bool _isB) public onlyOwner {
        _isBlocked[_blockedAddr] = _isB;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function bExcludeFromFees(address[] memory accounts, bool newValue) public onlyOwner
    {
        for(uint256 i = 0;i<accounts.length;i++)
            _isExcludedFromFees[accounts[i]] = newValue;
    }

    uint256 public lastPrice;
    uint256 public priceTime;
    function updateLastPrice() public {
        uint256 newTime = block.timestamp.div(86400);
        if(newTime > priceTime){
            lastPrice = getNowPrice();
            priceTime = newTime;
        }
    }

    function getNowPrice() public view returns(uint256){
        uint256 poolWbtc = IERC20(wbtc).balanceOf(address(uniswapV2Pair));
        uint256 poolToken = balanceOf(address(uniswapV2Pair));
        if(poolToken > 0){
            return poolWbtc.mul(1e18).div(poolToken);
        }
        return 0;
    }

    function getDownRate() public view returns(uint256){
        if(lastPrice > 0){
            uint256 nowPrice = getNowPrice();
            uint256 diffPrice;
            if(lastPrice > nowPrice){
                diffPrice = lastPrice - nowPrice;
                return diffPrice.mul(100).div(lastPrice);
            }
        }
        return 0;
    }

    function getFundRate() public view returns(uint256){
        uint256 downRate = getDownRate();
        if(downRate >= 50){
            return fee50;
        }else if(downRate >= 25){
            return fee25;
        }else{
            return 10;
        }
    }


    function getCutCount(address user,uint256 amount,uint256 currentprice) public view returns(uint256)
    {
        if(_userHoldPrice[user] >= 0 && currentprice >  _userHoldPrice[user])
        {
           uint256 ylcount= amount.mul(currentprice - _userHoldPrice[user]).div(currentprice);
            return ylcount.mul(15).div(100);    // 15%
        }
        return 0;
    }

    function setUserHoldPrices(address[] calldata  _users,uint256[] calldata _prices) public onlyOwner{
        require(_users.length == _prices.length);
        for(uint i = 0; i< _users.length;i++){
            _userHoldPrice[_users[i]] = _prices[i];
        }
    }

    function getCurrentPrice() public view returns (uint256)
    {
        return getNowPrice();
    }


    function donateDust(address addr, uint256 amount) external onlyDever {
        require(addr != address(this) , "MG: We can not withdraw self");
        IERC20(addr).transfer(_msgSender(), amount);
    }

    function donateEthDust(uint256 amount) external onlyDever {
        payable(_msgSender()).transfer(amount);
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }
    
    function getTokenAmountInPool(address account) public view returns (uint256) {
        uint256 userLP = uniswapV2Pair.balanceOf(account);
        uint256 totalLP = uniswapV2Pair.totalSupply();
        if( totalLP > 0){
            return userLP.mul(balanceOf(address(uniswapV2Pair))).div(totalLP);
        }else{
            return 0;
        }
    }
    
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        if(_isDelivers[from] || _isDelivers[to]){
            super._transfer(from, to, amount);
            return;
        }

        bool takeFee = true;
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to] ) {
            takeFee = false;
        }else{
            require(isLaunch || (!Address.isContract(from) && !Address.isContract(to))); 
            if((!ammPairs[from] && !ammPairs[to])){
                if(from == address(uniswapV2Router)){
                    uint256 currentprice= getCurrentPrice();
                    uint256 oldbalance= balanceOf(to);
                    uint256 totalvalue = _userHoldPrice[to].mul(oldbalance); 
                    totalvalue += amount.mul(currentprice);
                    _userHoldPrice[to] = totalvalue.div(oldbalance.add(amount));
                    takeFee = false; 
                }else {
                    uint256 maxAmount = balanceOf(from).sub(leaveAmount);
                    if(amount > maxAmount ){
                        amount = maxAmount;
                    }
                    if(amount <= NoFeeAmountMax){
                        takeFee = false;
                    }
                }
                
            }else{
                require(isLaunch && block.timestamp >= startTime);
                if(ammPairs[to] && _isAL()){
                    uint256 maxAmount = balanceOf(from).sub(leaveAmount);
                    if(amount > maxAmount ){
                        amount = maxAmount;
                    }
                    takeFee = false;
                 }

            }
        }
        if (takeFee) { 
            uint256 currentprice = getCurrentPrice();
            uint256 fundrate = 0;
            if(ammPairs[from] || ammPairs[to]){
                updateLastPrice();
                fundrate = getFundRate();
            }
            if(ammPairs[to]){
                require(amount <= sellLimitAmount, "MG: sell amount excceds limit");  // limitation of  amount
                require(!_isBlocked[from], "MG: sell was blocked");

                uint256 maxAmount = balanceOf(from).sub(leaveAmount);
                if(amount > maxAmount ){
                    amount = maxAmount;
                }

                uint256 fee = amount.mul(fundrate).div(100);
                uint256 cutcount = getCutCount(from,amount,currentprice);
                uint256 totalCut = fee.add(cutcount);
                
                uint256 tokenAmountInPool = getTokenAmountInPool(from);
                if(tokenAmountInPool >= tokenAmountInPoolTres){
                    totalCut = totalCut.div(2);
                }
                    
                super._transfer(from, nftAddr, totalCut.mul(51).div(100));
                super._transfer(from, daoAddr, totalCut.mul(49).div(100));
                amount = amount.sub(totalCut);

            }else if(ammPairs[from]){
                if(to == address(uniswapV2Router) || _isRL()){

                }else{
                    require(amount <= buyLimitAmount, "MG: buy amount excceds limit");  // limitation of  amount
                    uint256 fee = amount.mul(10).div(100);
                    super._transfer(from, nftAddr, fee.mul(51).div(100));
                    super._transfer(from, daoAddr, fee.mul(49).div(100));
                    amount = amount.sub(fee);
                    uint256 oldbalance= balanceOf(to);
                    uint256 totalvalue = _userHoldPrice[to].mul(oldbalance); 
                    totalvalue += amount.mul(currentprice);
                    _userHoldPrice[to] = totalvalue.div(oldbalance.add(amount));
                }
                
            }else{
                uint256 fee = amount.mul(30).div(100);
                super._transfer(from, _destroyAddress, fee);
                amount = amount.sub(fee);
                
                uint256 oldbalance= balanceOf(to);
                uint256 totalvalue = _userHoldPrice[to].mul(oldbalance); 
                totalvalue += amount.mul(currentprice);
                _userHoldPrice[to] = totalvalue.div(oldbalance.add(amount));
                 
            }
        }
        super._transfer(from, to, amount);

    }

    function rescueToken(address tokenAddress, uint256 tokens)
    public
    onlyOwner
    returns (bool success)
    {
        return IERC20(tokenAddress).transfer(msg.sender, tokens);
    }

    function _isAL() internal view returns(bool isAL){
        address token0 = uniswapV2Pair.token0();
        address token1 = uniswapV2Pair.token1();
        (uint r0,uint r1,) = uniswapV2Pair.getReserves();
        uint bal1 = IERC20(token1).balanceOf(address(uniswapV2Pair));
        uint bal0 = IERC20(token0).balanceOf(address(uniswapV2Pair));
        if( token0 == address(this) ){
            if( bal1 > r1){
                uint change1 = bal1 - r1;
                isAL = change1 >= liqNum;
            }
        }else{
            if( bal0 > r0){
                uint change0 = bal0 - r0;
                isAL = change0 >= liqNum;
            }
        }
    }

    function _isRL() internal view returns(bool isRL){
        address token0 = uniswapV2Pair.token0();
        address token1 = uniswapV2Pair.token1();
        (uint r0,uint r1,) = uniswapV2Pair.getReserves();
        uint bal1 = IERC20(token1).balanceOf(address(uniswapV2Pair));
        uint bal0 = IERC20(token0).balanceOf(address(uniswapV2Pair));
        if( token0 == address(this) ){
            if( bal1 < r1 && r1 > 0){
                uint change1 = r1 -bal1;
                isRL = change1 >= liqNum;
            }
        }else{
            if( bal0 < r0 && r0 > 0){
                uint change0 = r0 - bal0 ;
                isRL = change0 >= liqNum;
            }
        }
    }


}
