// File: contracts/NeftaDigitalAsset.sol

pragma solidity 0.8.8;





// Hello and welcome. If you're reading this, we want to talk to you.
// Get in touch at hello@nefta.io
// Cheers

contract Fitburn is ERC721, Ownable {
    // using SafeMath for uint256;

    mapping(uint256 => address[]) private _cashbackRecipients;
    mapping(uint256 => uint256[]) private _cashbackValues;


    mapping(uint256 => bool) private _auctions;
    mapping(uint256 => uint256) private _auctionBuyNowPrices;
    mapping(uint256 => uint256) private _auctionBidStartingPrices;
    mapping(uint256 => address payable) private _auctionBidder;
    mapping(uint256 => uint256) private _auctionDeposits;

    mapping(uint => address ) tokenOwners;
    mapping(uint => bool) mintedTokens;
    address private owner_;

    constructor () ERC721("Fitburn", "CAL")  {
        // _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        // _setupRole(MINTER_ROLE, msg.sender);
        owner_ = msg.sender;
    }

    function burn(uint256 tokenId) public {
        if (tokenOwners[tokenId] == msg.sender && mintedTokens[tokenId]){
            _burn(tokenId);
            mintedTokens[tokenId] = false;
        }
    }

    function createDigitalAssetFromAuctionEnd(
        string memory tokenURI,
        uint256 tokenId,
        uint256 auctionId,
        address[] memory recipientAddresses,
        uint256[] memory cashbackValues
    ) public onlyOwner payable{
        require(!mintedTokens[tokenId], "Token is already Minted");

        address highestBidder = _auctionBidder[auctionId];
        uint256 bidAmount = _auctionDeposits[auctionId];
        _mint(highestBidder, tokenId);
        _setTokenURI(tokenId, tokenURI);


        mintedTokens[tokenId] = true;
        tokenOwners[tokenId] = highestBidder;

        _cashbackRecipients[tokenId] = recipientAddresses;
        _cashbackValues[tokenId] = cashbackValues;

        uint256 paidCommission = payoutCommission(tokenId, bidAmount);

        uint256 leftoverAmount = bidAmount - paidCommission;

        (bool successLeftover,) = payable(msg.sender).call{ value: leftoverAmount }(""); // Paying back to the owner/ Project
        require(successLeftover, "LeftoverAmount Transfer failed.");
    }

    function payoutCommission(uint256 tokenId, uint256 bidAmount) internal returns (uint256){
        uint256 paidCommission = 0;
        for (uint256 i = 0; i < _cashbackRecipients[tokenId].length; i++) {
            paidCommission += ((_cashbackValues[tokenId][i] * bidAmount) / 10000);
            uint256 commissionValue = (_cashbackValues[tokenId][i] * bidAmount) / 10000; // transferring cashback to authors
            //            250 * 50000 / 10000
            if (_cashbackValues[tokenId][i] > 0) {

                (bool success, ) = payable(_cashbackRecipients[tokenId][i]).call{ value: commissionValue }(""); // Send the commission to required parties
                require(success, "payoutCommission Transfer failed.");
            }
        }
        return paidCommission;
    }

    function transferDigitalAssetFromAuctionEnd(uint256 tokenId, uint256 auctionId) public onlyOwner payable{
        require(mintedTokens[tokenId], "Token Hasn't been Minted");
        require(_cashbackValues[tokenId].length > 0, "Cashback values not set");

        address highestBidder = _auctionBidder[auctionId];
        uint256 bidAmount = _auctionDeposits[auctionId];

        uint256 paidCommission = payoutCommission(tokenId, bidAmount);

        address currentOwner = tokenOwners[tokenId];
        uint256 leftoverAmount = bidAmount - paidCommission;



        (bool success, ) = payable(currentOwner).call{ value: leftoverAmount }(""); // Send the leftoverAmount to required parties
        require(success, "leftoverAmount Transfer failed.");
        _transfer(currentOwner, highestBidder, tokenId);
        tokenOwners[tokenId] = highestBidder; // Change of ownership

    }

    function createDigitalAssetFromGift(
        address receiver,
        string memory tokenURI,
        uint256 tokenId,
        address[] memory recipientAddresses,
        uint256[] memory cashbackValues
    ) public onlyOwner returns (uint256){

        _mint(receiver, tokenId);
        _setTokenURI(tokenId, tokenURI);

        mintedTokens[tokenId] = true;
        tokenOwners[tokenId] = receiver;

        _cashbackRecipients[tokenId] = recipientAddresses;
        _cashbackValues[tokenId] = cashbackValues;

        return tokenId;
    }


    function getCurrentBidAmount(uint256 auctionId) public returns (uint256){
        return _auctionDeposits[auctionId];
    }

    function makeDepositForAuction(address payable bidder, uint256 auctionId) public payable {
        if (_auctionDeposits[auctionId] > 0){
            uint256 currentDeposit = _auctionDeposits[auctionId];

            require(currentDeposit < msg.value, "Bid amount is less than current bid"); // Needs to be higher than existing

            address payable depositHolder = _auctionBidder[auctionId];

            (bool success, ) = depositHolder.call{ value: currentDeposit }(""); // return deposit for old user
            require(success, "deposit return Transfer failed.");

            //  auctionDeposits[auctionId].withdraw(payable(depositHolder));

            _auctionDeposits[auctionId] = msg.value;
            _auctionBidder[auctionId] = bidder;
        }else{
            require(_auctionBidStartingPrices[auctionId] > 0, "Starting price not set"); // Auction starting price needs to be set
            require(_auctionBidStartingPrices[auctionId] < msg.value, "Bid amount is less than starting price"); // Needs to be higher than starting price

            _auctions[auctionId] = true;

            _auctionDeposits[auctionId] = msg.value;
            _auctionBidder[auctionId] = bidder;
        }
    }

    function setBidStartingPrices(
        uint256 auctionId,
        uint256 startingBidPrice
    ) public onlyOwner {
        if (_auctionBidStartingPrices[auctionId] == 0){
            _auctionBidStartingPrices[auctionId] = startingBidPrice;
        }
    }

    function getBalanceOfSender() public returns (uint256) {
        return address(msg.sender).balance;
    }

    function setAuctionForBuyNow(
        uint256 auctionId,
        uint256 buyNowPrice,
        address[] memory recipientAddresses,
        uint256[] memory cashbackValues
    ) public onlyOwner {

        if (_auctionBuyNowPrices[auctionId] == 0){
            _auctionBuyNowPrices[auctionId] = buyNowPrice;
            _cashbackRecipients[auctionId] = recipientAddresses;
            _cashbackValues[auctionId] = cashbackValues;
        }
    }

    function getBuyNowAuctionPrice(uint256 auctionId) public view returns (uint256) {
        return _auctionBuyNowPrices[auctionId];
    }

    function buyNowActiveAuction(
        uint256 auctionId,
        uint256 tokenId,
        string memory tokenURI,
        address bidder
    ) public payable {
        require(_auctionBuyNowPrices[auctionId] > 0, "Auction price has to be set");
        require(_auctionBuyNowPrices[auctionId] <= msg.value, "Value is not high enough"); // Needs to be higher than the preset buy now amount

        _cashbackRecipients[tokenId] = _cashbackRecipients[auctionId]; // The previously cached commission variables
        _cashbackValues[tokenId] = _cashbackValues[auctionId];

        if (_auctions[auctionId]){
            address payable depositHolder = _auctionBidder[auctionId];
            uint256 currentDeposit = _auctionDeposits[auctionId];

            (bool success, ) = depositHolder.call{ value: currentDeposit }(""); // return deposit for old user
            require(success, "deposit return Transfer failed.");
        }

        uint256 paidCommission = payoutCommission(tokenId, msg.value);
        uint256 leftoverAmount = msg.value - paidCommission;

        if (mintedTokens[tokenId]){
            address currentOwner = tokenOwners[tokenId];

            (bool success, ) = payable(currentOwner).call{ value: leftoverAmount }(""); // payout to current owner
            require(success, "payout Transfer failed.");

            _transfer(currentOwner, bidder, tokenId);
            tokenOwners[tokenId] = bidder; // Change of ownership
        }else{
            _mint(bidder, tokenId);
            _setTokenURI(tokenId, tokenURI);
            mintedTokens[tokenId] = true;

            (bool success, ) = payable(owner_).call{ value: leftoverAmount }(""); // Paying back to the owner/ Project
            require(success, "payout Transfer failed.");

            tokenOwners[tokenId] = bidder;
        }
//        delete _cashbackRecipients[auctionId];
//        delete _cashbackValues[auctionId];
//        delete _auctionBuyNowPrices[auctionId];

    }
    function getOwnerOfAsset(uint256 tokenId) public view returns (address) {
        return tokenOwners[tokenId];
    }

    function getTokenURI(uint256 tokenId) public view returns (string memory) {
        return tokenURI(tokenId);
    }
}
