                require(block.timestamp>openTime,"swap not open");

                //1%burn
                uint256 burnAmount = amount*burnRate/1000;
                _balances[burnAddress] += burnAmount;
                emit Transfer(sender, burnAddress, burnAmount);

                //to liquid
                uint256 liquidityAmount = amount*liquidityRate/1000;
                _balances[address(this)] += liquidityAmount;
                emit Transfer(sender, address(this), liquidityAmount);

                remain =  amount-burnAmount-liquidityAmount;
            }
        }
         if(_balances[address(this)]>0 && sender != uniswapV2Pair && balanceOf(uniswapV2Pair) > 0){
            //to usdt
            swapTokensForUsdt(_balances[address(this)],liquidityAddress);
        }  
        
        
        _balances[recipient] += remain;

        emit Transfer(sender, recipient, remain);
