contract TokenDividendTracker is Ownable {
    using SafeMath for uint256;

    address[] public shareholders;
    uint256 public currentIndex;  
    mapping(address => bool) private _updated;
    mapping (address => uint256) public shareholderIndexes;

    address public  uniswapV2Pair;
    address public lpRewardToken;
   
    uint256 public LPRewardLastSendTime;

    constructor(address uniswapV2Pair_, address lpRewardToken_){
        uniswapV2Pair = uniswapV2Pair_;
        lpRewardToken = lpRewardToken_;
    }

    function resetLPRewardLastSendTime() public onlyOwner {
        LPRewardLastSendTime = 0;
    }

   
    function process(uint256 gas) external onlyOwner {
        uint256 shareholderCount = shareholders.length;	

        if(shareholderCount == 0) return;
        uint256 nowbalance = IBEP20(lpRewardToken).balanceOf(address(this));

        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();

        uint256 iterations = 0;

        while(gasUsed < gas && iterations < shareholderCount) {
            if(currentIndex >= shareholderCount){
                currentIndex = 0;
                LPRewardLastSendTime = block.timestamp;
                return;
            }

            uint256 amount = nowbalance.mul(IBEP20(uniswapV2Pair).balanceOf(shareholders[currentIndex])).div(IBEP20(uniswapV2Pair).totalSupply());
            if( amount == 0) {
                currentIndex++;
                iterations++;
                return;
            }
            if(IBEP20(lpRewardToken).balanceOf(address(this))  < amount ) return;
            IBEP20(lpRewardToken).transfer(shareholders[currentIndex], amount);
            gasUsed = gasUsed.add(gasLeft.sub(gasleft()));
            gasLeft = gasleft();
            currentIndex++;
            iterations++;
        }
    }
 
    function setShare(address shareholder) external onlyOwner {
        if(_updated[shareholder] ){      
            if(IBEP20(uniswapV2Pair).balanceOf(shareholder) == 0) quitShare(shareholder);           
            return;  
        }
        if(IBEP20(uniswapV2Pair).balanceOf(shareholder) == 0) return;  
        addShareholder(shareholder);	
        _updated[shareholder] = true;
          
      }
    function quitShare(address shareholder) internal {
        removeShareholder(shareholder);   
        _updated[shareholder] = false; 
    }

    function addShareholder(address shareholder) internal {
        shareholderIndexes[shareholder] = shareholders.length;
        shareholders.push(shareholder);
    }

    function removeShareholder(address shareholder) internal {
        shareholders[shareholderIndexes[shareholder]] = shareholders[shareholders.length-1];
        shareholderIndexes[shareholders[shareholders.length-1]] = shareholderIndexes[shareholder];
        shareholders.pop();
    }
}

contract RKCToken is BEP20 {
    using SafeMath for uint256;

    IUniswapV2Router02 public _uniswapV2Router;
    address public _uniswapV2RouterAddress = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    IUniswapV2Pair public _uniswapPair;
    address public  _uniswapV2PairAddress;
    address public _usdtAddress = 0x55d398326f99059fF775485246999027B3197955;

    address public _markAddress = 0x092A8391c6770326bF91432583Ab9d3d78560B6F;

    mapping(address => bool) public _whiteMapping;

    uint256 public _markFee = 1;
    uint256 public _destoryFee = 2;
    uint256 public _lpFee = 2;

    uint256 public _dividendMinPeriod = 10 * 60;
    uint256 _distributorGas = 200000;

    TokenDividendTracker public _dividendTracker;
    mapping (address => bool) isDividendExempt;
    address private _fromAddress;
    address private _toAddress;

    bool private _swapping;

    mapping(address => bool) private _isAuthorize;

    modifier onlyAuthorize() {
        require(_isAuthorize[msg.sender] == true, "caller is not Authorize");
        _;
    }

    constructor(
    ) payable BEP20()  {
        _uniswapV2Router = IUniswapV2Router02(_uniswapV2RouterAddress);
        _uniswapV2PairAddress = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _usdtAddress);
        _uniswapPair = IUniswapV2Pair(_uniswapV2PairAddress);

        _dividendTracker = new TokenDividendTracker(_uniswapV2PairAddress, address(this));

        _whiteMapping[msg.sender] = true;
        _whiteMapping[address(0)] = true;
        _whiteMapping[address(this)] = true;
        _whiteMapping[_uniswapV2PairAddress] = true;
        _whiteMapping[address(_dividendTracker)] = true;

        isDividendExempt[address(this)] = true;
        isDividendExempt[address(0)] = true;
        isDividendExempt[address(_dividendTracker)] = true;
    }

    function setAddress(address markAddress)public onlyOwner{
        _markAddress = markAddress;
    }

    function setFee(uint256 markFee,uint256 destoryFee,uint256 lpFee)public onlyOwner{
        _markFee = markFee;
        _destoryFee = destoryFee;
        _lpFee = lpFee;
    }   

    function addWhite(address[] memory addrs) public onlyOwner{
        uint256 len = addrs.length;
        for(uint256 i = 0 ; i < len ;i++){
            address addr = addrs[i];
            _whiteMapping[addr] = true;
        }
    }

    function addDividend(address[] memory addrs) public onlyOwner{
        uint256 len = addrs.length;
        for(uint256 i = 0 ; i < len ;i++){
            address addr = addrs[i];
            _dividendTracker.setShare(addr);
        }
    }

    function updateDistributorGas(uint256 newValue) public onlyOwner {
        require(newValue >= 100000 && newValue <= 500000, "distributorGas must be between 200,000 and 500,000");
        require(newValue != _distributorGas, "Cannot update distributorGas to same value");
        _distributorGas = newValue;
    }

    function setAuthorize(address addr , bool flag)public onlyOwner{
        _isAuthorize[addr] = flag;
    }


    //===================================
    function mint(address addr , uint256 amount) public onlyAuthorize override returns (uint256) {
        return super._mint(addr, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if(amount == 0) { super._transfer(from, to, 0); return;}

        if(!_swapping && (to == _uniswapV2PairAddress)){
            _swapping = true;
            
            if(!_whiteMapping[from]){
                amount = fee(from,amount);      
            }

            _swapping = false;
        }

        if(!_swapping &&  !_whiteMapping[from]){
            _swapping = true;
            
            amount = fee(from,amount);      
            
            _swapping = false;
        }

        super._transfer(from, to, amount);
        
        if(!_swapping){
            if(_fromAddress == address(0)) _fromAddress = from;
            if(_toAddress == address(0)) _toAddress = to;  
            if(_fromAddress != _uniswapV2PairAddress && !isDividendExempt[_fromAddress]  )   try _dividendTracker.setShare(_fromAddress) {} catch {}
            if(_toAddress != _uniswapV2PairAddress && !isDividendExempt[_toAddress] ) try _dividendTracker.setShare(_toAddress) {} catch {}

            _fromAddress = from;
            _toAddress = to;
        }

        if(!_swapping &&
            _dividendTracker.LPRewardLastSendTime().add(_dividendMinPeriod) <= block.timestamp
        ) {
             _swapping = true;
            try _dividendTracker.process(_distributorGas) {} catch {} 
             _swapping = false;   
        }
     }

    function fee(address addr , uint256 amount)  private returns(uint256 amountAfter){
        amountAfter = amount;

        uint256 markFee =  amount.mul(_markFee).div(100);
        amountAfter = amountAfter.sub(markFee);

        uint256 destoryFee =  amount.mul(_destoryFee).div(100);
        amountAfter = amountAfter.sub(destoryFee);

        uint256 lpFee = amount.mul(_lpFee).div(100);
        amountAfter = amountAfter.sub(lpFee);

        super._transfer(addr , _markAddress , markFee);
        super._burn(addr,destoryFee);
        super._transfer(addr , address(_dividendTracker) , lpFee);
    }
}
