      "content": "// SPDX-License-Identifier: MIT

pragma solidity >=0.7.0;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/math/Math.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../token/TokenLock.sol";

abstract contract User {
    struct UserInfo {
        address referrer;
        address node;
        uint256 power;
        bool isNode;
    }

    constructor() {
        UserInfo storage userInfo = userInfos[address(this)];
        userInfo.referrer = address(this);
    }

    mapping(address => UserInfo) public userInfos;

    event BindSuperior(address account, address superior, address node);
    event UpgradeNode(address account);
    event AbandonNode(address account);

    function bindSuperior(address _referrer) external {
        UserInfo storage userInfo = userInfos[msg.sender];
        UserInfo memory superior = userInfos[_referrer];
        require(
            _referrer != address(0) &&
                _referrer != msg.sender &&
                superior.referrer != address(0),
            "INVALID_SUPERIOR_ADDRESS"
        );
        require(userInfo.referrer == address(0), "referrer has been bound");
        userInfo.referrer = _referrer;
        if (superior.isNode) {
            userInfo.node = _referrer;
        } else if (superior.node != address(0)) {
            userInfo.node = superior.node;
        }
        emit BindSuperior(msg.sender, _referrer, userInfo.node);
    }

    function getSuperior(address account) public view returns (address) {
        return userInfos[account].referrer;
    }

    function getNode(address account) public view returns (address) {
        return userInfos[account].node;
    }

    function _upgradeNode() internal {
        UserInfo storage userInfo = userInfos[msg.sender];
        userInfo.isNode = true;

        emit UpgradeNode(msg.sender);
    }

    function _abandonNode() internal {
        UserInfo storage userInfo = userInfos[msg.sender];
        userInfo.isNode = false;

        emit AbandonNode(msg.sender);
    }
}

contract LPTokenWrapper {
    using SafeMath for uint256;

    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    function _stake(address account, uint256 amount) internal {
        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
    }

    function _withdraw(address account, uint256 amount) internal {
        _totalSupply = _totalSupply.sub(amount);
        _balances[account] = _balances[account].sub(amount);
    }
}

abstract contract MosReward is LPTokenWrapper, Ownable, User {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public token;
    uint256 public initreward;
    uint256 public starttime;
    uint256 public periodFinish = 0;
    uint256 public rewardRate = 0;
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;
    mapping(address => uint256) public invReward;
    TokenLock public rewardLock;
    TokenLock public inviteLock;
    event RewardAdded(uint256 reward);
    event Donate(address indexed user, uint256 amount);
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);

    uint256 public constant DURATION = 365 days;

    constructor(
        uint256 _initreward,
        uint256 _starttme,
        IERC20 _token
    ) {
        initreward = _initreward;
        token = _token;
        starttime = _starttme;
        lastUpdateTime = starttime;
        periodFinish = lastUpdateTime;
    }

    function setRewardLock(TokenLock _rewardLock) external onlyOwner {
        rewardLock = _rewardLock;
        token.approve(address(rewardLock), uint256(-1));
    }

    function setInviteLock(TokenLock _inviteLock) external onlyOwner {
        inviteLock = _inviteLock;
        token.approve(address(inviteLock), uint256(-1));
    }

    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = lastTimeRewardApplicable();
        if (account != address(0)) {
            (uint256 _ea, uint256 _value) = earned(account);
            rewards[account] = _ea;
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    function lastTimeRewardApplicable() public view returns (uint256) {
        return Math.min(block.timestamp, periodFinish);
    }

    function rewardPerToken() public view returns (uint256) {
        if (totalSupply() == 0) {
            return rewardPerTokenStored;
        }
        return
            rewardPerTokenStored.add(
                lastTimeRewardApplicable()
                    .sub(lastUpdateTime)
                    .mul(rewardRate)
                    .mul(1e18)
                    .div(totalSupply())
            );
    }

    function earned(address account) public view returns (uint256 _ea, uint256 _value) {
        _ea = balanceOf(account)
        .mul(rewardPerToken().sub(userRewardPerTokenPaid[account]))
        .div(1e18)
        .add(rewards[account]);

        _value = _ea.sub(_ea.mul(30).div(100));
    }

    // stake visibility is public as overriding LPTokenWrapper's stake() function
    function stake_(address account, uint256 amount)
        internal
        updateReward(account)
        checkhalve
        checkStart
    {
        require(amount > 0, "Cannot stake 0");
        super._stake(account, amount);
        emit Staked(account, amount);
    }

    function withdraw_(address account, uint256 amount)
        internal
        updateReward(account)
        checkhalve
        checkStart
    {
        require(amount > 0, "Cannot withdraw 0");
        super._withdraw(account, amount);
        emit Withdrawn(account, amount);
    }

    function getReward() public updateReward(msg.sender) checkhalve checkStart {
        (uint256 reward, ) = earned(msg.sender);
        if (reward > 0) {
            rewards[msg.sender] = 0;

            address superior = getNode(msg.sender);
            if (superior != address(0)) {
                uint256 invRew = reward.mul(30).div(100);
                if (address(inviteLock) == address(0)) {
                    token.safeTransfer(superior, invRew);
                } else {
                    token.safeTransfer(superior, invRew.mul(30).div(100));
                    token.approve(address(inviteLock), invRew.mul(70).div(100));
                    inviteLock.locking(superior, invRew.mul(70).div(100));
                }
                reward = reward.mul(70).div(100);
                invReward[superior] = invReward[superior].add(
                    reward.mul(30).div(100)
                );
            }
            if (address(rewardLock) == address(0)) {
                token.safeTransfer(msg.sender, reward);
            } else {
                token.safeTransfer(msg.sender, reward.mul(30).div(100));
                token.approve(address(rewardLock), reward.mul(70).div(100));
                rewardLock.locking(msg.sender, reward.mul(70).div(100));
            }

            emit RewardPaid(msg.sender, reward);
        }
    }

    modifier checkhalve() {
        if (block.timestamp >= periodFinish) {
            initreward = initreward.mul(50).div(100);
            rewardRate = initreward.div(DURATION);

            if (block.timestamp > starttime.add(DURATION)) {
                starttime = starttime.add(DURATION);
            }
            periodFinish = starttime.add(DURATION);
            emit RewardAdded(initreward);
        }
        _;
    }

    modifier checkStart() {
        require(block.timestamp > starttime, "not start");
        _;
    }
}

contract NFTPool is MosReward {
    using SafeMath for uint256;

    mapping(address => uint256) public nftOwner;
    IERC721 public nodeNFT;
    address public caller;

    function setCaller(address newCaller) external onlyOwner {
        caller = newCaller;
    }

    constructor(
        IERC721 _nodeNFT,
        uint256 _initreward,
        uint256 _starttme,
        IERC20 _token
    ) MosReward(_initreward, _starttme, _token) {
        nodeNFT = _nodeNFT;
    }

    function upgradeNode(uint256 tokenId) external {
        require(nftOwner[msg.sender] == 0, "nft existed");
        require(
            getSuperior(msg.sender) != address(0),
            "Bind the inviter first"
        );
        nftOwner[msg.sender] = tokenId;
        nodeNFT.transferFrom(msg.sender, address(this), tokenId);
        _upgradeNode();
        UserInfo storage userInfo = userInfos[msg.sender];
        if (userInfo.power > 0) {
            super.stake_(msg.sender, userInfo.power);
        }
    }

    function abandonNode() external {
        require(nftOwner[msg.sender] != 0, "nft does not exist");
        nodeNFT.transferFrom(address(this), msg.sender, nftOwner[msg.sender]);
        nftOwner[msg.sender] = 0;
        _abandonNode();
        UserInfo storage userInfo = userInfos[msg.sender];
        if (userInfo.power > 0) {
            super.withdraw_(msg.sender, userInfo.power);
        }
    }

    function withdraw(address account, uint256 value) external checkSender {
        UserInfo storage userInfo = userInfos[account];
        if (userInfo.node != address(0)) {
            UserInfo storage nodeInfo = userInfos[userInfo.node];
            nodeInfo.power = nodeInfo.power.sub(value);
            if (userInfos[userInfo.node].isNode) {
                super.withdraw_(userInfo.node, value);
            }
        }
    }

    function stake(address account, uint256 value) external checkSender {
        UserInfo storage userInfo = userInfos[account];
        if (userInfo.node != address(0)) {
            UserInfo storage nodeInfo = userInfos[userInfo.node];
            nodeInfo.power = nodeInfo.power.add(value);
            if (userInfos[userInfo.node].isNode) {
                super.stake_(userInfo.node, value);
            }
        }
    }

    modifier checkSender() {
        require(msg.sender == caller);
        _;
    }
}
