/**
 * @title Pausable
 * @dev Base contract which allows children to implement an emergency stop mechanism.
 */
contract Pausable is Ownable {
  event Pause();
  event Unpause();

  bool public paused = false;


  /**
   * @dev Modifier to make a function callable only when the contract is not paused.
   */
  modifier whenNotPaused() {
    require(!paused);
    _;
  }

  /**
   * @dev Modifier to make a function callable only when the contract is paused.
   */
  modifier whenPaused() {
    require(paused);
    _;
  }

  /**
   * @dev called by the owner to pause, triggers stopped state
   */
  function pause() onlyOwner whenNotPaused public {
    paused = true;
    emit Pause();
  }

  /**
   * @dev called by the owner to unpause, returns to normal state
   */
  function unpause() onlyOwner whenPaused public {
    paused = false;
    emit Unpause();
  }
}



/**
 * @title Pausable token
 * @dev StandardToken modified with pausable transfers.
 **/
contract PausableToken is StandardToken, Pausable {

  function transfer(
    address _to,
    uint256 _value
  )
    public
    whenNotPaused
    returns (bool)
  {
    return super.transfer(_to, _value);
  }

  function transferFrom(
    address _from,
    address _to,
    uint256 _value
  )
    public
    whenNotPaused
    returns (bool)
  {
    return super.transferFrom(_from, _to, _value);
  }

  function approve(
    address _spender,
    uint256 _value
  )
    public
    whenNotPaused
    returns (bool)
  {
    return super.approve(_spender, _value);
  }

  function increaseApproval(
    address _spender,
    uint _addedValue
  )
    public
    whenNotPaused
    returns (bool success)
  {
    return super.increaseApproval(_spender, _addedValue);
  }

  function decreaseApproval(
    address _spender,
    uint _subtractedValue
  )
    public
    whenNotPaused
    returns (bool success)
  {
    return super.decreaseApproval(_spender, _subtractedValue);
  }
}


/**
 * A base token for the Long Asset (or any other coin with similar behavior).
 * Compatible with contracts and UIs expecting a BEP20 token.
 * Provides also a convenience method to burn tokens, permanently removing them from the pool;
 * the intent of this convenience method is for users who wish to burn tokens
 * (as they always can via a transfer to an unowned address or self-destructing
 * contract) to do so in a way that is then reflected in the token's total supply.
 */
contract BaseBEP20Token is StandardBurnableToken, PausableToken, DetailedBEP20 {

  constructor(
    uint256 _initialAmount,
    uint8 _decimalUnits,
    string _tokenName,
    string _tokenSymbol
  ) DetailedBEP20(_tokenName, _tokenSymbol, _decimalUnits) public {
    totalSupply_ = _initialAmount;
    balances[msg.sender] = totalSupply_;
  }

  // override the burnable token's "Burn" function: don't allow tokens to
  // be burned when paused
  function _burn(address _from, uint256 _value) internal whenNotPaused {
    super._burn(_from, _value);
  }

}


