    "/contracts/BITDAO.sol": {
      "content": "// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.6;

import "./utils/Ownable.sol";
import "./utils/SafeERC20.sol";
import "./utils/IERC20.sol";
import "./utils/IPancakeRouter02.sol";
import "./utils/IPancakeFactory.sol";
import "./utils/SafeMath.sol";
import "./utils/Address.sol";
import "./invite.sol";

contract BITDAO is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address;

    //to recieve ETH from router when swaping
    receive() external payable {}

    constructor(
        address _marketing,
        address _team,
        address _lp,
        address _root,
        address _swap,
        address _sharehodler,
        address _inviteContract
    ) {
        IPancakeRouter02 _router = IPancakeRouter02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        BUSD = address(0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56);
        marketingWalletAddress = _marketing;
        teamWalletAddress = _team;
        lpRewardAddress = _lp;
        rootAddress = _root;
        swapAddress = _swap;
        shareholderAddress = _sharehodler;
        inviteContract=Invite(_inviteContract);
        router = _router;
        pairAddress = IPancakeFactory(_router.factory()).createPair(
            address(this),
            BUSD
        );

        isExcludedFromFee[marketingWalletAddress] = true;
        isExcludedFromFee[teamWalletAddress] = true;
        isExcludedFromFee[lpRewardAddress] = true;
        isExcludedFromFee[rootAddress] = true;
        isExcludedFromFee[swapAddress] = true;
        isExcludedFromFee[shareholderAddress] = true;

        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[address(this)] = true;

        isMarketPair[address(_router)] = true;
        isMarketPair[address(pairAddress)] = true;
        isExcludedAddLiquidityLimit[address(this)] = true;

        addLiquidityEnabled = false;
        swapEnabled = false;
        takeFeeDisabled = false;

        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    string private _name = "BITDAO";
    string private _symbol = "BIT";
    uint8 private _decimals = 18;

    address private BUSD;
    Invite inviteContract;

    address public shareholderAddress;
    address public marketingWalletAddress; // Marketing Address
    address public teamWalletAddress; // Team Address
    address public lpRewardAddress; // LP Reward Address
    address public rootAddress;
    address private swapAddress;

    uint256 public transferFeeBalance;
    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) public isExcludedFromFee;
    mapping(address => bool) public isExcludedAddLiquidityLimit;
    mapping(address => bool) public isMarketPair;

    bool public takeFeeDisabled;
    bool public swapEnabled;
    bool public addLiquidityEnabled;

    function setInviteContract(address _contract) public onlyOwner {
        inviteContract = Invite(_contract);
    }

    function rescueToken(address tokenAddress, uint256 tokens)
        public
        onlyOwner
        returns (bool success)
    {
        return IERC20(tokenAddress).transfer(msg.sender, tokens);
    }

    function rescueBNB(address payable _recipient) public onlyOwner {
        _recipient.transfer(address(this).balance);
    }

    address public immutable deadAddress =
        0x000000000000000000000000000000000000dEaD;

    uint256 private _totalSupply = 210000 * 10**_decimals;

    IPancakeRouter02 public router;

    address public pairAddress;

    bool inSwapAndLiquify;

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        require(
            spender != address(router) ||
                addLiquidityEnabled ||
                isExcludedAddLiquidityLimit[owner],
            "can't add liquidity"
        );

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function setMarketPairStatus(address account, bool newValue)
        public
        onlyOwner
    {
        isMarketPair[account] = newValue;
    }

    function setTakeFeeDisabled(bool _value) public onlyOwner {
        takeFeeDisabled = _value;
    }

    function setSwapEnabled(bool _value) public onlyOwner {
        swapEnabled = _value;
    }

    function setAddLiquidityEnabled(bool _value) public onlyOwner {
        addLiquidityEnabled = _value;
    }

    function setIsExcludedFromFee(address account, bool newValue)
        public
        onlyOwner
    {
        isExcludedFromFee[account] = newValue;
    }

    function setIsExcludedAddLiquidityLimit(address account, bool newValue)
        public
        onlyOwner
    {
        isExcludedAddLiquidityLimit[account] = newValue;
    }

    function setWalletAddress(
        address _marketing,
        address _team,
        address _lp,
        address _swap,
        address _shareholder,
        address _root
    ) external onlyOwner {
        marketingWalletAddress = _marketing;
        teamWalletAddress = _team;
        lpRewardAddress = _lp;
        shareholderAddress = _shareholder;
        swapAddress = _swap;
        rootAddress = _root;

        isExcludedFromFee[marketingWalletAddress] = true;
        isExcludedFromFee[teamWalletAddress] = true;
        isExcludedFromFee[lpRewardAddress] = true;
        isExcludedFromFee[rootAddress] = true;
        isExcludedFromFee[swapAddress] = true;
        isExcludedFromFee[shareholderAddress] = true;
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        if (amount == 0) {
            _balances[recipient] = _balances[recipient].add(amount);
            return;
        }

        if (inSwapAndLiquify) {
            _basicTransfer(sender, recipient, amount);
        } else {
            _balances[sender] = _balances[sender].sub(
                amount,
                "Insufficient Balance"
            );

            if (
                swapEnabled &&
                isMarketPair[recipient] &&
                balanceOf(address(this)) > amount.div(2)
            ) {
                swapTokensForBUSD(amount.div(2));
            }

            uint256 finalAmount = (isExcludedFromFee[sender] ||
                isExcludedFromFee[recipient]) || takeFeeDisabled
                ? amount
                : takeFee(sender, recipient, amount);

            _balances[recipient] = _balances[recipient].add(finalAmount);

            emit Transfer(sender, recipient, finalAmount);
        }

        if (fromAddress == address(0)) fromAddress = sender;
        if (toAddress == address(0)) toAddress = recipient;
        if (fromAddress != pairAddress) setShare(fromAddress);
        if (toAddress != pairAddress) setShare(toAddress);
        fromAddress = sender;
        toAddress = recipient;
    }

    function _basicTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(
            amount,
            "Insufficient Balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function swapTokensForBUSD(uint256 tokenAmount) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = BUSD;

        _approve(address(this), address(router), tokenAmount);

        // make the swap
        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            swapAddress,
            block.timestamp
        );
    }

    /**
     * @notice It allows the admin to recover wrong tokens sent to the contract
     * @param _tokenAddress: the address of the token to withdraw
     */
    function rescueTokens(address _tokenAddress) external onlyOwner {
        require(_tokenAddress != address(this), "cannot be this token");
        IERC20(_tokenAddress).safeTransfer(
            address(msg.sender),
            IERC20(_tokenAddress).balanceOf(address(this))
        );
    }

    function takeFee(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (uint256) {
        uint256 feeAmount = 0;

        if (isMarketPair[sender]) {
            // buy
            //burn
            uint256 burnAmount = amount.mul(1).div(100);

            //transfer to burn
            _balances[deadAddress] = _balances[deadAddress].add(burnAmount);
            emit Transfer(sender, deadAddress, burnAmount);

            //reward to team
            uint256 shareAmount = amount.mul(1).div(100);

            //transfer to shareholder
            _balances[shareholderAddress] = _balances[shareholderAddress].add(
                shareAmount
            );
            emit Transfer(sender, shareholderAddress, shareAmount);

            feeAmount = feeAmount.add(burnAmount).add(shareAmount);
        } else if (isMarketPair[recipient]) {
            // sell

            //burn
            uint256 burnAmount = amount.mul(1).div(100);
            //transfer to burn
            _balances[deadAddress] = _balances[deadAddress].add(burnAmount);
            emit Transfer(sender, deadAddress, burnAmount);

            //reward to team
            uint256 teamAmount = amount.mul(3).div(100);
            //transfer to team
            _balances[teamWalletAddress] = _balances[teamWalletAddress].add(
                teamAmount
            );
            emit Transfer(sender, teamWalletAddress, teamAmount);

            //reward to marketing
            uint256 marketingAmount = amount.mul(1).div(100);
            //transfer to marketing
            _balances[marketingWalletAddress] = _balances[
                marketingWalletAddress
            ].add(marketingAmount);
            emit Transfer(sender, marketingWalletAddress, marketingAmount);

            //reward to lp
            uint256 lpAmount = amount.mul(2).div(100);
            //transfer to marketing
            _balances[lpRewardAddress] = _balances[lpRewardAddress].add(
                lpAmount
            );
            emit Transfer(sender, lpRewardAddress, lpAmount);

            feeAmount = feeAmount
                .add(burnAmount)
                .add(teamAmount)
                .add(marketingAmount)
                .add(lpAmount);

            //reward to inviter
            uint256 inviterAmount = 0;

            uint8[5] memory rate = [15, 10, 8, 5, 2];
            address inviter = inviteContract.getInviters(sender);
            uint256 i = 0;
            while (i < 5) {
                uint256 rewardAmount = amount.mul(rate[i]).div(1000);
                if (inviter != address(0)) {
                    _balances[inviter] = _balances[inviter].add(rewardAmount);
                    emit Transfer(sender, inviter, rewardAmount);
                } else {
                    _balances[rootAddress] = _balances[rootAddress].add(
                        rewardAmount
                    );
                    emit Transfer(sender, rootAddress, rewardAmount);
                }
                inviterAmount = inviterAmount.add(rewardAmount);
                inviter = inviteContract.getInviters(inviter);
                i++;
            }

            feeAmount = feeAmount.add(inviterAmount);
        } else {
            // transfer

            //burn
            uint256 burnAmount = amount.mul(5).div(100);

            //transfer to burn
            _balances[deadAddress] = _balances[deadAddress].add(burnAmount);
            emit Transfer(sender, deadAddress, burnAmount);

            feeAmount = feeAmount.add(burnAmount);
        }

        return amount.sub(feeAmount);
    }

    address[] public shareholders;
    address private fromAddress;
    address private toAddress;

    mapping(address => bool) private _updated;
    mapping(address => uint256) shareholderIndexes;

    function setShare(address shareholder) private {
        if (_updated[shareholder]) {
            if (IERC20(pairAddress).balanceOf(shareholder) == 0)
                quitShare(shareholder);
            return;
        }
        if (IERC20(pairAddress).balanceOf(shareholder) == 0) return;
        addShareholder(shareholder);
        _updated[shareholder] = true;
    }

    function getShareholderLength() public view returns (uint256) {
        return shareholders.length;
    }

    function addShareholder(address shareholder) internal {
        shareholderIndexes[shareholder] = shareholders.length;
        shareholders.push(shareholder);
    }

    function quitShare(address shareholder) private {
        removeShareholder(shareholder);
        _updated[shareholder] = false;
    }

    function removeShareholder(address shareholder) internal {
        shareholders[shareholderIndexes[shareholder]] = shareholders[
            shareholders.length - 1
        ];
        shareholderIndexes[
            shareholders[shareholders.length - 1]
        ] = shareholderIndexes[shareholder];
        shareholders.pop();
    }
}
"
    },
