    "/contracts/invite.sol": {
      "content": "// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.6;

import "./utils/Ownable.sol";
import "./utils/SafeMath.sol";

contract Invite is Context, Ownable {
    using SafeMath for uint256;
    address public rootAddress;

    mapping(address => uint256) public firendCount;
    mapping(address => uint256) public groupCount;
    mapping(address => uint256) public rewardCount;
    mapping(address => bool) public isCounter;
    mapping(address => address) public inviters;

    constructor(address _root) {
        rootAddress = _root;
    }

    function setRootAddress(address _root) public onlyOwner {
        rootAddress = _root;
    }

    function setIsCounter(address _counter, bool _isCounter) public onlyOwner {
        isCounter[_counter] = _isCounter;
    }

    function getInviters(address _address) external view returns (address) {
        return inviters[_address];
    }

    function addReward(address _address, uint256 amount) external {
        require(isCounter[_msgSender()], "not counter");
        rewardCount[_address] = rewardCount[_address].add(amount);
    }

    function setInviter(address inviter) public {
        require(inviter != _msgSender(), "no self");
        require(inviters[_msgSender()] == address(0), "hasinviter");
        require(
            inviters[inviter] != address(0) || inviter == rootAddress,
            "inviter must have inviter or root"
        );
        inviters[_msgSender()] = inviter;

        //invite count;
        firendCount[inviter] = firendCount[inviter].add(1);
        address i = inviter;
        while (i != address(0)) {
            groupCount[i] = groupCount[i].add(1);
            i = inviters[i];
        }
    }
}
