// File: Dvision1155Dvi.sol



pragma solidity ^0.6.12;








contract Dvision1155Dvi is ERC1155, ERC1155Holder, AccessControl, ReentrancyGuard
{    
    using SafeMath for uint256;
    
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    
    uint private _currentTokenId;
    uint private _currentOrderId;
    uint private _currentSaleAmount;
    uint private _currentUserId;
    
    
    address payable private fee_addr;
    uint256 private fee_percent;
    uint256 private fee_on_mint;
    
    IERC20 private currencyToken;
    
    mapping(uint => bool) public _itemExists;
    mapping(address => mapping(uint => Item)) public _items;
    mapping(uint => uint256) public _itemSupply;
    mapping(uint => Order) public _itemForSale;
    
    mapping(uint=>address) public _tokenUsers;
    

    struct Item 
    { 
        uint256 amount;
        uint256 orderId;
    }
    
    struct Order
    {
        address seller;
        uint256 tokenId;
        uint256 price;
        uint256 currency;
        uint256 sell_amount;
        bool forSale;
    }
    
    event TokenChange(address indexed _old, address indexed _new);
    event ItemSelled(uint256 _tokenId);
    event MintEvent(uint256 _tokenId);
    
    event FeeAddressChange(address indexed _new);
    event FeePercentChange(uint256 _percent);
    event FeeOnMintChange(uint256 _fee);
    
    event SetUser(address indexed _new);
    
    event URIChange(string _new);
    
    modifier onlyManager{
        require(hasRole(MANAGER_ROLE, msg.sender), "You are not manager.");
        _;
    }
    
    
    constructor(address _erc20, address payable _fee_addr, string memory _uri) 
    ERC1155("DVI 1155") ERC1155Holder() ReentrancyGuard() public 
    {
        require(_erc20 != address(0), "_erc20 is a zero address.");
        require(_fee_addr != address(0), "_fee_addr is a zero address.");
        
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        
        currencyToken = IERC20(_erc20);
        fee_addr = _fee_addr;
        _setURI(_uri);
    }
    
    
    function Set_20Token(address _new) onlyManager external 
    {
        require(_new != address(0) ,"_new is a zero address.");
        
        emit TokenChange(address(currencyToken), _new);
        currencyToken = IERC20(_new);
    }
    
    function Show_20Token() external view returns(address)
    {
        return address(currencyToken);
    }
    
    function Set_URI (string memory _new_uri) onlyManager external 
    {
        emit URIChange(_new_uri);
       _setURI(_new_uri);
    }
    
    function Mint(uint256 _amount) external payable returns(uint256)
    {
        require(_amount > 0, "ERC1155 : Amount of token to create must be bigger than 0");
        require(msg.value >= fee_on_mint, "fee is not enough");
        
        Item memory _item = Item({
            amount : _amount,
            orderId : 0
        });
        
        uint _id = _getNextTokenId();
        _mint(msg.sender, _id, _amount, "");
        
        _incrementTokenId();
        
        _items[msg.sender][_id] = _item;
        _itemExists[_id] = true;
        
        _itemSupply[_id] = _amount;
        
        this.Set_User(msg.sender);
        
        fee_addr.transfer(msg.value);
        
        emit MintEvent(_id);
        return (_id);
    }
    
    function MintTo(address _to, uint256 _amount) onlyManager external returns(uint256)
    {
        require(_amount > 0, "ERC1155 : Amount of token to create must be bigger than 0");
        
        Item memory _item = Item({
            amount : _amount,
            orderId : 0
        });
        
        uint _id = _getNextTokenId();
        _mint(_to, _id, _amount, "");
        
        _incrementTokenId();
        
        _items[_to][_id] = _item;
        _itemExists[_id] = true;

        _itemSupply[_id] = _amount;
        
        this.Set_User(_to);
        
        emit MintEvent(_id);
        return (_id);
    }
    
    
    function _getNextTokenId() private view returns (uint256) 
    {
        return _currentTokenId.add(1);
    }
    
    function _incrementTokenId() private 
    {
        _currentTokenId++;
    }
    
    function Sell_Item(address _marketaddress, uint256 _tokenId, uint256 _price, uint256 _currency, uint256 _sell_amount) external 
    {
        require(_marketaddress != address(0), "_marketaddress is a zero address.");
        require(_sell_amount > 0, "_sell_amount should be greater than zero.");
        require(balanceOf(msg.sender, _tokenId) >= _sell_amount, "Not enough balance.");
        
        if(_items[msg.sender][_tokenId].orderId > 0) //selled many times
        {
            if(!_itemForSale[_items[msg.sender][_tokenId].orderId].forSale) 
            {
                _currentSaleAmount++;
            }
            
            _itemForSale[_items[msg.sender][_tokenId].orderId].seller = msg.sender;
            _itemForSale[_items[msg.sender][_tokenId].orderId].tokenId = _tokenId;
            _itemForSale[_items[msg.sender][_tokenId].orderId].sell_amount = _sell_amount;
            _itemForSale[_items[msg.sender][_tokenId].orderId].price = _price;
            _itemForSale[_items[msg.sender][_tokenId].orderId].currency = _currency;
            _itemForSale[_items[msg.sender][_tokenId].orderId].forSale = true;
                
            setApprovalForAll(_marketaddress, true);
                
            emit ItemSelled(_tokenId);
            
            /*
            if(!_itemForSale[_items[msg.sender][_tokenId].orderId].forSale) 
            {
                uint _orderId = _getNextOrderId();
                _items[msg.sender][_tokenId].orderId = _orderId;
                
                _itemForSale[_orderId].seller = msg.sender;
                _itemForSale[_orderId].tokenId = _tokenId;
                _itemForSale[_orderId].sell_amount = _sell_amount;
                _itemForSale[_orderId].price = _price;
                _itemForSale[_orderId].currency = _currency;
                _itemForSale[_orderId].forSale = true;
                
                setApprovalForAll(_marketaddress, true);
                
                _currentSaleAmount++;
                _incrementOrderId();
                
                emit ItemSelled(_tokenId);
            }
            else //renewed selling data
            {
                _itemForSale[_items[msg.sender][_tokenId].orderId].seller = msg.sender;
                _itemForSale[_items[msg.sender][_tokenId].orderId].tokenId = _tokenId;
                _itemForSale[_items[msg.sender][_tokenId].orderId].sell_amount = _sell_amount;
                _itemForSale[_items[msg.sender][_tokenId].orderId].price = _price;
                _itemForSale[_items[msg.sender][_tokenId].orderId].currency = _currency;
                _itemForSale[_items[msg.sender][_tokenId].orderId].forSale = true;
                
                setApprovalForAll(_marketaddress, true);
                
                emit ItemSelled(_tokenId);
            }
            */
        }
        else // selled first time
        {
            uint _orderId = _getNextOrderId();
            _items[msg.sender][_tokenId].orderId = _orderId;
            
            _itemForSale[_orderId].seller = msg.sender;
            _itemForSale[_orderId].tokenId = _tokenId;
            _itemForSale[_orderId].sell_amount = _sell_amount;
            _itemForSale[_orderId].price = _price;
            _itemForSale[_orderId].currency = _currency;
            _itemForSale[_orderId].forSale = true;
            
            setApprovalForAll(_marketaddress, true);
            
            _currentSaleAmount++;
            _incrementOrderId();
            
            emit ItemSelled(_tokenId);
        }        
    }
    
    //_from : seller, _to : buyer
    function Transaction_Item(address _from, address _to, uint256 _tokenId, uint256 _amount) nonReentrant external payable 
    { 
        address payable owner = address(uint160(_from));
        require(owner != msg.sender, "Owner can't buy their tokens.");
        require(owner != address(0), "Owner must not be zero address.");
        
        require(msg.value >= _itemForSale[_items[_from][_tokenId].orderId].price * _amount, "Not Enough Pays.");
        require(_itemForSale[_items[_from][_tokenId].orderId].forSale, "This order is not for sale.");
        require(_itemForSale[_items[_from][_tokenId].orderId].sell_amount > 0, "Not For Sale.");
        require(_itemForSale[_items[_from][_tokenId].orderId].sell_amount >= _amount, "Not Enough Tokens.");
        require(_itemForSale[_items[_from][_tokenId].orderId].currency == 0, "This token can buy on ethereum.");
        
        //approve(_to, _order.itemId);
        owner.transfer(_itemForSale[_items[_from][_tokenId].orderId].price * _amount * (100 - fee_percent) / 100);
        fee_addr.transfer(_itemForSale[_items[_from][_tokenId].orderId].price * _amount * fee_percent / 100);
        
        safeTransferFrom(owner, _to, _tokenId, _amount, "");
        
        this.Set_User(_to);
    }
    
    //_from : seller, _to : buyer
    function Transaction_Item_WithToken(address _from, address _to, uint256 _tokenId, uint256 _amount, uint256 _price) nonReentrant external 
    {
        address owner = address(uint160(_from));
        require(owner != msg.sender, "Owner can't buy their tokens.");
        require(owner != address(0), "Owner must not be zero address.");
    
        require(_price >= _itemForSale[_items[_from][_tokenId].orderId].price * _amount, "Not Enough Pays.");
        require(_itemForSale[_items[_from][_tokenId].orderId].forSale, "This order is not for sale.");
        require(_itemForSale[_items[_from][_tokenId].orderId].sell_amount > 0, "Not For Sale.");
        require(_itemForSale[_items[_from][_tokenId].orderId].sell_amount >= _amount, "Not Enough Tokens");
        require(_itemForSale[_items[_from][_tokenId].orderId].currency == 1, "This token can buy on DVI token.");
        
        //approve(_to, _order.itemId);
        bool result = currencyToken.transferFrom(_to, owner, _price);
        require(result, "transfer token failed");
        
        safeTransferFrom(owner, _to, _tokenId, _amount, "");
        
        this.Set_User(_to);
    }
    
    
    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    )
        public
        override
    {
        require(to != address(0), "ERC1155: transfer to the zero address");
        require(
            from == _msgSender() || isApprovedForAll(from, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );

        _safeTransfer(from, to, id, amount, data);
        
        _itemForSale[_items[from][id].orderId].sell_amount -= amount;
        _items[from][id].amount -= amount;
        
        _items[to][id].amount += amount;
        
        if(_items[from][id].amount == 0 || _itemForSale[_items[from][id].orderId].sell_amount == 0)
        {
            if(_itemForSale[_items[from][id].orderId].forSale)//_currentSaleAmount > 0)
            {
                _currentSaleAmount--;
            }
            
            _itemForSale[_items[from][id].orderId].forSale = false;
        }
    }
    
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    )
        public
        override
    {
        require(ids.length == amounts.length, "ERC1155: ids and amounts length mismatch");
        require(to != address(0), "ERC1155: transfer to the zero address");
        require(
            from == _msgSender() || isApprovedForAll(from, _msgSender()),
            "ERC1155: transfer caller is not owner nor approved"
        );
        
        _safeBatchTransfer(from, to, ids, amounts, data);
        
        for(uint256 i = 0; i < ids.length; ++i)
        {
            uint256 id = ids[i];
            uint256 amount = amounts[i];
            
            _itemForSale[_items[from][id].orderId].sell_amount -= amount;
            _items[from][id].amount -= amount;
            
            _items[to][id].amount += amount;
            
            if(_items[from][id].amount == 0 || _itemForSale[_items[from][id].orderId].sell_amount == 0)
            {
                if(_itemForSale[_items[from][id].orderId].forSale)//_currentSaleAmount > 0)
                {
                    _currentSaleAmount--;
                }
                
                _itemForSale[_items[from][id].orderId].forSale = false;
            }
            
        }
       
    }
    
    
    function _getNextOrderId() private view returns (uint256) 
    {
        return _currentOrderId.add(1);
    }
    
    function _incrementOrderId() private 
    {
        _currentOrderId++;
    }
    
    
    function Get_Orders(address _seller, uint _tokenId) external view returns(uint256 _id, uint256 _price, uint256 _currency, uint256 _amount, address _owner)
    {
        Order storage _order = _itemForSale[_items[_seller][_tokenId].orderId];
        
        _id = _tokenId;
        _price = _order.price;
        _currency = _order.currency;
        _amount = _order.sell_amount;
        _owner = _order.seller;
    }
    
    function TokensOfOwner(address _owner) external view returns(uint[] memory)
    {
        uint256 _balance = 0;
        
        uint256 _maxTokenId = _currentTokenId;
        uint256 _tokenId = 0;
        
        
        for(_tokenId = 1; _tokenId < _maxTokenId + 1; _tokenId++)
        {
            if(balanceOf(_owner, _tokenId) > 0)
            {
                _balance++;
            }
        }
        
        if(_balance == 0)
        {
            return new uint256[](0);
        }
        else
        {
            uint[] memory _result = new uint[](_balance);
            uint256 _idx = 0;
            
            for(_tokenId = 1; _tokenId < _maxTokenId + 1; _tokenId++)
            {
                if(balanceOf(_owner, _tokenId) > 0)
                {
                    _result[_idx] = _tokenId;
                    _idx++;
                }
            }
            
            return _result;
        }
    }
    
    function Get_SellingItems() external view returns(uint[] memory, address[] memory, uint[] memory, uint[] memory, uint[] memory)
    {
        uint[] memory _result_Id = new uint[](_currentSaleAmount);
        address[] memory _result_Addr = new address[](_currentSaleAmount);
        uint[] memory _result_Price = new uint[](_currentSaleAmount); 
        uint[] memory _result_Currency = new uint[](_currentSaleAmount);
        uint[] memory _result_SellAmount = new uint[](_currentSaleAmount);

        uint256 _maxOrderId = _currentOrderId;
        uint256 _idx = 0;
        
        uint256 _orderId = 0;
        for(_orderId = 1; _orderId < _maxOrderId + 1; _orderId++)
        {
            if(_itemForSale[_orderId].forSale)
            {
                _result_Id[_idx] = _orderId;
                _result_Addr[_idx] = _itemForSale[_orderId].seller;
                _result_Price[_idx] = _itemForSale[_orderId].price;
                _result_Currency[_idx] = _itemForSale[_orderId].currency;
                _result_SellAmount[_idx] = _itemForSale[_orderId].sell_amount;
                _idx++;
            }
        }   
            
        return (_result_Id, _result_Addr, _result_Price, _result_Currency, _result_SellAmount);
    }
    
    
    //// About Fee
    function Change_Fee_Addr(address payable _addr) onlyManager external returns(address)
    {
        require(_addr != address(0), "_addr is a zero address.");
        
        fee_addr = _addr;
        
        emit FeeAddressChange(_addr);
        
        return(fee_addr);
    }
    
    function Show_Fee_Addr() external view returns(address)
    {
        return(fee_addr);
    }
    
    function Change_Fee_Percent(uint256 _percent) onlyManager external returns(uint256)
    {
        require(_percent <= 100, "_percent exeed 100.");

        fee_percent = _percent;
        
        emit FeePercentChange(_percent);
        
        return(fee_percent);
    }
    
    function Show_Fee_Percent() external view returns(uint256){
        return(fee_percent);
    }
    
    function Change_Fee_On_Mint(uint256 _new) onlyManager external returns(uint256)
    {
        fee_on_mint = _new;
        
        emit FeeOnMintChange(_new);
        
        return(fee_on_mint);
    }
    
    function Show_Fee_On_Mint() external view returns(uint256)
    {
        return(fee_on_mint);
    }
    
    
    function Set_User(address _user) external
    {
        if(!Contain_User(_user))
        {
            _currentUserId++;
            _tokenUsers[_currentUserId] = _user;
            
            emit SetUser(_user);
        }
        
    }
    
    function Contain_User(address _user) private view returns(bool)
    {
        bool _result = false;
        
        uint _i = 1;
        for(_i = 1; _i < _currentUserId + 1; _i++)
        {
            if(_tokenUsers[_i] == _user)
            {
                _result = true;
                break;
            }
        }
        
        return _result;
    }
    
    function ownerOf(uint _tokenId) external view returns(address[] memory, uint256[] memory)
    {
        
        uint _holders = 0;
        uint _i = 1;
        
        for(_i = 1; _i < _currentUserId + 1; _i++)
        {
            if(balanceOf(_tokenUsers[_i], _tokenId) > 0)
            {
                _holders++;
            }
        }
        
        address[] memory _addr = new address[](_holders);
        uint256[] memory _amount = new uint256[](_holders);
        
        
        uint _idx = 0;
        for(_i = 1; _i < _currentUserId + 1; _i++)
        {
            uint256 _balance = balanceOf(_tokenUsers[_i], _tokenId);
            
            if(_balance > 0)
            {
                _addr[_idx] = _tokenUsers[_i];
                _amount[_idx] = _balance;
                
                _idx++;
            }
        }
        
        return(_addr, _amount);
    }
    
    
}
