    "/contracts/MetaliveMarketplace.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./PlatformOwnable.sol";
import "erc721a/contracts/ERC721A.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

error PriceNotMet(address nftAddress, uint256 marketplaceId, uint256 price);
error NotOwner();
error PriceMustBeAboveZero();
error NotApprovedForMarketplace();
error NotAllowAddressZero();
error SharesMustBeAboveZero();
error ShareBelowOrAboveTotalShare(uint256 share);
error CurrentTotalShareAboveMaximum(uint256 share);
error ListingIsCanceled();

contract MetaliveMarketplace is
    ReentrancyGuard,
    Ownable,
    PlatformOwnable,
    AccessControl,
    IERC721Receiver
{
    using Counters for Counters.Counter;
    Counters.Counter private marketplaceIds;
    Counters.Counter private totalMarketplaceItemsSold;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    address paymentTokenAddress;

    // NFT contract address => creator address
    mapping(address => address) public nftCreators;
    // NFT contract address => creator share
    uint256 public nftCreatorShare = 100;
    // mapping(address => uint256) public nftCreatorsShares;
    // NFT contract address
    address[] public nftCollections;

    // shareholder address
    address[] public shareholders;
    // shareholder address => share
    mapping(address => uint256) public shares;
    // total shares
    uint256 public totalShares = 10000;
    uint256 public totalSharesDecimalPlace = 2;
    uint256 public currentTotalShares = 0;
    // marketplaceId => Listing
    mapping(uint256 => Listing) public marketplaceIdToListingItem;

    struct Listing {
        uint256 marketplaceId;
        address nftAddress;
        uint256 tokenId;
        address seller;
        address owner;
        uint256 listPrice;
        uint256 totalShares;
        address nftCreatorAddress;
        uint256 nftCreatorShare;
        address[] shareholders;
        uint256[] shares;
        uint256 listTime;
        uint256 sellTime;
        bool isCanceled;
    }

    event ListingCreated(
        uint256 marketplaceId,
        address nftAddress,
        uint256 tokenId,
        address seller,
        address owner,
        uint256 listPrice,
        uint256 listTime,
        address paymentTokenAddress
    );

    event ListingSold(
        uint256 marketplaceId,
        address nftAddress,
        uint256 tokenId,
        address newOwner,
        uint256 listPrice,
        uint256 amountSellerReceived,
        uint256 amountNftCreatorReceived,
        address[] shareholdersAddress,
        uint256[] amountShareholdersReceived,
        uint256 sellTime
    );

    event ListingCanceled(
        uint256 marketplaceId,
        address nftAddress,
        uint256 tokenId,
        address newOwner,
        bool isCanceled
    );

    event CollectionAdded(
        address nftAddress,
        address nftCreatorAddress,
        address adminAddress
    );

    event CollectionRemoved(address nftAddress, address adminAddress);

    event ShareholdersSharesUpdated(address shareholderAddress, uint256 share);

    event NftCreatorSharesUpdated( uint256 share);

    modifier isShareAboveZero(uint256 share) {
        if (share <= 0) {
            revert SharesMustBeAboveZero();
        }
        _;
    }

    modifier isPriceAboveZero(uint256 price) {
        if (price <= 0) {
            revert PriceMustBeAboveZero();
        }
        _;
    }

    modifier isOwner(
        address nftAddress,
        uint256 tokenId,
        address spender
    ) {
        IERC721 nft = IERC721(nftAddress);
        address owner = nft.ownerOf(tokenId);
        if (spender != owner) {
            revert NotOwner();
        }
        _;
    }

    modifier isAddressZero(address cAddress) {
        if (cAddress == address(0)) {
            revert NotAllowAddressZero();
        }
        _;
    }

    modifier isExceededTotalShare(uint256 share) {
        if (share < 0 && share > totalShares) {
            revert ShareBelowOrAboveTotalShare(share);
        }
        _;
    }

    modifier isCancel(uint256 marketplaceItemId) {
        if (marketplaceIdToListingItem[marketplaceItemId].isCanceled == true) {
            revert ListingIsCanceled();
        }
        _;
    }

    constructor(
        address[] memory _shareholders,
        uint256[] memory _shares,
        uint256 _nftCreatorShares,
        address _contractOwner,
        address _paymentTokenAddress
    )
        PlatformOwnable(_contractOwner)
        isAddressZero(_contractOwner)
        isAddressZero(_paymentTokenAddress)
        isExceededTotalShare(_nftCreatorShares)
    {
        require(
            _shareholders.length == _shares.length,
            "shareholders and shares length mismatch"
        );
        require(_shareholders.length > 0, "no shareholders");

         nftCreatorShare = _nftCreatorShares;
        currentTotalShares += nftCreatorShare;
        for (uint256 i = 0; i < _shareholders.length; i++) {
            _addShareholder(_shareholders[i], _shares[i]);
            currentTotalShares+= _shares[i];
        }
        if(currentTotalShares>=9999)
        {
            revert CurrentTotalShareAboveMaximum(currentTotalShares);
        }
        _setupRole(DEFAULT_ADMIN_ROLE, _contractOwner);
        _grantRole(ADMIN_ROLE, _contractOwner);
        paymentTokenAddress = _paymentTokenAddress;

    }

    // add shareholder address
    function _addShareholder(
        address _account,
        uint256 _accountShares
    )
        private
        nonReentrant
        onlyOwner
        isAddressZero(_account)
        isShareAboveZero(_accountShares)
    {
        shareholders.push(_account);
        shares[_account] = _accountShares;
    }

    // add NFT collection address to get supported
    function addCollection(
        address _nft,
        address _nftCreator
        // uint256 _nftCreatorShare
    )
        external
        onlyRole(ADMIN_ROLE)
        isAddressZero(_nft)
        isAddressZero(_nftCreator)
    {
        nftCreators[_nft] = _nftCreator;
        // nftCreatorsShares[_nft] = _nftCreatorShare;
        nftCollections.push(_nft);
        emit CollectionAdded(_nft, _nftCreator, _msgSender());
    }

    // remove NFT collection address to get supported
    function removeCollection(
        address _nft
    ) external onlyRole(ADMIN_ROLE) isAddressZero(_nft) {
        delete nftCreators[_nft];
        // delete nftCreatorsShares[_nft];
        for (uint256 i = 0; i < nftCollections.length; i++) {
            if (nftCollections[i] == _nft) {
                nftCollections[i] = nftCollections[nftCollections.length - 1];
                nftCollections.pop();
            }
        }

        emit CollectionRemoved(_nft, _msgSender());
    }

    // create NFT listing
    function createListing(
        uint256 tokenId,
        address nftAddress,
        uint256 price
    )
        external
        nonReentrant
        isOwner(nftAddress, tokenId, _msgSender())
        isPriceAboveZero(price)
    {
        marketplaceIds.increment();
        uint256 marketplaceItemId = marketplaceIds.current();
        uint256[] memory sharesList = new uint256[](shareholders.length);
        for (uint256 i = 0; i < shareholders.length; i++) {
            sharesList[i] = shares[shareholders[i]];
        }
        marketplaceIdToListingItem[marketplaceItemId] = Listing(
            marketplaceItemId,
            nftAddress,
            tokenId,
            _msgSender(),
            address(this),
            price,
            totalShares,
            nftCreators[nftAddress],
            nftCreatorShare,
            // nftCreatorsShares[nftAddress],
            shareholders,
            sharesList,
            block.timestamp,
            0,
            false
        );
        IERC721(nftAddress).safeTransferFrom(
            _msgSender(),
            address(this),
            tokenId
        );
        emit ListingCreated(
            marketplaceItemId,
            nftAddress,
            tokenId,
            _msgSender(),
            address(this),
            price,
            block.timestamp,
            paymentTokenAddress
        );
    }

    // buy NFT listing
    function buyListing(
        uint256 marketplaceItemId
    ) external nonReentrant isCancel(marketplaceItemId) {
        uint256 price = marketplaceIdToListingItem[marketplaceItemId].listPrice;

        IERC20 token = IERC20(paymentTokenAddress);
        if (token.allowance(_msgSender(), address(this)) < price) {
            revert NotApprovedForMarketplace();
        }

        uint256 tokenId = marketplaceIdToListingItem[marketplaceItemId].tokenId;
        address[] memory shareholdersList = marketplaceIdToListingItem[
            marketplaceItemId
        ].shareholders;
        uint256[] memory shareList = marketplaceIdToListingItem[
            marketplaceItemId
        ].shares;
        uint256[] memory distributeSharesList = new uint256[](
            shareholdersList.length
        );
        uint256 remainingAmount = price;

        // distribute to shareholders
        for (uint256 i = 0; i < shareholdersList.length; i++) {
            uint256 distributeAmount = (price * shareList[i]) / totalShares;
            IERC20(paymentTokenAddress).transferFrom(
                _msgSender(),
                shareholdersList[i],
                distributeAmount
            );
            distributeSharesList[i] = distributeAmount;
            remainingAmount -= distributeAmount;
        }
        // distribute to nft creator
        uint256 nftCreatorAmount = (price *
            marketplaceIdToListingItem[marketplaceItemId].nftCreatorShare) /
            totalShares;
        IERC20(paymentTokenAddress).transferFrom(
            _msgSender(),
            marketplaceIdToListingItem[marketplaceItemId].nftCreatorAddress,
            nftCreatorAmount
        );
        remainingAmount -= nftCreatorAmount;
        // distribute to seller
        IERC20(paymentTokenAddress).transferFrom(
            _msgSender(),
            marketplaceIdToListingItem[marketplaceItemId].seller,
            remainingAmount
        );
        address nftAddress = marketplaceIdToListingItem[marketplaceItemId]
            .nftAddress;

        // transfer NFT to buyer
        IERC721(nftAddress).safeTransferFrom(
            address(this),
            _msgSender(),
            tokenId
        );
        // update listing
        marketplaceIdToListingItem[marketplaceItemId].owner = _msgSender();
        marketplaceIdToListingItem[marketplaceItemId].sellTime = block
            .timestamp;
        emit ListingSold(
            marketplaceItemId,
            nftAddress,
            tokenId,
            _msgSender(),
            price,
            remainingAmount,
            nftCreatorAmount,
            shareholdersList,
            distributeSharesList,
            block.timestamp
        );
    }

    // cancel NFT listing
    function cancelListing(
        uint256 marketplaceItemId
    ) external nonReentrant isCancel(marketplaceItemId) {
        address seller = marketplaceIdToListingItem[marketplaceItemId].seller;
        if (seller != _msgSender()) {
            revert NotOwner();
        }
        uint256 tokenId = marketplaceIdToListingItem[marketplaceItemId].tokenId;
        address nftAddress = marketplaceIdToListingItem[marketplaceItemId]
            .nftAddress;
        // transfer NFT to buyer
        IERC721(nftAddress).safeTransferFrom(address(this), seller, tokenId);
        // update listing
        marketplaceIdToListingItem[marketplaceItemId].owner = _msgSender();
        marketplaceIdToListingItem[marketplaceItemId].isCanceled = true;

        emit ListingCanceled(
            marketplaceItemId,
            nftAddress,
            tokenId,
            _msgSender(),
            true
        );
    }

    // get NFT listing
    function getMarketItem(
        uint256 marketplaceItemId
    ) public view returns (Listing memory) {
        return marketplaceIdToListingItem[marketplaceItemId];
    }

    // get my NFT listing
    function getMyListedNFTs() public view returns (Listing[] memory) {
        uint256 totalListingCount = marketplaceIds.current();
        uint256 listingCount = 0;
        uint256 index = 0;

        for (uint256 i = 0; i < totalListingCount; i++) {
            if (marketplaceIdToListingItem[i + 1].owner == _msgSender()) {
                listingCount += 1;
            }
        }
        Listing[] memory items = new Listing[](listingCount);
        for (uint256 i = 0; i < totalListingCount; i++) {
            if (marketplaceIdToListingItem[i + 1].owner == _msgSender()) {
                uint256 currentId = marketplaceIdToListingItem[i + 1]
                    .marketplaceId;
                Listing memory currentItem = marketplaceIdToListingItem[
                    currentId
                ];
                items[index] = currentItem;
                index += 1;
            }
        }
        return items;
    }

    // update shareholder shares
    function updateShareholdersShares(
        address shareholderAddress,
        uint256 share
    ) external nonReentrant isExceededTotalShare(share) onlyRole(ADMIN_ROLE) returns (bool) {
        uint256 tempCurrTotalShares = currentTotalShares - shares[shareholderAddress];
        tempCurrTotalShares += share;
        if(tempCurrTotalShares>=9999)
        {
            revert CurrentTotalShareAboveMaximum(tempCurrTotalShares);
        }
        currentTotalShares = tempCurrTotalShares;
        shares[shareholderAddress] = share;
        emit ShareholdersSharesUpdated(shareholderAddress, share);
        return true;
    }

    // update NFT creator shares
    function updateNftCreatorShares(
        uint256 share
    )
        external
        nonReentrant
        isExceededTotalShare(share)
        onlyRole(ADMIN_ROLE)
        returns (bool)
    {
        uint256 tempCurrTotalShares = currentTotalShares - nftCreatorShare;
        tempCurrTotalShares += share;
        if(tempCurrTotalShares>=9999)
        {
            revert CurrentTotalShareAboveMaximum(tempCurrTotalShares);
        }
        currentTotalShares = tempCurrTotalShares;
        nftCreatorShare = share;
        emit NftCreatorSharesUpdated( share);
        return true;
    }

    // update payment address
    function updatePaymentTokenAddress(
        address _paymentTokenAddress
    ) external nonReentrant onlyRole(ADMIN_ROLE) returns (bool) {
        paymentTokenAddress = _paymentTokenAddress;
        return true;
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
    // function removeShareholder(
    //     address _account
    // ) public nonReentrant onlyOwner isAddressZero(_account) {
    //     require(shares[_account] >= 1, "");
    //     for (uint256 i = 0; i < shareholders.length; i++) {
    //         if (shareholders[i] == _account) {
    //             shareholders[i] = shareholders[shareholders.length - 1];
    //             shareholders.pop();
    //         }
    //     }
    //     totalShares -= shares[_account];
    //     shares[_account] = 0;
    // }
}
"
