    function getVotes(address account) public view virtual override returns (uint256) {
        uint256 pos = _checkpoints[account].length;
        unchecked {
            return pos == 0 ? 0 : _checkpoints[account][pos - 1].votes;
        }
    }

    /**
     * @dev Retrieve the number of votes for `account` at the end of `timepoint`.
     *
     * Requirements:
     *
     * - `timepoint` must be in the past
     */
    function getPastVotes(address account, uint256 timepoint) public view virtual override returns (uint256) {
        require(timepoint < clock(), "ERC20Votes: future lookup");
        return _checkpointsLookup(_checkpoints[account], timepoint);
    }

    /**
     * @dev Retrieve the `totalSupply` at the end of `timepoint`. Note, this value is the sum of all balances.
     * It is NOT the sum of all the delegated votes!
     *
     * Requirements:
     *
     * - `timepoint` must be in the past
     */
    function getPastTotalSupply(uint256 timepoint) public view virtual override returns (uint256) {
        require(timepoint < clock(), "ERC20Votes: future lookup");
        return _checkpointsLookup(_totalSupplyCheckpoints, timepoint);
    }

    /**
     * @dev Lookup a value in a list of (sorted) checkpoints.
     */
    function _checkpointsLookup(Checkpoint[] storage ckpts, uint256 timepoint) private view returns (uint256) {
        // We run a binary search to look for the last (most recent) checkpoint taken before (or at) `timepoint`.
        //
        // Initially we check if the block is recent to narrow the search range.
        // During the loop, the index of the wanted checkpoint remains in the range [low-1, high).
        // With each iteration, either `low` or `high` is moved towards the middle of the range to maintain the invariant.
        // - If the middle checkpoint is after `timepoint`, we look in [low, mid)
        // - If the middle checkpoint is before or equal to `timepoint`, we look in [mid+1, high)
        // Once we reach a single value (when low == high), we've found the right checkpoint at the index high-1, if not
        // out of bounds (in which case we're looking too far in the past and the result is 0).
        // Note that if the latest checkpoint available is exactly for `timepoint`, we end up with an index that is
        // past the end of the array, so we technically don't find a checkpoint after `timepoint`, but it works out
        // the same.
        uint256 length = ckpts.length;

        uint256 low = 0;
        uint256 high = length;

        if (length > 5) {
            uint256 mid = length - Math.sqrt(length);
            if (_unsafeAccess(ckpts, mid).fromBlock > timepoint) {
                high = mid;
            } else {
                low = mid + 1;
            }
        }

        while (low < high) {
            uint256 mid = Math.average(low, high);
            if (_unsafeAccess(ckpts, mid).fromBlock > timepoint) {
                high = mid;
            } else {
                low = mid + 1;
            }
        }

        unchecked {
            return high == 0 ? 0 : _unsafeAccess(ckpts, high - 1).votes;
        }
    }

    /**
     * @dev Delegate votes from the sender to `delegatee`.
     */
    function delegate(address delegatee) public virtual override {
        _delegate(_msgSender(), delegatee);
    }

    /**
     * @dev Delegates votes from signer to `delegatee`
     */
    function delegateBySig(
        address delegatee,
        uint256 nonce,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public virtual override {
        require(block.timestamp <= expiry, "ERC20Votes: signature expired");
        address signer = ECDSA.recover(
            _hashTypedDataV4(keccak256(abi.encode(_DELEGATION_TYPEHASH, delegatee, nonce, expiry))),
            v,
            r,
            s
        );
        require(nonce == _useNonce(signer), "ERC20Votes: invalid nonce");
        _delegate(signer, delegatee);
    }

    /**
     * @dev Maximum token supply. Defaults to `type(uint224).max` (2^224^ - 1).
     */
    function _maxSupply() internal view virtual returns (uint224) {
        return type(uint224).max;
    }

    /**
     * @dev Snapshots the totalSupply after it has been increased.
     */
    function _mint(address account, uint256 amount) internal virtual override {
        super._mint(account, amount);
        require(totalSupply() <= _maxSupply(), "ERC20Votes: total supply risks overflowing votes");

        _writeCheckpoint(_totalSupplyCheckpoints, _add, amount);
    }

    /**
     * @dev Snapshots the totalSupply after it has been decreased.
     */
    function _burn(address account, uint256 amount) internal virtual override {
        super._burn(account, amount);

        _writeCheckpoint(_totalSupplyCheckpoints, _subtract, amount);
    }

    /**
     * @dev Move voting power when tokens are transferred.
     *
     * Emits a {IVotes-DelegateVotesChanged} event.
     */
    function _afterTokenTransfer(address from, address to, uint256 amount) internal virtual override {
        super._afterTokenTransfer(from, to, amount);

        _moveVotingPower(delegates(from), delegates(to), amount);
    }

    /**
     * @dev Change delegation for `delegator` to `delegatee`.
     *
     * Emits events {IVotes-DelegateChanged} and {IVotes-DelegateVotesChanged}.
     */
    function _delegate(address delegator, address delegatee) internal virtual {
        address currentDelegate = delegates(delegator);
        uint256 delegatorBalance = balanceOf(delegator);
        _delegates[delegator] = delegatee;

        emit DelegateChanged(delegator, currentDelegate, delegatee);

        _moveVotingPower(currentDelegate, delegatee, delegatorBalance);
    }

    function _moveVotingPower(address src, address dst, uint256 amount) private {
        if (src != dst && amount > 0) {
            if (src != address(0)) {
                (uint256 oldWeight, uint256 newWeight) = _writeCheckpoint(_checkpoints[src], _subtract, amount);
                emit DelegateVotesChanged(src, oldWeight, newWeight);
            }

            if (dst != address(0)) {
                (uint256 oldWeight, uint256 newWeight) = _writeCheckpoint(_checkpoints[dst], _add, amount);
                emit DelegateVotesChanged(dst, oldWeight, newWeight);
            }
        }
    }

    function _writeCheckpoint(
        Checkpoint[] storage ckpts,
        function(uint256, uint256) view returns (uint256) op,
        uint256 delta
    ) private returns (uint256 oldWeight, uint256 newWeight) {
        uint256 pos = ckpts.length;

        unchecked {
            Checkpoint memory oldCkpt = pos == 0 ? Checkpoint(0, 0) : _unsafeAccess(ckpts, pos - 1);

            oldWeight = oldCkpt.votes;
            newWeight = op(oldWeight, delta);

            if (pos > 0 && oldCkpt.fromBlock == clock()) {
                _unsafeAccess(ckpts, pos - 1).votes = SafeCast.toUint224(newWeight);
            } else {
                ckpts.push(Checkpoint({fromBlock: SafeCast.toUint32(clock()), votes: SafeCast.toUint224(newWeight)}));
            }
        }
    }

    function _add(uint256 a, uint256 b) private pure returns (uint256) {
        return a + b;
    }

    function _subtract(uint256 a, uint256 b) private pure returns (uint256) {
        return a - b;
    }

    /**
     * @dev Access an element of the array without performing bounds check. The position is assumed to be within bounds.
     */
    function _unsafeAccess(Checkpoint[] storage ckpts, uint256 pos) private pure returns (Checkpoint storage result) {
        assembly {
            mstore(0, ckpts.slot)
            result.slot := add(keccak256(0, 0x20), pos)
        }
    }
}

// 
abstract contract ERC20Decimals is ERC20 {
    uint8 private immutable _decimals;

    constructor(uint8 decimals_) {
        _decimals = decimals_;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }
}

abstract contract ERC20VotesDecimals is ERC20Votes {
    uint8 private immutable _decimals;

    constructor(uint8 decimals_) {
        _decimals = decimals_;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function _afterTokenTransfer(
        address _from,
        address _to,
        uint256 _amount
    ) internal virtual override {
        ERC20Votes._afterTokenTransfer(_from, _to, _amount);
    }
}

// 
struct ShareHolder {
    uint256 shares;
    uint256 rewardDebt;
    uint256 claimed;
    uint256 pending;
}

struct TaxInfo {
    uint64 charity;
    uint64 autoLiquidity;
    uint64 marketing;
    uint64 reflection;
}

struct DividendConstructorArgs {
    address uniswapFactory;
    IUniswapV2Router02 uniswapV2Router;
    address mainLPToken;
    address charityAddress;
    address marketingAddress;
    address autoLiquidityAddress;
    TaxInfo transferTaxInfo;
    TaxInfo buyTaxInfo;
    TaxInfo sellTaxInfo;
    bool swapTaxes;
    uint256 minShareForRewards;
    bool sameTokenForRewards;
}

contract ERC20TempHolder {
    address immutable owner;

    constructor() {
        owner = msg.sender;
    }

    function transfer(address token, address to, uint256 amount) external {
        require(msg.sender == owner, "Only owner");
        IERC20(token).transfer(to, amount);
    }
}

abstract contract DividendsTokenCustom is ERC20Decimals, Ownable {
    /*  Universal distributor */
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint256 public minShareForRewards;

    uint256 public maxAutoProcessGas = 500000;

    bool public autoDistribute = true;

    uint256 public accPerShare; // Accumulated per share, times 1e36.
    uint256 public totalShares; // total number of shares
    uint256 public totalClaimed; // total amount claimed
    uint256 public totalRewardsDebt; // total amount claimed

    // use getShare-holderInfo function to get this data
    mapping(address => ShareHolder) shareHolders;
    address[] public allShareHolders;
    mapping(address => uint256) public indexOfShareHolders;

    uint256 private _lastProccessedIndex = 1;

    mapping(address => bool) public excludedFromRewards;

    // events
    event Claimed(address indexed claimer, uint256 indexed amount);
    event RewardsAdded(uint256 indexed amount);

    event ShareUpdated(
        address indexed shareHolder,
        uint256 indexed sharesAmount
    );
    event IncludedInRewards(address indexed shareHolder);
    event ExcludedFromRewards(address indexed shareHolder);

    address public immutable weth;
    address public immutable rewardToken;
    bool immutable isWethRewardToken;

    /** */

    uint256 public constant PERCENT_DENOMINATOR = 1000;
    uint256 public constant MAX_TAX = 200; // 20%

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    address public immutable wrappedNativeAddress;
    address public immutable mainLPToken;
    ERC20TempHolder public immutable tempHolder = new ERC20TempHolder();
    bool public immutable sameTokenForRewards;

    address public charityAddress;
    address public marketingAddress;
    address public autoLiquidityAddress;

    TaxInfo public transferTax;
    TaxInfo public buyTax;
    TaxInfo public sellTax;

    bool public isTaxActive;
    bool public swapAndLiquifyActive = true;

    uint256 public minTokensBeforeSwap; // 1 token

    mapping(address => bool) public isExcludedFromFee;

    mapping(address => bool) public isPair;

    uint256 public charityReserves;
    uint256 public marketingReserves;
    uint256 public autoLiquidityReserves;
    uint256 public reflectionReserves;

    bool public swapTaxes;

    function setSwapTaxes(bool _swapTaxes) external onlyOwner {
        swapTaxes = _swapTaxes;
    }

    constructor(DividendConstructorArgs memory _args) {
        minTokensBeforeSwap = (1 * 10) ** (decimals() - 1);
        uniswapV2Router = _args.uniswapV2Router;
        address __weth = _args.uniswapV2Router.WETH();
        wrappedNativeAddress = __weth;
        uniswapV2Pair = IUniswapV2Factory(_args.uniswapFactory).createPair(
            address(this),
            _args.mainLPToken
        );
        isPair[uniswapV2Pair] = true;

        mainLPToken = _args.mainLPToken;

        allShareHolders.push(address(0)); // use the index zero for address zero

        weth = __weth;
        rewardToken = _args.sameTokenForRewards
            ? address(this)
            : _args.mainLPToken;
        isWethRewardToken =
            !_args.sameTokenForRewards &&
            __weth == _args.mainLPToken;

        charityAddress = _args.charityAddress;
        marketingAddress = _args.marketingAddress;
        autoLiquidityAddress = _args.autoLiquidityAddress;

        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[charityAddress] = true;
        isExcludedFromFee[marketingAddress] = true;
        isExcludedFromFee[address(tempHolder)] = true;

        transferTax = _args.transferTaxInfo;
        buyTax = _args.buyTaxInfo;
        sellTax = _args.sellTaxInfo;

        excludedFromRewards[uniswapV2Pair] = true;
        excludedFromRewards[address(this)] = true;
        excludedFromRewards[address(_args.uniswapV2Router)] = true;
        excludedFromRewards[address(tempHolder)] = true;
        excludedFromRewards[owner()] = true;

        // approve max uint for router
        _approve(address(this), address(uniswapV2Router), type(uint256).max);
        // approve max int mainLPToken for router
        IERC20(_args.mainLPToken).approve(
            address(uniswapV2Router),
            type(uint256).max
        );

        swapTaxes = _args.swapTaxes;

        minShareForRewards = _args.minShareForRewards;
        sameTokenForRewards = _args.sameTokenForRewards;
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _customTransfer(_msgSender(), recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        require(
            amount <= allowance(sender, _msgSender()),
            "Transfer amount exceeds allowance"
        );

        _customTransfer(sender, recipient, amount);

        unchecked {
            _approve(
                sender,
                _msgSender(),
                allowance(sender, _msgSender()) - amount
            );
        }
        return true;
    }

    bool inSwap = false;

    function _customTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        if (from == address(this) || to == address(this)) {
            _transfer(from, to, amount);
            return;
        }
        // TODO: implement taxes
        if (from != uniswapV2Pair && swapAndLiquifyActive) {
            processReserves();
        }

        // if excluded then do normal transfer
        if (
            !isTaxActive ||
            inSwap ||
            isExcludedFromFee[from] ||
            isExcludedFromFee[to] ||
            amount == 0
        ) {
            _transfer(from, to, amount);
            return;
        }
        // if not excluded then apply tax
        TaxInfo memory taxInfo;
        uint64 totalTax;
        uint256 taxAmount;
        if (!isPair[from] && !isPair[to]) {
            taxInfo = transferTax;
        } else if (isPair[from] && !isPair[to]) {
            taxInfo = buyTax;
        } else {
            taxInfo = sellTax;
        }

        unchecked {
            totalTax =
                taxInfo.charity +
                taxInfo.autoLiquidity +
                taxInfo.marketing +
                taxInfo.reflection;
            taxAmount = (amount * totalTax) / PERCENT_DENOMINATOR;
        }
        if (taxAmount > 0) _transfer(from, address(this), taxAmount);
        _transfer(from, to, amount - taxAmount);

        // prevent div by zero
        if (totalTax == 0) return;

        unchecked {
            // charity
            charityReserves += (taxAmount * taxInfo.charity) / totalTax;
            // auto liquidity
            autoLiquidityReserves +=
                (taxAmount * taxInfo.autoLiquidity) /
                totalTax;
            // marketing
            marketingReserves += (taxAmount * taxInfo.marketing) / totalTax;
            // reflection
            reflectionReserves += (taxAmount * taxInfo.reflection) / totalTax;
        }
    }

    // setters
    function setCharityAddress(address _charityAddress) external onlyOwner {
        charityAddress = _charityAddress;
    }

    function setMarketingAddress(address _marketingAddress) external onlyOwner {
        marketingAddress = _marketingAddress;
    }

    function setAutoLiquidityAddress(
        address _autoLiquidityAddress
    ) external onlyOwner {
        autoLiquidityAddress = _autoLiquidityAddress;
    }

    function setTransferTax(
        uint64 _charity,
        uint64 _autoLiquidity,
        uint64 _marketing,
        uint64 _holdersRewards
    ) external onlyOwner {
        require(
            _charity + _autoLiquidity + _marketing + _holdersRewards <= MAX_TAX,
            "Transfer tax too high"
        );
        transferTax = TaxInfo(
            _charity,
            _autoLiquidity,
            _marketing,
            _holdersRewards
        );
    }

    function setBuyTax(
        uint64 _charity,
        uint64 _autoLiquidity,
        uint64 _marketing,
        uint64 _holdersRewards
    ) external onlyOwner {
        require(
            _charity + _autoLiquidity + _marketing + _holdersRewards <= MAX_TAX,
            "Buy tax too high"
        );
        buyTax = TaxInfo(_charity, _autoLiquidity, _marketing, _holdersRewards);
    }

    function setSellTax(
        uint64 _charity,
        uint64 _autoLiquidity,
        uint64 _marketing,
        uint64 _holdersRewards
    ) external onlyOwner {
        require(
            _charity + _autoLiquidity + _marketing + _holdersRewards <= MAX_TAX,
            "Sell tax too high"
        );
        sellTax = TaxInfo(
            _charity,
            _autoLiquidity,
            _marketing,
            _holdersRewards
        );
    }

    function setExcludedFromFee(
        address account,
        bool excluded
    ) external onlyOwner {
        isExcludedFromFee[account] = excluded;
    }

    function setPair(address pair, bool value) external onlyOwner {
        require(pair != uniswapV2Pair, "Cannot change pair");
        isPair[pair] = value;
    }

    function setTaxActive(bool value) external onlyOwner {
        isTaxActive = value;
    }

    function setSwapAndLiquifyActive(
        bool _swapAndLiquifyActive
    ) external onlyOwner {
        swapAndLiquifyActive = _swapAndLiquifyActive;
    }

    function setMinTokensBeforeSwap(
        uint256 _minTokensBeforeSwap
    ) external onlyOwner {
        minTokensBeforeSwap = _minTokensBeforeSwap;
    }

    function processReserves() public {
        if (inSwap) return;
        inSwap = true;

        if (
            charityReserves > minTokensBeforeSwap &&
            charityAddress != address(0)
        ) {
            if (swapTaxes) {
                if (mainLPToken == wrappedNativeAddress) {
                    swapForETH(charityReserves, charityAddress);
                } else {
                    swapForERC20(charityReserves, charityAddress);
                }
            } else _transfer(address(this), charityAddress, charityReserves);
            delete charityReserves;
        }

        if (marketingReserves > minTokensBeforeSwap) {
            if (swapTaxes) {
                if (mainLPToken == wrappedNativeAddress) {
                    swapForETH(marketingReserves, marketingAddress);
                } else {
                    swapForERC20(marketingReserves, marketingAddress);
                }
            } else
                _transfer(address(this), marketingAddress, marketingReserves);
            delete marketingReserves;
        }

        if (reflectionReserves > minTokensBeforeSwap && !sameTokenForRewards) {
            if (mainLPToken == wrappedNativeAddress) {
                uint256 initialBalance = address(this).balance;

                swapForETH(reflectionReserves, address(this));

                uint256 balanceAfter = address(this).balance - initialBalance;

                _updateGlobalShares(balanceAfter);
            } else {
                swapForERC20(reflectionReserves, address(tempHolder));

                uint256 balanceAfter = IERC20(mainLPToken).balanceOf(
                    address(tempHolder)
                );

                tempHolder.transfer(mainLPToken, address(this), balanceAfter);

                _updateGlobalShares(balanceAfter);
            }

            delete reflectionReserves;
        } else if (sameTokenForRewards) {
            _updateGlobalShares(reflectionReserves);
            delete reflectionReserves;
        }

        if (
            autoLiquidityReserves > minTokensBeforeSwap &&
            autoLiquidityAddress != address(0)
        ) {
            autoLiquidityReserves = swapAndLiquify(autoLiquidityReserves);
        }

        inSwap = false;
    }

    function swapAndLiquify(uint256 _amount) internal returns (uint256) {
        uint256 half = _amount / 2;
        uint256 otherHalf = _amount - half;
        uint256 leftOver;
        if (mainLPToken == wrappedNativeAddress) {
            uint256 initialBalance = address(this).balance;

            swapForETH(half, address(this));

            uint256 newBalance = address(this).balance - initialBalance;

            (uint amountToken, , ) = addLiquidityETH(otherHalf, newBalance);

            leftOver = otherHalf - amountToken;
        } else {
            swapForERC20(half, address(tempHolder));

            uint256 newBalance = IERC20(mainLPToken).balanceOf(
                address(tempHolder)
            );

            tempHolder.transfer(mainLPToken, address(this), newBalance);

            (uint amountToken, , ) = addLiquidityERC20(otherHalf, newBalance);

            leftOver = otherHalf - amountToken;
        }

        return leftOver;
    }

    function addLiquidityETH(
        uint256 _tokenAmount,
        uint256 _NativeAmount
    ) private returns (uint, uint, uint) {
        return
            uniswapV2Router.addLiquidityETH{value: _NativeAmount}(
                address(this),
                _tokenAmount,
                0,
                0,
                autoLiquidityAddress,
                block.timestamp
            );
    }

    function addLiquidityERC20(
        uint256 _tokenAmount,
        uint256 _counterPartAmount
    ) private returns (uint, uint, uint) {
        return
            uniswapV2Router.addLiquidity(
                address(this),
                mainLPToken,
                _tokenAmount,
                _counterPartAmount,
                0,
                0,
                autoLiquidityAddress,
                block.timestamp
            );
    }

    function swapForETH(uint256 _amount, address _to) internal {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = wrappedNativeAddress;
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            _amount,
            0,
            path,
            _to,
            block.timestamp
        );
    }

    function swapForERC20(uint256 _amount, address _to) internal {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = mainLPToken;
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            _amount,
            0,
            path,
            _to,
            block.timestamp
        );
    }

    receive() external payable {
        // only receive from router
        require(msg.sender == address(uniswapV2Router), "Invalid sender");
    }

    function _afterTokenTransfer(
        address from_,
        address to_,
        uint256
    ) internal virtual override {
        _updateShare(from_);
        _updateShare(to_);
        if (!inSwap) _massProcess();
    }

    function _massProcess() internal {
        if (autoDistribute)
            batchProcessClaims(
                gasleft() > maxAutoProcessGas
                    ? maxAutoProcessGas
                    : (gasleft() * 80) / 100
            );
    }

    function _updateShare(address wallet) internal {
        if (!excludedFromRewards[wallet])
            _setShare(
                wallet,
                balanceOf(wallet) > minShareForRewards ? balanceOf(wallet) : 0
            );
    }

    function includeInRewards(
        address shareHolderToBeIncluded_
    ) external onlyOwner {
        require(
            excludedFromRewards[shareHolderToBeIncluded_],
            "HODLRewardsDistributor: not excluded"
        );

        _updateUserShares(
            shareHolderToBeIncluded_,
            balanceOf(shareHolderToBeIncluded_)
        );
        excludedFromRewards[shareHolderToBeIncluded_] = false;
        emit IncludedInRewards(shareHolderToBeIncluded_);
    }

    function setProcessingGasLimit(uint256 maxAmount_) external onlyOwner {
        maxAutoProcessGas = maxAmount_;
    }

    function setMinSharePerRewards(
        uint256 _minSharePerRewards
    ) external onlyOwner {
        minShareForRewards = _minSharePerRewards;
    }

    function setAutoDistribute(bool _autoDistribute) external onlyOwner {
        autoDistribute = _autoDistribute;
    }

    /**
        retruns the pending rewards amount
        */
    function pending(
        address sharholderAddress_
    ) public view returns (uint256 pendingAmount) {
        ShareHolder storage user = shareHolders[sharholderAddress_];
        pendingAmount = user.shares.mul(accPerShare).div(1e36).sub(
            user.rewardDebt
        );
    }

    function totalPending() public view returns (uint256) {
        return accPerShare.mul(totalShares).div(1e36).sub(totalRewardsDebt);
    }

    /**
        returns information about the share holder
        */
    function shareHolderInfo(
        address shareHoldr_
    ) external view returns (ShareHolder memory) {
        ShareHolder storage user = shareHolders[shareHoldr_];
        return
            ShareHolder(
                user.shares, // How many tokens the user is holding.
                user.rewardDebt, // see @masterChef contract for more details
                user.claimed,
                pending(shareHoldr_)
            );
    }

    function holders() external view returns (uint256) {
        return allShareHolders.length;
    }

    function _setShare(address sharholderAddress_, uint256 amount_) internal {
        ShareHolder storage user = shareHolders[sharholderAddress_];

        // pay any pending rewards
        if (user.shares > 0) claimPending(sharholderAddress_);
        // update total shares
        _updateUserShares(sharholderAddress_, amount_);
    }

    /*
        excludes shareHolderToBeExcluded_ from participating in rewards
        Will distribute what ever pending rewards he has to all other shareholders
        This is needed in case a contract that can't claim needs to be excluded
        If owner wants the excluded to receive their pending rewards he can call claimPending on their behalf before calling this function
    */
    function excludeFromRewards(
        address shareHolderToBeExcluded_
    ) external onlyOwner {
        _excludeFromRewards(shareHolderToBeExcluded_);
    }

    function _excludeFromRewards(address shareHolderToBeExcluded_) internal {
        if (excludedFromRewards[shareHolderToBeExcluded_]) return;

        uint256 amountPending = pending(shareHolderToBeExcluded_);
        // update this user's shares to 0
        _updateUserShares(shareHolderToBeExcluded_, 0);
        // distribute his pending share to all shareholders
        if (amountPending > 0) _updateGlobalShares(amountPending);
        excludedFromRewards[shareHolderToBeExcluded_] = true;
        emit ExcludedFromRewards(shareHolderToBeExcluded_);
    }

    /** 
        @dev
        claim pending rewards for sharholderAddress_
        can be called by anyone but only sharholderAddress_
        can receive the reward
    */
    function claimPending(address sharholderAddress_) public {
        ShareHolder storage user = shareHolders[sharholderAddress_];

        uint256 pendingAmount = user.shares.mul(accPerShare).div(1e36).sub(
            user.rewardDebt
        );

        if (pendingAmount <= 0) return;

        totalRewardsDebt = totalRewardsDebt.sub(user.rewardDebt);
        user.rewardDebt = user.shares.mul(accPerShare).div(1e36);
        totalRewardsDebt = totalRewardsDebt.add(user.rewardDebt);

        bool sent;

        if (isWethRewardToken) {
            (sent, ) = payable(sharholderAddress_).call{value: pendingAmount}(
                ""
            );
            //if !sent means probably the receiver is a non payable address
            if (!sent) {
                // add pending amount to global shares to prevent loss of ETH
                _updateGlobalShares(pendingAmount);
            }
        } else {
            IERC20(rewardToken).transfer(sharholderAddress_, pendingAmount);
            sent = true;
        }

        if (sent) {
            emit Claimed(sharholderAddress_, pendingAmount);
            user.claimed = user.claimed.add(pendingAmount);
            totalClaimed = totalClaimed.add(pendingAmount);
        }
    }

    bool private _inBatchProcess;

    function batchProcessClaims(uint256 gas) public {
        if (gasleft() < gas || _inBatchProcess) return;
        _inBatchProcess = true;
        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();
        uint256 iterations = 1; // index 0 is ocupied by address(0)
        uint256 index = _lastProccessedIndex;
        // we
        while (gasUsed < gas && iterations < allShareHolders.length) {
            claimPending(allShareHolders[index]);
            gasUsed = gasUsed.add(gasLeft.sub(gasleft()));
            gasLeft = gasleft();
            index = _incrementLastProccessed(index);
            iterations++;
        }

        _lastProccessedIndex = index;

        _inBatchProcess = false;
    }

    /**
        updates the accumulatedPerShare amount based on the new amount and total shares
        */
    uint256 reserved = 0;

    function _updateGlobalShares(uint256 amount_) internal {
        if (totalShares == 0) {
            reserved = reserved.add(amount_);
            return;
        }
        accPerShare = accPerShare.add(amount_.mul(1e36).div(totalShares)).add(
            reserved.mul(1e36).div(totalShares)
        );
        reserved = 0;
        emit RewardsAdded(amount_);
    }

    function updateGlobalShares(uint256 amount_) external onlyOwner {
        _updateGlobalShares(amount_);
    }

    /**
        updates a user share
    */
    function _updateUserShares(
        address sharholderAddress_,
        uint256 newAmount_
    ) internal {
        ShareHolder storage user = shareHolders[sharholderAddress_];
        totalShares = totalShares.sub(user.shares).add(newAmount_);
        totalRewardsDebt = totalRewardsDebt.sub(user.rewardDebt);
        user.shares = newAmount_;
        user.rewardDebt = user.shares.mul(accPerShare).div(1e36);
        totalRewardsDebt = totalRewardsDebt.add(user.rewardDebt);
        if (user.shares > 0 && indexOfShareHolders[sharholderAddress_] == 0) {
            // add this shareHolder to array
            allShareHolders.push(sharholderAddress_);

            indexOfShareHolders[sharholderAddress_] =
                allShareHolders.length -
                1;
        } else if (
            user.shares == 0 && indexOfShareHolders[sharholderAddress_] != 0
        ) {
            // remove this share holder from array
            uint256 index = indexOfShareHolders[sharholderAddress_];

            allShareHolders[index] = allShareHolders[
                allShareHolders.length - 1
            ];

            indexOfShareHolders[
                allShareHolders[allShareHolders.length - 1]
            ] = index;

            allShareHolders.pop();

            indexOfShareHolders[sharholderAddress_] = 0;
        }
        emit ShareUpdated(sharholderAddress_, newAmount_);
    }

    function _incrementLastProccessed(
        uint256 _index
    ) internal view returns (uint256) {
        _index++;
        if (_index >= allShareHolders.length) _index = 1;

        return _index;
    }
}

abstract contract DividendsTokenCustomVotes is ERC20VotesDecimals, Ownable {
    /*  Universal distributor */
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint256 public minShareForRewards;

    uint256 public maxAutoProcessGas = 500000;

    bool public autoDistribute = true;

    uint256 public accPerShare; // Accumulated per share, times 1e36.
    uint256 public totalShares; // total number of shares
    uint256 public totalClaimed; // total amount claimed
    uint256 public totalRewardsDebt; // total amount claimed

    // use getShare-holderInfo function to get this data
    mapping(address => ShareHolder) shareHolders;
    address[] public allShareHolders;
    mapping(address => uint256) public indexOfShareHolders;

    uint256 private _lastProccessedIndex = 1;

    mapping(address => bool) public excludedFromRewards;

    // events
    event Claimed(address indexed claimer, uint256 indexed amount);
    event RewardsAdded(uint256 indexed amount);

    event ShareUpdated(
        address indexed shareHolder,
        uint256 indexed sharesAmount
    );
    event IncludedInRewards(address indexed shareHolder);
    event ExcludedFromRewards(address indexed shareHolder);

    address public immutable weth;
    address public immutable rewardToken;
    bool immutable isWethRewardToken;

    /** */

    uint256 public constant PERCENT_DENOMINATOR = 1000;
    uint256 public constant MAX_TAX = 200; // 20%

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    address public immutable wrappedNativeAddress;
    address public immutable mainLPToken;
    ERC20TempHolder public immutable tempHolder = new ERC20TempHolder();
    bool public immutable sameTokenForRewards;

    address public charityAddress;
    address public marketingAddress;
    address public autoLiquidityAddress;

    TaxInfo public transferTax;
    TaxInfo public buyTax;
    TaxInfo public sellTax;

    bool public isTaxActive;
    bool public swapAndLiquifyActive = true;

    uint256 public minTokensBeforeSwap; // 1 token

    mapping(address => bool) public isExcludedFromFee;

    mapping(address => bool) public isPair;

    uint256 public charityReserves;
    uint256 public marketingReserves;
    uint256 public autoLiquidityReserves;
    uint256 public reflectionReserves;

    bool public swapTaxes;

    function setSwapTaxes(bool _swapTaxes) external onlyOwner {
        swapTaxes = _swapTaxes;
    }

    constructor(DividendConstructorArgs memory _args) {
        minTokensBeforeSwap = (1 * 10) ** (decimals() - 1);
        uniswapV2Router = _args.uniswapV2Router;
        address __weth = _args.uniswapV2Router.WETH();
        wrappedNativeAddress = __weth;
        uniswapV2Pair = IUniswapV2Factory(_args.uniswapFactory).createPair(
            address(this),
            _args.mainLPToken
        );
        isPair[uniswapV2Pair] = true;

        mainLPToken = _args.mainLPToken;

        allShareHolders.push(address(0)); // use the index zero for address zero

        weth = __weth;
        rewardToken = _args.sameTokenForRewards
            ? address(this)
            : _args.mainLPToken;
        isWethRewardToken =
            !_args.sameTokenForRewards &&
            __weth == _args.mainLPToken;

        charityAddress = _args.charityAddress;
        marketingAddress = _args.marketingAddress;
        autoLiquidityAddress = _args.autoLiquidityAddress;

        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[charityAddress] = true;
        isExcludedFromFee[marketingAddress] = true;
        isExcludedFromFee[address(tempHolder)] = true;

        transferTax = _args.transferTaxInfo;
        buyTax = _args.buyTaxInfo;
        sellTax = _args.sellTaxInfo;

        excludedFromRewards[uniswapV2Pair] = true;
        excludedFromRewards[address(this)] = true;
        excludedFromRewards[address(_args.uniswapV2Router)] = true;
        excludedFromRewards[address(tempHolder)] = true;
        excludedFromRewards[owner()] = true;

        // approve max uint for router
        _approve(address(this), address(uniswapV2Router), type(uint256).max);
        // approve max int mainLPToken for router
        IERC20(_args.mainLPToken).approve(
            address(uniswapV2Router),
            type(uint256).max
        );

        swapTaxes = _args.swapTaxes;

        minShareForRewards = _args.minShareForRewards;
        sameTokenForRewards = _args.sameTokenForRewards;
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _customTransfer(_msgSender(), recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        require(
            amount <= allowance(sender, _msgSender()),
            "Transfer amount exceeds allowance"
        );

        _customTransfer(sender, recipient, amount);

        unchecked {
            _approve(
                sender,
                _msgSender(),
                allowance(sender, _msgSender()) - amount
            );
        }
        return true;
    }

    bool inSwap = false;

    function _customTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        if (from == address(this) || to == address(this)) {
            _transfer(from, to, amount);
            return;
        }
        // TODO: implement taxes
        if (from != uniswapV2Pair && swapAndLiquifyActive) {
            processReserves();
        }

        // if excluded then do normal transfer
        if (
            !isTaxActive ||
            inSwap ||
            isExcludedFromFee[from] ||
            isExcludedFromFee[to] ||
            amount == 0
        ) {
            _transfer(from, to, amount);
            return;
        }
        // if not excluded then apply tax
        TaxInfo memory taxInfo;
        uint64 totalTax;
        uint256 taxAmount;
        if (!isPair[from] && !isPair[to]) {
            taxInfo = transferTax;
        } else if (isPair[from] && !isPair[to]) {
            taxInfo = buyTax;
        } else {
            taxInfo = sellTax;
        }

        unchecked {
            totalTax =
                taxInfo.charity +
                taxInfo.autoLiquidity +
                taxInfo.marketing +
                taxInfo.reflection;
            taxAmount = (amount * totalTax) / PERCENT_DENOMINATOR;
        }

        if (taxAmount > 0) _transfer(from, address(this), taxAmount);
        _transfer(from, to, amount - taxAmount);

        // prevent div by zero
        if (totalTax == 0) return;

        unchecked {
            // charity
            charityReserves += (taxAmount * taxInfo.charity) / totalTax;
            // auto liquidity
            autoLiquidityReserves +=
                (taxAmount * taxInfo.autoLiquidity) /
                totalTax;
            // marketing
            marketingReserves += (taxAmount * taxInfo.marketing) / totalTax;
            // reflection
            reflectionReserves += (taxAmount * taxInfo.reflection) / totalTax;
        }
    }

    // setters
    function setCharityAddress(address _charityAddress) external onlyOwner {
        charityAddress = _charityAddress;
    }

    function setMarketingAddress(address _marketingAddress) external onlyOwner {
        marketingAddress = _marketingAddress;
    }

    function setAutoLiquidityAddress(
        address _autoLiquidityAddress
    ) external onlyOwner {
        autoLiquidityAddress = _autoLiquidityAddress;
    }

    function setTransferTax(
        uint64 _charity,
        uint64 _autoLiquidity,
        uint64 _marketing,
        uint64 _holdersRewards
    ) external onlyOwner {
        require(
            _charity + _autoLiquidity + _marketing + _holdersRewards <= MAX_TAX,
            "Transfer tax too high"
        );
        transferTax = TaxInfo(
            _charity,
            _autoLiquidity,
            _marketing,
            _holdersRewards
        );
    }

    function setBuyTax(
        uint64 _charity,
        uint64 _autoLiquidity,
        uint64 _marketing,
        uint64 _holdersRewards
    ) external onlyOwner {
        require(
            _charity + _autoLiquidity + _marketing + _holdersRewards <= MAX_TAX,
            "Buy tax too high"
        );
        buyTax = TaxInfo(_charity, _autoLiquidity, _marketing, _holdersRewards);
    }

    function setSellTax(
        uint64 _charity,
        uint64 _autoLiquidity,
        uint64 _marketing,
        uint64 _holdersRewards
    ) external onlyOwner {
        require(
            _charity + _autoLiquidity + _marketing + _holdersRewards <= MAX_TAX,
            "Sell tax too high"
        );
        sellTax = TaxInfo(
            _charity,
            _autoLiquidity,
            _marketing,
            _holdersRewards
        );
    }

    function setExcludedFromFee(
        address account,
        bool excluded
    ) external onlyOwner {
        isExcludedFromFee[account] = excluded;
    }

    function setPair(address pair, bool value) external onlyOwner {
        require(pair != uniswapV2Pair, "Cannot change pair");
        isPair[pair] = value;
    }

    function setTaxActive(bool value) external onlyOwner {
        isTaxActive = value;
    }

    function setSwapAndLiquifyActive(
        bool _swapAndLiquifyActive
    ) external onlyOwner {
        swapAndLiquifyActive = _swapAndLiquifyActive;
    }

    function setMinTokensBeforeSwap(
        uint256 _minTokensBeforeSwap
    ) external onlyOwner {
        minTokensBeforeSwap = _minTokensBeforeSwap;
    }

    function processReserves() public {
        if (inSwap) return;
        inSwap = true;

        if (
            charityReserves > minTokensBeforeSwap &&
            charityAddress != address(0)
        ) {
            if (swapTaxes) {
                if (mainLPToken == wrappedNativeAddress) {
                    swapForETH(charityReserves, charityAddress);
                } else {
                    swapForERC20(charityReserves, charityAddress);
                }
            } else _transfer(address(this), charityAddress, charityReserves);
            delete charityReserves;
        }

        if (marketingReserves > minTokensBeforeSwap) {
            if (swapTaxes) {
                if (mainLPToken == wrappedNativeAddress) {
                    swapForETH(marketingReserves, marketingAddress);
                } else {
                    swapForERC20(marketingReserves, marketingAddress);
                }
            } else
                _transfer(address(this), marketingAddress, marketingReserves);
            delete marketingReserves;
        }

        if (reflectionReserves > minTokensBeforeSwap && !sameTokenForRewards) {
            if (mainLPToken == wrappedNativeAddress) {
                uint256 initialBalance = address(this).balance;

                swapForETH(reflectionReserves, address(this));

                uint256 balanceAfter = address(this).balance - initialBalance;

                _updateGlobalShares(balanceAfter);
            } else {
                swapForERC20(reflectionReserves, address(tempHolder));

                uint256 balanceAfter = IERC20(mainLPToken).balanceOf(
                    address(tempHolder)
                );

                tempHolder.transfer(mainLPToken, address(this), balanceAfter);

                _updateGlobalShares(balanceAfter);
            }

            delete reflectionReserves;
        } else if (sameTokenForRewards) {
            _updateGlobalShares(reflectionReserves);
            delete reflectionReserves;
        }

        if (
            autoLiquidityReserves / 2 > minTokensBeforeSwap &&
            autoLiquidityAddress != address(0)
        ) {
            autoLiquidityReserves = swapAndLiquify(autoLiquidityReserves);
        }

        inSwap = false;
    }

    function swapAndLiquify(uint256 _amount) internal returns (uint256) {
        uint256 half = _amount / 2;
        uint256 otherHalf = _amount - half;
        uint256 leftOver;
        if (mainLPToken == wrappedNativeAddress) {
            uint256 initialBalance = address(this).balance;

            swapForETH(half, address(this));

            uint256 newBalance = address(this).balance - initialBalance;

            (uint amountToken, , ) = addLiquidityETH(otherHalf, newBalance);

            leftOver = otherHalf - amountToken;
        } else {
            swapForERC20(half, address(tempHolder));

            uint256 newBalance = IERC20(mainLPToken).balanceOf(
                address(tempHolder)
            );

            tempHolder.transfer(mainLPToken, address(this), newBalance);

            (uint amountToken, , ) = addLiquidityERC20(otherHalf, newBalance);

            leftOver = otherHalf - amountToken;
        }

        return leftOver;
    }

    function addLiquidityETH(
        uint256 _tokenAmount,
        uint256 _NativeAmount
    ) private returns (uint, uint, uint) {
        return
            uniswapV2Router.addLiquidityETH{value: _NativeAmount}(
                address(this),
                _tokenAmount,
                0,
                0,
                autoLiquidityAddress,
                block.timestamp
            );
    }

    function addLiquidityERC20(
        uint256 _tokenAmount,
        uint256 _counterPartAmount
    ) private returns (uint, uint, uint) {
        return
            uniswapV2Router.addLiquidity(
                address(this),
                mainLPToken,
                _tokenAmount,
                _counterPartAmount,
                0,
                0,
                autoLiquidityAddress,
                block.timestamp
            );
    }

    function swapForETH(uint256 _amount, address _to) internal {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = wrappedNativeAddress;

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            _amount,
            0,
            path,
            _to,
            block.timestamp
        );
    }

    function swapForERC20(uint256 _amount, address _to) internal {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = mainLPToken;

        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            _amount,
            0,
            path,
            _to,
            block.timestamp
        );
    }

    receive() external payable {
        // only receive from router
        require(msg.sender == address(uniswapV2Router), "Invalid sender");
    }

    function _afterTokenTransfer(
        address from_,
        address to_,
        uint256
    ) internal virtual override {
        _updateShare(from_);
        _updateShare(to_);
        if (!inSwap) _massProcess();
    }

    function _massProcess() internal {
        if (autoDistribute)
            batchProcessClaims(
                gasleft() > maxAutoProcessGas
                    ? maxAutoProcessGas
                    : (gasleft() * 80) / 100
            );
    }

    function _updateShare(address wallet) internal {
        if (!excludedFromRewards[wallet])
            _setShare(
                wallet,
                balanceOf(wallet) > minShareForRewards ? balanceOf(wallet) : 0
            );
    }

    function includeInRewards(
        address shareHolderToBeIncluded_
    ) external onlyOwner {
        require(
            excludedFromRewards[shareHolderToBeIncluded_],
            "HODLRewardsDistributor: not excluded"
        );

        _updateUserShares(
            shareHolderToBeIncluded_,
            balanceOf(shareHolderToBeIncluded_)
        );
        excludedFromRewards[shareHolderToBeIncluded_] = false;
        emit IncludedInRewards(shareHolderToBeIncluded_);
    }

    function setProcessingGasLimit(uint256 maxAmount_) external onlyOwner {
        maxAutoProcessGas = maxAmount_;
    }

    function setMinSharePerRewards(
        uint256 _minSharePerRewards
    ) external onlyOwner {
        minShareForRewards = _minSharePerRewards;
    }

    function setAutoDistribute(bool _autoDistribute) external onlyOwner {
        autoDistribute = _autoDistribute;
    }

    /**
        retruns the pending rewards amount
        */
    function pending(
        address sharholderAddress_
    ) public view returns (uint256 pendingAmount) {
        ShareHolder storage user = shareHolders[sharholderAddress_];
        pendingAmount = user.shares.mul(accPerShare).div(1e36).sub(
            user.rewardDebt
        );
    }

    function totalPending() public view returns (uint256) {
        return accPerShare.mul(totalShares).div(1e36).sub(totalRewardsDebt);
    }

    /**
        returns information about the share holder
        */
    function shareHolderInfo(
        address shareHoldr_
    ) external view returns (ShareHolder memory) {
        ShareHolder storage user = shareHolders[shareHoldr_];
        return
            ShareHolder(
                user.shares, // How many tokens the user is holding.
                user.rewardDebt, // see @masterChef contract for more details
                user.claimed,
                pending(shareHoldr_)
            );
    }

    function holders() external view returns (uint256) {
        return allShareHolders.length;
    }

    function _setShare(address sharholderAddress_, uint256 amount_) internal {
        ShareHolder storage user = shareHolders[sharholderAddress_];

        // pay any pending rewards
        if (user.shares > 0) claimPending(sharholderAddress_);
        // update total shares
        _updateUserShares(sharholderAddress_, amount_);
    }

    /*
        excludes shareHolderToBeExcluded_ from participating in rewards
        Will distribute what ever pending rewards he has to all other shareholders
        This is needed in case a contract that can't claim needs to be excluded
        If owner wants the excluded to receive their pending rewards he can call claimPending on their behalf before calling this function
    */
    function excludeFromRewards(
        address shareHolderToBeExcluded_
    ) external onlyOwner {
        _excludeFromRewards(shareHolderToBeExcluded_);
    }

    function _excludeFromRewards(address shareHolderToBeExcluded_) internal {
        if (excludedFromRewards[shareHolderToBeExcluded_]) return;

        uint256 amountPending = pending(shareHolderToBeExcluded_);
        // update this user's shares to 0
        _updateUserShares(shareHolderToBeExcluded_, 0);
        // distribute his pending share to all shareholders
        if (amountPending > 0) _updateGlobalShares(amountPending);
        excludedFromRewards[shareHolderToBeExcluded_] = true;
        emit ExcludedFromRewards(shareHolderToBeExcluded_);
    }

    /** 
        @dev
        claim pending rewards for sharholderAddress_
        can be called by anyone but only sharholderAddress_
        can receive the reward
    */
    function claimPending(address sharholderAddress_) public {
        ShareHolder storage user = shareHolders[sharholderAddress_];

        uint256 pendingAmount = user.shares.mul(accPerShare).div(1e36).sub(
            user.rewardDebt
        );

        if (pendingAmount <= 0) return;

        totalRewardsDebt = totalRewardsDebt.sub(user.rewardDebt);
        user.rewardDebt = user.shares.mul(accPerShare).div(1e36);
        totalRewardsDebt = totalRewardsDebt.add(user.rewardDebt);

        bool sent;

        if (isWethRewardToken) {
            (sent, ) = payable(sharholderAddress_).call{value: pendingAmount}(
                ""
            );
            //if !sent means probably the receiver is a non payable address
            if (!sent) {
                // add pending amount to global shares to prevent loss of ETH
                _updateGlobalShares(pendingAmount);
            }
        } else {
            IERC20(rewardToken).transfer(sharholderAddress_, pendingAmount);
            sent = true;
        }

        if (sent) {
            emit Claimed(sharholderAddress_, pendingAmount);
            user.claimed = user.claimed.add(pendingAmount);
            totalClaimed = totalClaimed.add(pendingAmount);
        }
    }

    bool private _inBatchProcess;

    function batchProcessClaims(uint256 gas) public {
        if (gasleft() < gas || _inBatchProcess) return;
        _inBatchProcess = true;
        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();
        uint256 iterations = 1; // index 0 is ocupied by address(0)
        uint256 index = _lastProccessedIndex;
        // we
        while (gasUsed < gas && iterations < allShareHolders.length) {
            claimPending(allShareHolders[index]);
            gasUsed = gasUsed.add(gasLeft.sub(gasleft()));
            gasLeft = gasleft();
            index = _incrementLastProccessed(index);
            iterations++;
        }

        _lastProccessedIndex = index;

        _inBatchProcess = false;
    }

    /**
        updates the accumulatedPerShare amount based on the new amount and total shares
        */
    uint256 reserved = 0;

    function _updateGlobalShares(uint256 amount_) internal {
        if (totalShares == 0) {
            reserved = reserved.add(amount_);
            return;
        }
        accPerShare = accPerShare.add(amount_.mul(1e36).div(totalShares)).add(
            reserved.mul(1e36).div(totalShares)
        );
        reserved = 0;
        emit RewardsAdded(amount_);
    }

    function updateGlobalShares(uint256 amount_) external onlyOwner {
        _updateGlobalShares(amount_);
    }

    /**
        updates a user share
    */
    function _updateUserShares(
        address sharholderAddress_,
        uint256 newAmount_
    ) internal {
        ShareHolder storage user = shareHolders[sharholderAddress_];
        totalShares = totalShares.sub(user.shares).add(newAmount_);
        totalRewardsDebt = totalRewardsDebt.sub(user.rewardDebt);
        user.shares = newAmount_;
        user.rewardDebt = user.shares.mul(accPerShare).div(1e36);
        totalRewardsDebt = totalRewardsDebt.add(user.rewardDebt);
        if (user.shares > 0 && indexOfShareHolders[sharholderAddress_] == 0) {
            // add this shareHolder to array
            allShareHolders.push(sharholderAddress_);

            indexOfShareHolders[sharholderAddress_] =
                allShareHolders.length -
                1;
        } else if (
            user.shares == 0 && indexOfShareHolders[sharholderAddress_] != 0
        ) {
            // remove this share holder from array
            uint256 index = indexOfShareHolders[sharholderAddress_];

            allShareHolders[index] = allShareHolders[
                allShareHolders.length - 1
            ];

            indexOfShareHolders[
                allShareHolders[allShareHolders.length - 1]
            ] = index;

            allShareHolders.pop();

            indexOfShareHolders[sharholderAddress_] = 0;
        }
        emit ShareUpdated(sharholderAddress_, newAmount_);
    }

    function _incrementLastProccessed(
        uint256 _index
    ) internal view returns (uint256) {
        _index++;
        if (_index >= allShareHolders.length) _index = 1;

        return _index;
    }
}

