contract DividendsTokenCustomSimple is DividendsTokenCustom {
    constructor(
        uint256 _initialSupply,
        uint8 _decimals,
        string memory _name,
        string memory _symbol,
        address _owner,
        DividendConstructorArgs memory _args
    )
        ERC20(_name, _symbol)
        ERC20Decimals(_decimals)
        Ownable(_owner)
        DividendsTokenCustom(_args)
    {
        _mint(_owner, _initialSupply);
    }
}
