pragma solidity ^0.8.0;

contract TokenLockup {

    using SafeMath for uint256;
    // NO DECIMAL NUMBERS IN ANY INPUT (Not Supported by solidity directly)
    address public owner;
    
    address public constant token = 0x68E374F856bF25468D365E539b700b648Bf94B67; // Using DAI from testnet, replace this with real token address CHANGE THIS
    
    uint256 public constant tokenDecimal = 18; // Token Decimals CHANGE THIS
    
    uint256 private constant decimalFactor = 10 ** uint256(tokenDecimal);
    
    uint256 public constant distributionAmount = 1250000; // Amount of tokens to be distributed to each person per Distribution CHANGE THIS
    
    uint256 public constant initialDelay = 2 * 30 days; // Total days before the unlocking starts, edit to change number of months CHANGE FIRST NUMBER ONLY
    // Example 1 * 30 days = 1 month, 2 * 30 days = 2 months etc.
    
    uint256 public nextUnlock = block.timestamp + initialDelay;
    
    uint256 public constant interval = 1 * 30 days;  // Delays between each unlock CHANGE FIRST NUMBER ONLY
    // Example: Similar to initialDelay
    
    uint256 public constant cycles = 12 ; // Numbers of months the distribution needs to run for CHANGE THIS
    
    uint256 public unlockEnd = block.timestamp + initialDelay + (cycles * interval);
    
    uint256 public constant scaledAmount = distributionAmount * decimalFactor;
    
    IERC20 _token = IERC20(token);
    
    address[] public whiteList = [
        0x9AFFe73C2716A4F5F424909750c9Dc21a4D9Da07,
        0x32Ba9D95323f661CB61867143cCC042aFC98bB06,
        0xf9938e84c53Dcdc4Bcd6479C8f6c6C94119e3EAc,
        0xb6A6319b5712DD535FA506d6CC9F8e45a5D9F792,
        0x3B0498Ef73db974d53Cfd8168Aa47e002b24b8d2,
        0x85d485Be26D881F3a7f0cCAeCBe2cE79F92b3f49
        ]; // CHANGE THIS to proper whiteList

    constructor() {
        owner = 0x20bA5eD762E521d5745bC69EbAe92cE5209daC97; // CHANGE THIS to whoever is expected to recieve leftover tokens (This is probably the main dev/token minter's wallet)
    }

    function distribute() public // This can be called by anyone and will distribute the tokens to everyone on the whitelist, as long as Distribution is possible
    {
        require(block.timestamp > nextUnlock, "No tokens to distribute yet");
        require(unlockEnd > nextUnlock, "Distribution Completed"); 
        nextUnlock = nextUnlock + interval;
        for (uint256 i = 0; i < whiteList.length; i++) {
            _token.transfer(whiteList[i], scaledAmount);
        }
    }
    
    function recoverLeftover() public // Only callable once distribute has been called X amount of times (sucessfully), where X is the number of cycles above
    {
        require(unlockEnd <= nextUnlock, "Distribution not completed");
        uint256 leftOverBalance = _token.balanceOf(address(this));
        _token.transfer(owner, leftOverBalance);
    }
}
