    "/Users/bioharz/git/jupiter-and-planning/goodtoken-ok/contracts/GoodToken.sol": {
      "content": "pragma solidity 0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/GSN/Context.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@pancakeswap2/pancake-swap-core/contracts/interfaces/IPancakeFactory.sol";
import "@pancakeswap2/pancake-swap-core/contracts/interfaces/IPancakePair.sol";
import "pancakeswap-peripheral/contracts/interfaces/IPancakeRouter01.sol";
import "pancakeswap-peripheral/contracts/interfaces/IPancakeRouter02.sol";
import "./Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";


contract GoodToken is Context, IERC20, Ownable, AccessControl {
    using SafeMath for uint256;
    using Address for address;

    /**
    * Role-based authorization to be able to rennounce ownership and
    * still claim the charity rewards.
    */
    bytes32 public constant CHARITY_ROLE = keccak256("CHARITY_ROLE");

    address public immutable WETH;

    IPancakeRouter02 public immutable pancakeswapV2Router;
    address public immutable pancakeswapV2Pair;

    /**
     * Max uint8 constant to avoid throwing errors.
     * Used to determine if all Sets have been cleared.
     */
    uint8 private constant MAX_INT8 = uint8(-1);

    address[] private _excluded;

    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal = 1000000000 * 10**6 * 10**9;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;


    string private _name;
    string private _symbol;
    uint8 private _decimals = 9;

    /**
    * Bool to lock recursive calls to avoid interfering calls.
    */
    bool inSwapAndLiquify;

    /**
    * Enable/Disable liquidity swap functionality.
    */
    bool public swapAndLiquifyEnabled = true;

    /**
    * Enable/Disable  swap functionality.
    */
    bool public SwapForCharityEnabled = true;

    uint256 public _charityFee = 3;
    uint256 private _previousCharityFee = _charityFee;

    uint256 public _taxFee = 3;
    uint256 private _previousTaxFee = _taxFee;

    uint256 public _liquidityFee = 3;
    uint256 private _previousLiquidityFee = _liquidityFee;

    /**
    * Token sum for adding to the LP pool
    */
    uint256 public _liquidityBalance = 0;

    /**
    * Charity sum to claim.
    * in token
    */
    uint256 public _charityBalance = 0;

    uint256 public _maxTxAmount = 1000000 * 10**6 * 10**9;
    uint256 private numTokensSellToAddToLiquidity = 500000 * 10**6 * 10**9;
    uint256 private numTokensSellForCharity = 500000 * 10**6 * 10**9;

    mapping (address => bool) private donatingAddresses;

    bool public _isClaimable = true;
    uint256 private donatingCharityFee = 2;
    uint256 private donatingTaxFee = 5;
    uint256 private donatingLPFee = 92;

    mapping(address => uint256) private _rOwned;
    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) private _isExcluded;

    event InternalWarning(string message);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    event SwapForCharity(uint256 amount);

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    // Structs sometimes needed as solidity only allows a certain number of function/return arguments.
    struct RValuesStruct {
        uint256 tAmount;
        uint256 tFee;
        uint256 tLiquidity;
        uint256 tCharity;
        uint256 currentRate;
    }
    struct ValuesStruct {
        uint256 rAmount;
        uint256 rTransferAmount;
        uint256 rFee;
        uint256 rCharity;
        uint256 tTransferAmount;
        uint256 tFee;
        uint256 tLiquidity;
        uint256 tCharity;
    }

    constructor(
        string memory name,
        string memory symbol,
        address routerAddress
    )
        public
    {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(CHARITY_ROLE, msg.sender);

        IPancakeRouter02 _pancakeswapV2Router = IPancakeRouter02(routerAddress);
        WETH = _pancakeswapV2Router.WETH();

        _name = name;
        _symbol = symbol;

        _rOwned[_msgSender()] = _rTotal;

        // Create a Pancake pair for this new token
        pancakeswapV2Pair = IPancakeFactory(_pancakeswapV2Router.factory())
            .createPair(address(this), _pancakeswapV2Router.WETH());

        // set the rest of the contract variables
        pancakeswapV2Router = _pancakeswapV2Router;

        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;

        emit Transfer(address(0), _msgSender(), _tTotal);
    }

    function claimTokensForCharity() external {
        require(hasRole(CHARITY_ROLE, msg.sender), "Caller is not a charity claimer.");
        (payable (msg.sender)).transfer(address(this).balance);
    }

    /**
     * @dev Remember that only owner can call so be careful when use on contracts generated from other contracts.
     * @param tokenAddress The token contract address
     * @param tokenAmount Number of tokens to be sent
     source: https://github.com/vittominacori/eth-token-recover/blob/master/contracts/TokenRecover.sol
     */
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner() {
        IERC20(tokenAddress).transfer(owner(), tokenAmount);
    }

    function recoverGas() external onlyOwner() {
        (payable (owner())).transfer(address(this).balance);
    }

    /**
    * Evaluates whether address is a contract and exists.
    */
    function isContract(address addr) view private returns (bool) {
        uint size;
        assembly { size := extcodesize(addr) }
        return size > 0;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount /*, "ERC20: transfer amount exceeds allowance"*/
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue /*, "ERC20: decreased allowance below zero"*/
            )
        );
        return true;
    }

    function isExcludedFromReward(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function deliver(uint256 tAmount) public {
        address sender = _msgSender();
        require(
            !_isExcluded[sender],
            "Excluded addresses cannot call this function"
        );
        uint256 rAmount = _getValues(tAmount).rAmount;

        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rTotal = _rTotal.sub(rAmount);
        _tFeeTotal = _tFeeTotal.add(tAmount);
    }

    function reflectionFromToken(uint256 tAmount, bool deductTransferFee)
        public
        view
        returns (uint256)
    {
        require(tAmount <= _tTotal, "Amount must be less than supply");
        if (!deductTransferFee) {
            return _getValues(tAmount).rAmount;
        } else {
            return _getValues(tAmount).rTransferAmount;
        }
    }

    function tokenFromReflection(uint256 rAmount)
        public
        view
        returns (uint256)
    {
        require(
            rAmount <= _rTotal,
            "Amount must be less than total reflections"
        );
        uint256 currentRate = _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromReward(address account) public onlyOwner() {
        // require(account != 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D, 'We can not exclude Pancake router.');
        require(!_isExcluded[account], "Account is already excluded");
        if (_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeInReward(address account) external onlyOwner() {
        require(_isExcluded[account], "Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function _transferBothExcluded(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        ValuesStruct memory valuesStruct = _getValues(tAmount);
        uint256 rAmount = valuesStruct.rAmount;
        uint256 rTransferAmount = valuesStruct.rTransferAmount;
        uint256 rFee = valuesStruct.rFee;
        uint256 tTransferAmount = valuesStruct.tTransferAmount;
        uint256 tFee = valuesStruct.tFee;
        uint256 tLiquidity = valuesStruct.tLiquidity;
        uint256 tCharity = valuesStruct.tCharity;


        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        _reflectFee(rFee, tFee);
        _takeForCharity(tCharity);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function setTaxFeePercent(uint256 taxFee) external onlyOwner() {
        _taxFee = taxFee;
    }

    function setIsClaimable(bool isClaimable) external onlyOwner() {
        _isClaimable = isClaimable;
    }

    function setNumTokensSellToAddToLiquidity(uint256 _numTokensSellToAddToLiquidity) external onlyOwner() {
        numTokensSellToAddToLiquidity = _numTokensSellToAddToLiquidity;
    }

    function setNumTokensSellForCharity(uint256 _numTokensSellForCharity) external onlyOwner() {
        numTokensSellForCharity = _numTokensSellForCharity;
    }

    function setCharityFeePercent(uint256 charityFee) external onlyOwner() {
        _charityFee = charityFee;
    }

    function setLiquidityFeePercent(uint256 liquidityFee) external onlyOwner() {
        _liquidityFee = liquidityFee;
    }

    function setMaxTxPerMille(uint256 maxTxPerMille) external onlyOwner() {
        _maxTxAmount = _tTotal.mul(maxTxPerMille).div(
            10**3
        );
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
    }

    function setSwapForCharityEnabled(bool _enabled) public onlyOwner {
        SwapForCharityEnabled = _enabled;
    }

    // to recieve ETH from pancakeswapV2Router when swaping
    receive() external payable {}

    function _reflectFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    function _getValues(uint256 tAmount)
        private
        view
        returns (ValuesStruct memory)
    {
        (uint256 tTransferAmount, uint256 tFee, uint256 tLiquidity, uint256 tCharity) =
            _getTValues(tAmount);

        RValuesStruct memory rValuesStruct = RValuesStruct(tAmount, tFee, tLiquidity, tCharity, _getRate());
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 rCharity) =
            _getRValues(rValuesStruct);

        return ValuesStruct(
            rAmount,
            rTransferAmount,
            rFee,
            rCharity,
            tTransferAmount,
            tFee,
            tLiquidity,
            tCharity
        );
    }

    function _getTValues(uint256 tAmount)
        private
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        uint256 tFee = calculateTaxFee(tAmount);
        uint256 tLiquidity = calculateLiquidityFee(tAmount);
        uint256 tCharity = calculateCharityFee(tAmount);
        uint256 tTransferAmount = tAmount.sub(tFee).sub(tLiquidity).sub(tCharity);
        return (tTransferAmount, tFee, tLiquidity, tCharity);
    }

    function _getRValues(
       RValuesStruct memory rValuesStruct
    )
        private
        pure
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        uint256 rAmount = rValuesStruct.tAmount.mul(rValuesStruct.currentRate);
        uint256 rFee = rValuesStruct.tFee.mul(rValuesStruct.currentRate);
        uint256 rLiquidity = rValuesStruct.tLiquidity.mul(rValuesStruct.currentRate);
        uint256 rCharity = rValuesStruct.tCharity.mul(rValuesStruct.currentRate);
        uint256 rTransferAmount = rAmount.sub(rFee).sub(rLiquidity).sub(rCharity);
        return (rAmount, rTransferAmount, rFee, rCharity);
    }

    function _getRate() private view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (
                _rOwned[_excluded[i]] > rSupply ||
                _tOwned[_excluded[i]] > tSupply
            ) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function _takeLiquidity(uint256 tLiquidity) private {
        _liquidityBalance = _liquidityBalance + tLiquidity; // Save liquidity to overall balance
        uint256 currentRate = _getRate();
        uint256 rLiquidity = tLiquidity.mul(currentRate);
        _rOwned[address(this)] = _rOwned[address(this)].add(rLiquidity);
        if (_isExcluded[address(this)])
            _tOwned[address(this)] = _tOwned[address(this)].add(tLiquidity);
    }

    function _takeForCharity(uint tCharity) private {
        _charityBalance = _charityBalance + tCharity;
        uint256 currentRate = _getRate();
        uint256 rCharity = tCharity.mul(currentRate);
        _rOwned[address(this)] = _rOwned[address(this)].add(rCharity);
        if (_isExcluded[address(this)]) {
            _tOwned[address(this)] = _tOwned[address(this)].add(tCharity);
        }
    }

    function addDonatingFee() private {
        if(_taxFee == donatingTaxFee && _liquidityFee == donatingLPFee && _charityFee == donatingCharityFee) return;

        _previousTaxFee = _taxFee;
        _previousLiquidityFee = _liquidityFee;
        _previousCharityFee = _charityFee;

        _taxFee = donatingTaxFee;
        _charityFee = donatingCharityFee;
        _liquidityFee = donatingLPFee;
    }

    function calculateTaxFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_taxFee).div(10**2);
    }

    function calculateLiquidityFee(uint256 _amount)
        private
        view
        returns (uint256)
    {
        return _amount.mul(_liquidityFee).div(10**2);
    }

    function calculateCharityFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_charityFee).div(10**2);
    }

    function removeAllFee() private {
        if (_taxFee == 0 && _liquidityFee == 0 && _charityFee == 0) return;

        _previousTaxFee = _taxFee;
        _previousLiquidityFee = _liquidityFee;
        _previousCharityFee = _charityFee;

        _taxFee = 0;
        _liquidityFee = 0;
        _charityFee = 0;
    }

    function restoreAllFee() private {
        _taxFee = _previousTaxFee;
        _charityFee = _previousCharityFee;
        _liquidityFee = _previousLiquidityFee;
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        if (
            (from != owner() && to != owner()) &&
            (!_isExcludedFromFee[from] && !_isExcludedFromFee[to])
        )
            require(
                amount <= _maxTxAmount,
                "Transfer amount exceeds the maxTxAmount."
            );

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is Pancake pair.
        uint256 contractTokenBalance = balanceOf(address(this));


        if (contractTokenBalance >= _maxTxAmount) {
            contractTokenBalance = _maxTxAmount;
        }

        bool LiquidityOverMinTokenBalance = ((_liquidityBalance >= numTokensSellToAddToLiquidity) && (contractTokenBalance >= numTokensSellToAddToLiquidity));
        bool CharityOverMinTokenBalance = ((_charityBalance >= numTokensSellForCharity) && (contractTokenBalance >= numTokensSellForCharity));
        bool takeFee = true;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }

    if (
            LiquidityOverMinTokenBalance &&
            !inSwapAndLiquify &&
            from != pancakeswapV2Pair && //Non buy tx (sell or transfer)
            swapAndLiquifyEnabled &&
            takeFee
        ) {
            //add liquidity
            swapAndLiquify(numTokensSellToAddToLiquidity);

            // LiquidityBalance always larger because of outer if condition
            _liquidityBalance = _liquidityBalance.sub(numTokensSellToAddToLiquidity);

        } else if (
            CharityOverMinTokenBalance &&
            !inSwapAndLiquify &&
            from != pancakeswapV2Pair && //Non buy tx (sell or transfer)
            SwapForCharityEnabled &&
            takeFee
        ) {
            // add to  pot
            _swapForCharity(numTokensSellForCharity);
            _charityBalance = _charityBalance.sub(numTokensSellForCharity);
        }

        //transfer amount, it will take tax, burn, liquidity fee
        _tokenTransfer(from, to, amount, takeFee);
    }

    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // split the contract balance into halves
        uint256 half = contractTokenBalance.div(2); //ETH
        uint256 otherHalf = contractTokenBalance.sub(half); //BNB

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to Pancake
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function _swapForCharity(uint256 amount) private lockTheSwap {
        swapTokensForEth(amount);
        emit SwapForCharity(amount);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the Pancake pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = pancakeswapV2Router.WETH();

        _approve(address(this), address(pancakeswapV2Router), tokenAmount);

        // make the swap
        pancakeswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(pancakeswapV2Router), tokenAmount);

        // add the liquidity
        pancakeswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner(),
            block.timestamp
        );
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount,
        bool takeFee
    ) private {
        bool isVested = getDonating(sender) && sender != pancakeswapV2Pair;

        if(!takeFee) {
            removeAllFee();
        } else if (isVested) {
            addDonatingFee();
        }

        if (_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferFromExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && _isExcluded[recipient]) {
            _transferToExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferStandard(sender, recipient, amount);
        } else if (_isExcluded[sender] && _isExcluded[recipient]) {
            _transferBothExcluded(sender, recipient, amount);
        } else {
            _transferStandard(sender, recipient, amount);
        }

        if (!takeFee || isVested) restoreAllFee();
    }

    function _transferStandard(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        ValuesStruct memory valuesStruct = _getValues(tAmount);
        uint256 rAmount = valuesStruct.rAmount;
        uint256 rTransferAmount = valuesStruct.rTransferAmount;
        uint256 rFee = valuesStruct.rFee;
        // uint256 rCharity = valuesStruct.rCharity;
        uint256 tTransferAmount = valuesStruct.tTransferAmount;
        uint256 tFee = valuesStruct.tFee;
        uint256 tLiquidity = valuesStruct.tLiquidity;
        uint256 tCharity = valuesStruct.tCharity;

        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        _reflectFee(rFee, tFee);
        _takeForCharity(tCharity);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferToExcluded(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        ValuesStruct memory valuesStruct = _getValues(tAmount);
        uint256 rAmount = valuesStruct.rAmount;
        uint256 rTransferAmount = valuesStruct.rTransferAmount;
        uint256 rFee = valuesStruct.rFee;
        // uint256 rCharity = valuesStruct.rCharity;
        uint256 tTransferAmount = valuesStruct.tTransferAmount;
        uint256 tFee = valuesStruct.tFee;
        uint256 tLiquidity = valuesStruct.tLiquidity;
        uint256 tCharity = valuesStruct.tCharity;

        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        _reflectFee(rFee, tFee);
        _takeForCharity(tCharity);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferFromExcluded(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        ValuesStruct memory valuesStruct = _getValues(tAmount);
        uint256 rAmount = valuesStruct.rAmount;
        uint256 rTransferAmount = valuesStruct.rTransferAmount;
        uint256 rFee = valuesStruct.rFee;
        // uint256 rCharity = valuesStruct.rCharity;
        uint256 tTransferAmount = valuesStruct.tTransferAmount;
        uint256 tFee = valuesStruct.tFee;
        uint256 tLiquidity = valuesStruct.tLiquidity;
        uint256 tCharity = valuesStruct.tCharity;

        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        _reflectFee(rFee, tFee);
        _takeForCharity(tCharity);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function setDonating(address donatingAddress, bool enableDonating) public onlyOwner() {
        require(donatingAddress != address(pancakeswapV2Router), "Do not donate the router.");
        require(donatingAddress != address(pancakeswapV2Pair), "Do not donate the lpPair.");

        donatingAddresses[donatingAddress] = enableDonating;
    }

    function setDonatingForArray(address[] memory donatingAddressArr, bool enableDonating) external onlyOwner() {
        for (uint8 i = 0; i < donatingAddressArr.length; i++) {
            setDonating(donatingAddressArr[i], enableDonating);
        }
    }

    function getDonating(address donatingAddress) public view returns (bool) {
        return _isClaimable && donatingAddresses[donatingAddress];
    }
}
