pragma solidity ^0.8.9;
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Autoburn {

    address public constant MARKETING_WALLET = 0x4dcc41E99b56570BC96D4a449E75f5b664245Ba7;
    address public constant PEPA = 0xC3137c696796D69F783CD0Be4aB4bB96814234Aa;

    uint256 public totalBurned;
    uint256 public lastBurnTimestamp;

    event Burn(address indexed operator, uint256 amount);

    function burn() external {
        uint256 amount = burnable();
        if (amount > 0) {
            IERC20(PEPA).transferFrom(MARKETING_WALLET, address(0xdead), amount);
            totalBurned += amount;
            lastBurnTimestamp = block.timestamp;

            emit Burn(msg.sender, amount);
        }
    } 

    function burnable() public view returns (uint256) {
        return IERC20(PEPA).balanceOf(MARKETING_WALLET);
    }
}
