interface INFT is IERC721Upgradeable {
    function spawnAxie(bytes calldata _genes, address _owner) external returns (uint256);

}

pragma solidity 0.6.12;

contract Governances is Ownable {

    event GovernanceAdded(address indexed governance);

    event GovernanceRemoved(address indexed governance);

    mapping(address => bool) public governances;

    function addGovernance(address governance) public onlyOwner {
        governances[governance] = true;
        emit GovernanceAdded(governance);
    }

    function removeGovernance(address governance) public onlyOwner {
        governances[governance] = false;
        emit GovernanceRemoved(governance);
    }

    modifier onlyGovernance() {
        require(governances[_msgSender()], "Token: caller is not the owner");
        _;
    }

}

pragma solidity 0.6.12;

contract HeroEquipSwap is Context, ERC721Holder, Governances, Pausable{
    address public  signaturer;
    mapping(bytes => bool) public heroWithdrawed;
    mapping(bytes => bool) public itemWithdrawed;

    event DepositHero(uint256 tokenId, uint256 region);
    event WithdrawHero(uint256 tokenId, uint256 guid, uint256 region, bytes signature);

    event DepositEquip(uint256 tokenId, uint256 region);
    event WithdrawEquip(uint256 tokenId, uint256 guid, uint256 region, bytes signature);

    INFT public nftProxyHero;
    INFT public nftProxyItem;

    function initialize(address _heroProxy,address _itemProxy) external onlyOwner {
        nftProxyHero = INFT(_heroProxy);
        nftProxyItem = INFT(_itemProxy);
    }


    /**
     * @dev Triggers stopped state.
     *
     * Requirements:
     *
     * - The contract must not be paused.
     */
    function pause() external virtual onlyOwner whenNotPaused {
        _pause();
    }

    /**
     * @dev Returns to normal state.
     *
     * Requirements:
     *
     * - The contract must be paused.
     */
    function unpause() external virtual onlyOwner whenPaused {
        _unpause();
    }

    ///////////////////////////////////////Verify Signature///////////////////////////////////////////////////

    function recoverSigner(bytes32 _ethSignedMessageHash, bytes memory _signature) public pure returns (address)
    {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);
        return ecrecover(_ethSignedMessageHash, v, r, s);
    }

    function splitSignature(bytes memory sig) public pure returns (bytes32 r, bytes32 s, uint8 v)
    {
        require(sig.length == 65, "invalid signature length");
        assembly {
            r := mload(add(sig, 32))
            s := mload(add(sig, 64))
            v := byte(0, mload(add(sig, 96)))
        }
    }

    function getEthSignedMessageHash(bytes32 _messageHash) public pure returns (bytes32)
    {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", _messageHash));
    }

    function setSignaturer(address s) public onlyOwner{
        signaturer = s;
    }

    function getMessageHashWithdraw(address sender, address owner, address contract_addr, uint256 guid, uint256 region, uint256 tokenId, uint256 nonce, bytes calldata _genes) public pure returns (bytes32)
    {
        return keccak256(abi.encodePacked(sender, owner, contract_addr, guid, region, tokenId, nonce, _genes));
    }

    function verifyWithdraw(bytes memory signature, uint256 guid, uint256 region, uint256 tokenId, uint256 nonce, bytes calldata _genes) public view returns (bool)
    {
        bytes32 messageHash = getMessageHashWithdraw(_msgSender(), signaturer, address(this), guid, region, tokenId,  nonce, _genes);
        bytes32 ethSignedMessageHash = getEthSignedMessageHash(messageHash);
        return recoverSigner(ethSignedMessageHash, signature) == signaturer;
    }

    function heroTransfer(address target, uint256 tokenId) external onlyOwner {
        nftProxyHero.safeTransferFrom(address(this), target, tokenId);
    }

    function itemTransfer(address target, uint256 tokenId) external onlyOwner {
        nftProxyItem.safeTransferFrom(address(this), target, tokenId);
    }

    ///////////////////////////////////////StepN Model///////////////////////////////////////////////////

    function depositHero(uint256 tokenId, uint256 region) public whenNotPaused{
        nftProxyHero.safeTransferFrom(_msgSender(), address(this), tokenId);
        emit DepositHero(tokenId, region);
    }

    function batchDepositHero(uint256[] memory tokenList,uint256[] memory regionList) external whenNotPaused {
        for(uint index=0; index<tokenList.length; index++) {
            depositHero(tokenList[index],regionList[index]);
        }
    }

    function depositItem(uint256 tokenId, uint256 region) public whenNotPaused{
        nftProxyItem.safeTransferFrom(_msgSender(), address(this), tokenId);
        emit DepositEquip(tokenId, region);
    }

    function batchDepositItem(uint256[] memory tokenList,uint256[] memory regionList) external whenNotPaused {
        for(uint index=0; index<tokenList.length; index++) {
            depositItem(tokenList[index],regionList[index]);
        }
    }



    //trans token from game to contract
    function withdrawHero(uint256 guid, uint256 region, uint256 tokenId, uint256 nonce, bytes calldata _genes,  bytes memory signature) public whenNotPaused returns(uint256){
        require(block.timestamp <= nonce,"signature expired");
        require(verifyWithdraw(signature, guid, region, tokenId,  nonce, _genes), "invalid signature");
        require(heroWithdrawed[signature] ==false,"already withdrawed");

        if (tokenId == 0) {
            uint256 newTokenId = nftProxyHero.spawnAxie(_genes, msg.sender);
            heroWithdrawed[signature] = true;
            emit WithdrawHero(newTokenId, guid, region, signature);
            return newTokenId;
        } else {
            nftProxyHero.safeTransferFrom(address(this), _msgSender(), tokenId);
            heroWithdrawed[signature] = true;
            emit WithdrawHero(tokenId, guid, region, signature);
            return tokenId;
        }
    }

    function withdrawItem(uint256 guid, uint256 region, uint256 tokenId, uint256 nonce, bytes calldata _genes,  bytes memory signature) public whenNotPaused returns(uint256){
        require(block.timestamp <= nonce,"signature expired");
        require(verifyWithdraw(signature, guid, region, tokenId,  nonce, _genes), "invalid signature");
        require(itemWithdrawed[signature] ==false,"already withdrawed");

        if (tokenId == 0) {
            uint256 newTokenId = nftProxyItem.spawnAxie(_genes, msg.sender);
            itemWithdrawed[signature] = true;
            emit WithdrawEquip(newTokenId, guid, region, signature);
            return newTokenId;
        } else {
            nftProxyItem.safeTransferFrom(address(this), _msgSender(), tokenId);
            itemWithdrawed[signature] = true;
            emit WithdrawEquip(tokenId, guid, region, signature);
            return tokenId;
        }
    }
}
