        pendingBurnerAdmin = newBurner;
    }

    function claimBurner() public onlyPendingBurnerAdmin {
        emit BurnerTransferred(_burnerAdmin, pendingBurnerAdmin);
        _burnerAdmin = pendingBurnerAdmin;
        pendingBurnerAdmin = address(0);
    }
