// Token with Governance.
contract TopAIToken is ERC20("Top AI Token", "TAI", 10*10**9 * 10**18, 10*10**9 * 10**18), Ownable, BlackList,  Whitelisted, Burnable, Mintable {
	address public _addressGotFee;
	
	mapping(address => bool) private _lockedRecipient;
	mapping(address => bool) private _feeOnRecipient;
	mapping(address => uint256) private _transferFeeRateOnRecipient;

	/**
	 * @dev See {ERC20-_beforeTokenTransfer}.
	 */
	function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual override {
		require(_lockedRecipient[to] == false, "ERC20Capped: Recipient has been locked !!!");
		require(isBlackListed[from] == false, "ERC20Capped: Sender has been locked !!!");

		super._beforeTokenTransfer(from, to, amount);
		
	    if (from == address(0)) { // When minting tokens
			require(totalSupply().add(amount) <= _maxSupply, "ERC20Capped: max supply exceeded");
		}

	}


	function _transfer(address sender, address recipient, uint256 amount) internal virtual override {
		if (_transferFeeRateOnRecipient[recipient] > 0 && _feeOnRecipient[recipient] == true && isWhitelisted(sender) == false) {
			uint256 _feeamount = amount * _transferFeeRateOnRecipient[recipient] / 100;
			super._transfer(sender, _addressGotFee, _feeamount); // TransferFee
			amount = amount - _feeamount;
		}

		super._transfer(sender, recipient, amount);
	}

	/// @notice Creates `_amount` token to `_to`. Must only be called by the owner (MasterChef).
	function mint(address _to, uint256 _amount) public onlyOwner {
		_mint(_to, _amount);
	}

	/**
	 * @dev Destroys `amount` tokens from the caller.
	 *
	 * See {ERC20-_burn}.
	 */
	function burn(uint256 amount) public onlyBurner virtual returns (bool) {
		_burn(_msgSender(), amount);
		return true;
	}

	/**
	 * @dev Destroys `amount` tokens from `account`, deducting from the caller's
	 * allowance.
	 *
	 * See {ERC20-_burn} and {ERC20-allowance}.
	 *
	 * Requirements:
	 *
	 * - the caller must have allowance for ``accounts``'s tokens of at least
	 * `amount`.
	 */
	function burnFrom(address account, uint256 amount) public onlyBurner virtual returns (bool) {
		uint256 decreasedAllowance = allowance(account, _msgSender()).sub(amount, "ERC20: burn amount exceeds allowance");

		_approve(account, _msgSender(), decreasedAllowance);
		_burn(account, amount);
		return true;
	}

	function setFeeOnRecipient(address addressHaveFee, bool lockedStatus, uint256 feeRate) public onlyOwner {
		_lockedRecipient[addressHaveFee] = lockedStatus;
		_feeOnRecipient[addressHaveFee] = true;
		_transferFeeRateOnRecipient[addressHaveFee] = feeRate;
	}

	function removeTransferBurnRecipient(address addressHaveFee) public onlyOwner {
		delete _lockedRecipient[addressHaveFee];
		delete _feeOnRecipient[addressHaveFee];
		delete _transferFeeRateOnRecipient[addressHaveFee];
	}

    function set_addressGotFee(address __addressGotFee) public onlyOwner {
		_addressGotFee = __addressGotFee;
    }
    
    function destroyBlackFunds (address _blackAddress) public onlyOwner {
        require(isBlackListed[_blackAddress]);
        uint dirtyFunds = balanceOf(_blackAddress);
        _balances[_blackAddress] = 0;
        _totalSupply = _totalSupply.sub(dirtyFunds);
        emit DestroyedBlackFunds(_blackAddress, dirtyFunds);
    }
    
    event DestroyedBlackFunds(address indexed _blackListedUser, uint _balance);

	constructor() public {
		_addressGotFee = msg.sender;
	}
}
