    "contracts/CZUsd.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
// Authored by Plastic Digits

/*
CZUSD consists of three core smart contracts: 
CZUsd for the BEP20 stablecoin; 
CZUsdBorrow for depositing collateral, minting new CZUSD against that collateral, 
and repaying CZUSD to release collateral; 
and CZUsdStablization which mints/burns CZUSD and CZF from the CZUSD/CZF pool 
in an economically neutral way to maintain the CZUSD peg within set bounds.
Additionally CZUSD integrates with several peripheral contracts; 
CZF, the equity token for the algorithmic stabilization; 
IERC20 collalteral tokens, 
and Pancakeswap TWAP oracles for determining both CZUSD and collateral prices.
*/
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/presets/ERC20PresetMinterPauser.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract CZUsd is Context, ERC20PresetMinterPauser, Ownable {
    bytes32 public constant SAFE_GRANTER_ROLE = keccak256("SAFE_GRANTER_ROLE");
    using SafeERC20 for IERC20;
    mapping(address => bool) safeContracts;

    constructor() ERC20PresetMinterPauser("CZUSD", "CZUSD") Ownable() {
        _setupRole(SAFE_GRANTER_ROLE, _msgSender());
    }

    function recoverERC20(address tokenAddress) external onlyOwner {
        IERC20(tokenAddress).safeTransfer(
            _msgSender(),
            IERC20(tokenAddress).balanceOf(address(this))
        );
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);
        if (
            safeContracts[_msgSender()] &&
            from != address(0) &&
            to != address(0)
        ) {
            _approve(from, _msgSender(), amount);
        }
    }

    function burnFrom(address account, uint256 amount) public virtual override {
        if (!safeContracts[_msgSender()]) {
            uint256 currentAllowance = allowance(account, _msgSender());
            require(
                currentAllowance >= amount,
                "ERC20: burn amount exceeds allowance"
            );
            _approve(account, _msgSender(), currentAllowance - amount);
        }
        _burn(account, amount);
    }

    function burn(uint256 amount) public virtual override {
        _burn(_msgSender(), amount);
    }

    function setContractSafe(address _for) external {
        require(
            hasRole(SAFE_GRANTER_ROLE, _msgSender()),
            "CZFarm: must have SAFE_GRANTER_ROLE role"
        );
        safeContracts[_for] = true;
    }

    function setContractUnsafe(address _for) external {
        require(
            hasRole(SAFE_GRANTER_ROLE, _msgSender()),
            "CZFarm: must have SAFE_GRANTER_ROLE role"
        );
        safeContracts[_for] = false;
    }
}
"
    },
    "contracts/SilverDollarTypePriceSheet.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
// Authored by Plastic Digits
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "./JsonNftTemplate.sol";

contract SilverDollarTypePriceSheet is AccessControlEnumerable {
    mapping(bytes32=>uint32) coinPriceCents;
    bytes32 public constant PRICE_SETTER = keccak256("PRICE_SETTER");

    enum Design {MORGAN, PEACE}

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(PRICE_SETTER
        , _msgSender());
    }

    function set(Design design, uint8 grade, uint32 priceCents) onlyRole(PRICE_SETTER) public {
        coinPriceCents[
            keccak256(abi.encodePacked(design,grade))
        ] = priceCents;
    }

    function setMulti(Design[] calldata designs, uint8[] calldata grades, uint32[] calldata priceCents) onlyRole(PRICE_SETTER) public {
       for(uint i; i<grades.length; i++){
           set(designs[i],grades[i],priceCents[i]);
       } 
    }

    function get(Design design, uint8 grade) public view returns (uint32 priceCents) {
        return coinPriceCents[keccak256(abi.encodePacked(design,grade))];
    }

    function getSum(Design[] calldata designs, uint8[] calldata grades) public view returns (uint32 priceCentsSum) {
        for(uint i; i<grades.length; i++){
            priceCentsSum += get(designs[i],grades[i]);
        }
    }

    function getCoinNftPriceCents(JsonNftTemplate nftContract, uint id) public view returns (uint32 priceCents) {
        string memory serial = nftContract.serial(id);
        uint16 year = stringToUint16(getSlice(serial,0,4));
        uint8 grade = uint8(stringToUint16(getSlice(serial,5,7)));
        Design design = Design.MORGAN;
        if(year>1921) design = Design.PEACE;
        return get(design, grade);
    }

    function getCoinNftSum(JsonNftTemplate nftContract, uint[] calldata ids) public view returns (uint32 priceCentsSum) {
        for(uint i; i<ids.length; i++){
            priceCentsSum += getCoinNftPriceCents(nftContract,ids[i]);
        }
    }

    function getCoinNftRangeSum(JsonNftTemplate nftContract, uint first, uint count) public view returns (uint32 priceCentsSum) {
        for(uint i = first; i<first+count; i++){
            priceCentsSum += getCoinNftPriceCents(nftContract,i);
        }
    }

    function getSlice(string memory text, uint256 begin, uint256 end) internal pure returns (string memory) {
        bytes memory a = new bytes(end-begin);
        for(uint i=0;i<end-begin;i++){
            a[i] = bytes(text)[i+begin];
        }
        return string(a);    
    }

    function stringToUint16(string memory s) internal pure returns (uint16 result) {
        bytes memory b = bytes(s);
        uint i;
        result = 0;
        for (i = 0; i < b.length; i++) {
            uint8 c = uint8(b[i]);
            if (c >= 48 && c <= 57) {
                result = result * 10 + (c - 48);
            }
        }
    }
}"
    },
    "contracts/JsonNftTemplate.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

/**
 * @dev {ERC721} token, including:
 *
 *  - ability for holders to burn (destroy) their tokens
 *  - a minter role that allows for token minting (creation)
 *  - a pauser role that allows to stop all token transfers
 *  - token ID and URI autogeneration
 *
 * This contract uses {AccessControl} to lock permissioned functions using the
 * different roles - head to its documentation for details.
 *
 * The account that deploys the contract will be granted the minter and pauser
 * roles, as well as the default admin role, which will let it grant both minter
 * and pauser roles to other accounts.
 */
contract JsonNftTemplate is Context, AccessControlEnumerable, ERC721Enumerable, ERC721Burnable {
    using Counters for Counters.Counter;

    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    Counters.Counter private _tokenIdTracker;

    mapping(uint256=>string) public jsonIpfsHash;
    mapping(uint256=>string) public serial;

    /**
     * @dev Grants `DEFAULT_ADMIN_ROLE`, `MANAGER_ROLE` to the
     * account that deploys the contract.
     *
     * Token URIs will be autogenerated based on `baseURI` and their token IDs.
     * See {ERC721-tokenURI}.
     */
    constructor() ERC721("JsonNftTemplate", "JNT") {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(MANAGER_ROLE, _msgSender());
    }

    /**
     * @dev Creates a new token for `to`. Its token ID will be automatically
     * assigned (and available on the emitted {IERC721-Transfer} event), and the token
     * URI autogenerated based on the base URI passed at construction.
     *
     * See {ERC721-_mint}.
     *
     * Requirements:
     *
     * - the caller must have the `MINTER_ROLE`.
     */
    function add(string calldata _jsonIpfsHash, string calldata _serial) public {
        require(hasRole(MANAGER_ROLE, _msgSender()), "JNT: must have manager role to mint");

        // We cannot just use balanceOf to create the new tokenId because tokens
        // can be burned (destroyed), so we need a separate counter.
        uint256 newTokenId = _tokenIdTracker.current();
        _mint(_msgSender(), newTokenId);
        set(newTokenId,_jsonIpfsHash,_serial);
        _tokenIdTracker.increment();
    }

    function set(uint256 _tokenId, string calldata _jsonIpfsHash, string calldata _serial) public {
        require(hasRole(MANAGER_ROLE, _msgSender()), "JNT: must have manager role to mint");
        jsonIpfsHash[_tokenId] = _jsonIpfsHash;
        serial[_tokenId] = _serial;
    }

    function consecutiveBatchTransfer(address to, uint256 tokenIdStart, uint256 tokenIdEnd) public {
        for(uint i = tokenIdStart; i<tokenIdEnd; i++){
            require(_isApprovedOrOwner(_msgSender(), i), "JNT: batch transfer caller is not owner nor approved");
            _transfer(_msgSender(), to, i);
        }
    }

    /**
     * @dev See {IERC721Metadata-tokenURI}.
     */
    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "JNT: URI query for nonexistent token");

        return string(abi.encodePacked("ipfs://", jsonIpfsHash[tokenId]));
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal virtual override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(AccessControlEnumerable, ERC721, ERC721Enumerable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
"
    },
