// SPDX-License-Identifier: MIT

// File: MNFT/enumeration.sol

pragma solidity ^0.8.17;

enum TokenType {ERC721, ERC1155}
enum OrderState {Started, Completed, Canceled}
enum OrderType {Sale, Gift}
// File: MNFT/gallerystructs.sol


pragma solidity ^0.8.17;

struct Param {
    uint ListingPrice;
    uint ComissionPrice;
    bool Paused;
    address payable MNFTAddress;
    address Owner;
}

struct Collections{ 
    address Contract;
    address payable Wallet;
    string Name;
    TokenType Type;
    bool Banned;
    bool Status;
}

struct NFTArt{
    uint ArtId;
    address Owner;
    address Collection;
    uint TokenId;
    TokenType TokenType; 
    bool FirstSale;
    bool Banned;
    bool Status;
    string URI;
}

struct ArtOrder{
    uint OrderId; 
    address Collection;
    address payable Seller;
    address TokenSale;
    uint TokenId; 
    uint Price;
    uint Amount;
    TokenType TokenType;
    OrderType OrderType;
    OrderState State;
    address payable Reciver;
}

// File: MNFT/configuration.sol


pragma solidity ^0.8.17;


contract Configuration {
    
    Param public Config;

    constructor() {
        Config.Owner = msg.sender;
        Config.MNFTAddress = payable(msg.sender);
        Config.ListingPrice = 0;
        Config.ComissionPrice = 10;
        AcceptedTokens[address(0)] = true;
    }

    modifier onlyOwner{
        if (msg.sender == Config.Owner){
            _;
        }
    }

    mapping(address => bool) public AcceptedTokens;

    function PauseContract(bool _Paused) public onlyOwner{
            Config.Paused = _Paused;
        }
       
    function setMNFTAddress(address payable _Address) public onlyOwner{
            Config.MNFTAddress = _Address;
        }

    function setOwner(address _Owner) public onlyOwner{
            Config.Owner = _Owner;
        }

    function setListingPrice(uint256  _ListingPrice) public onlyOwner{
            Config.ListingPrice = _ListingPrice;
        }

    function setComissionPrice(uint256  _ComissionPrice) public onlyOwner{
            Config.ComissionPrice = _ComissionPrice;
        }

    function setAcceptedTokens(address _Token, bool _State) public onlyOwner{
            AcceptedTokens[_Token] = _State;
        }

}
