// File: MNFT/collections.sol


pragma solidity ^0.8.17;





contract ArtCollections is Configuration {

    using Counters for Counters.Counter;
    mapping(address => Collections) public ArtCollection;
    mapping(uint => address) public CollectionId;
    
    Counters.Counter public ColIndex; 

    function addCollection(address _Contract, address payable _Wallet, string memory _Name, TokenType _Type) public onlyOwner{
        require(!ArtCollection[_Contract].Status);

            uint current = ColIndex.current();

            CollectionId[current] = _Contract;

            ArtCollection[_Contract] = Collections(
                _Contract,
                payable(_Wallet),
                _Name,
                _Type,
                false,
                true
            );
            ColIndex.increment();
        }

    function updateCollection(address _Contract, address payable _Wallet, string memory _Name, TokenType _Type, bool _Banned) public onlyOwner{
        require(ArtCollection[_Contract].Status);

            ArtCollection[_Contract].Wallet = payable(_Wallet);
            ArtCollection[_Contract].Name = _Name;
            ArtCollection[_Contract].Type = _Type;
            ArtCollection[_Contract].Banned = _Banned;
        }
}
