// File: MNFT/artnfts.sol


pragma solidity ^0.8.17;







contract ArtNFTs is ArtCollections {

    using Counters for Counters.Counter;
    mapping(address => mapping(uint => NFTArt)) public GalleryArt;

    Counters.Counter public ArtIndex;

    function addGalleryArt(address _Collection, uint _TokenId, address _Owner) internal{
        require(!GalleryArt[_Collection][_TokenId].Status);

        TokenType _TokenType = ArtCollection[_Collection].Type;
        string memory _URI;
        uint _ArtId = ArtIndex.current();

        if(_TokenType == TokenType.ERC721){
            _URI = IERC721Metadata(_Collection).tokenURI(_TokenId);
        }
        if(_TokenType == TokenType.ERC1155){
            _URI = IERC1155MetadataURI(_Collection).uri(_TokenId);
        }    
        
        NFTArt memory _NewArt = NFTArt(
                _ArtId,
                _Owner,
                _Collection,
                _TokenId,
                _TokenType,
                true,
                false,
                true,
                _URI
            );

        ArtIndex.increment();
        GalleryArt[_Collection][_TokenId] = _NewArt;
    }

    function banGalleryArt(address _Collection, uint _TokenId, bool _Banned) public onlyOwner{
        require(GalleryArt[_Collection][_TokenId].Status);
        GalleryArt[_Collection][_TokenId].Banned = _Banned;
    }

}

// File: MNFT/galleryorders.sol


pragma solidity ^0.8.17;





contract GalleryOrders is ArtNFTs{
    
    using Counters for Counters.Counter;
    Counters.Counter public OrderIndex;
    mapping(uint => ArtOrder) public GalleryOrder;

    function createOrder(address _Collection, uint _TokenId, address payable _Seller, address _TokenSale, uint _Price, uint _Amount, OrderType _OrderType, address payable _Reciver) internal returns(uint){

        uint OrderId = OrderIndex.current();

        if(!GalleryArt[_Collection][_TokenId].Status){
            addGalleryArt(_Collection, _TokenId, _Seller);
        }

        TokenType _TokenType = GalleryArt[_Collection][_TokenId].TokenType;

            ArtOrder memory _NewOrder = ArtOrder(
                OrderId,
                _Collection,
                _Seller,
                _TokenSale,
                _TokenId,
                _Price,
                _Amount,
                _TokenType,
                _OrderType,
                OrderState.Started,
                _Reciver
            );

        GalleryOrder[OrderId] = _NewOrder;
        OrderIndex.increment();
        return OrderId;
    }

}
