pragma solidity ^0.8.17;










contract CoreGallery is ReentrancyGuard, Configuration, GalleryOrders, ERC1155Holder{

    using Counters for Counters.Counter;
    Counters.Counter public SoldIndex;

event BuyOrderEvent(
        uint indexed OrderId,
        address indexed Collection,
        uint indexed TokenId,
        uint Amount,
        TokenType TokenType,
        address Seller,
        address Buyer,
        address TokenSale,
        uint Price
    );

event NewOrderEvent(
        uint indexed OrderId,
        address indexed Collection,
        uint indexed TokenId,
        uint Amount,
        OrderType OrderType,
        address Seller,
        address TokenSale,
        uint Price
    );

event CancelOrderEvent(
        uint indexed OrderId,
        address indexed Collection,
        uint indexed TokenId
    );

function NewOrder(address _Collection, uint _TokenId, uint _Price, uint _Amount, address _TokenSale, OrderType _OrderType, address payable _Reciver) public payable returns(uint){
    require(ArtCollection[_Collection].Status);
    require(!GalleryArt[_Collection][_TokenId].Banned);
    require(msg.value == Config.ListingPrice);
    require(AcceptedTokens[_TokenSale]);
    require(!Config.Paused);
    require(_Price >= 0);
    require(_Amount > 0);

    TokenType _TokenType = ArtCollection[_Collection].Type;

    if(_TokenType == TokenType.ERC721){
        require(IERC721(_Collection).ownerOf(_TokenId) == msg.sender);
        require(IERC721(_Collection).isApprovedForAll(msg.sender,address(this)));

        IERC721(_Collection).transferFrom(msg.sender, address(this), _TokenId);
        }
    if(_TokenType == TokenType.ERC1155){
        require(IERC1155(_Collection).balanceOf(msg.sender, _TokenId) >= _Amount);
        require(IERC1155(_Collection).isApprovedForAll(msg.sender,address(this)));

        IERC1155(_Collection).safeTransferFrom(msg.sender, address(this), _TokenId, _Amount, "0x68");
        }

    uint _OrderId = createOrder(_Collection, _TokenId, payable(msg.sender), _TokenSale, _Price, _Amount, _OrderType, _Reciver);

    emit NewOrderEvent(
        _OrderId,
        _Collection,
        _TokenId,
        _Amount,
        _OrderType,
        msg.sender,
        _TokenSale,
        _Price
        );

    return _OrderId;
}

function CancelOrder(uint _OrderId) public payable{
    require(GalleryOrder[_OrderId].Seller == msg.sender);
    require(GalleryOrder[_OrderId].State == OrderState.Started);

    GalleryOrder[_OrderId].State = OrderState.Canceled;

    if(GalleryOrder[_OrderId].TokenType == TokenType.ERC721){
        IERC721(GalleryOrder[_OrderId].Collection).transferFrom(address(this), GalleryOrder[_OrderId].Seller, GalleryOrder[_OrderId].TokenId);
    }
    if(GalleryOrder[_OrderId].TokenType == TokenType.ERC1155){
        IERC1155(GalleryOrder[_OrderId].Collection).safeTransferFrom(address(this), GalleryOrder[_OrderId].Seller, GalleryOrder[_OrderId].TokenId, GalleryOrder[_OrderId].Amount, "0x70");
    }

    emit CancelOrderEvent(
        _OrderId,
        GalleryOrder[_OrderId].Collection,
        GalleryOrder[_OrderId].TokenId
        );
}

function BuyOrder(uint _OrderId, uint _Amount) public payable nonReentrant{
    require(GalleryOrder[_OrderId].State == OrderState.Started);
    require(GalleryOrder[_OrderId].OrderType == OrderType.Sale);
    require(!Config.Paused);
    require(GalleryOrder[_OrderId].Amount >= _Amount);
    require(!GalleryArt[GalleryOrder[_OrderId].Collection][GalleryOrder[_OrderId].TokenId].Banned);
    uint TempValue;
    uint _TotalVal = GalleryOrder[_OrderId].Price * _Amount;

    if(GalleryOrder[_OrderId].TokenSale == address(0)){
        require(msg.value == _TotalVal);

            if(GalleryArt[GalleryOrder[_OrderId].Collection][GalleryOrder[_OrderId].TokenId].FirstSale){
                TempValue = (_TotalVal / 100) * Config.ComissionPrice;
                TempValue = _TotalVal - TempValue;
                bool sentS = GalleryOrder[_OrderId].Seller.send(TempValue);
                require(sentS);

            }else{
                TempValue = _TotalVal / 100;
                bool sentA = ArtCollection[GalleryOrder[_OrderId].Collection].Wallet.send(TempValue);
                TempValue = _TotalVal - (2 * TempValue);
                bool sentS = GalleryOrder[_OrderId].Seller.send(TempValue);
                require(sentA);
                require(sentS);
            }

    }else{
        IERC20 Token = IERC20(GalleryOrder[_OrderId].TokenSale);
        uint256 Allowance = Token.allowance(msg.sender, address(this));
        uint256 Balance = Token.balanceOf(msg.sender);
        require(Balance >= _TotalVal);
        require(Allowance >= _TotalVal);

        if(Token.transferFrom(msg.sender, address(this), _TotalVal)){
            if(GalleryArt[GalleryOrder[_OrderId].Collection][GalleryOrder[_OrderId].TokenId].FirstSale){
                TempValue = (_TotalVal / 100) * Config.ComissionPrice;
                TempValue = _TotalVal - TempValue;
                Token.transfer(GalleryOrder[_OrderId].Seller, TempValue);

            }else{
                TempValue = _TotalVal / 100;
                Token.transfer(ArtCollection[GalleryOrder[_OrderId].Collection].Wallet, TempValue);
                TempValue = _TotalVal - (2 * TempValue);
                Token.transfer(GalleryOrder[_OrderId].Seller, TempValue);
            }
        }
    }

    GalleryOrder[_OrderId].Amount -= _Amount;

    if(GalleryOrder[_OrderId].Amount == 0){
        GalleryArt[GalleryOrder[_OrderId].Collection][GalleryOrder[_OrderId].TokenId].FirstSale = false;
        GalleryOrder[_OrderId].State = OrderState.Completed;
    }

    if(GalleryOrder[_OrderId].TokenType == TokenType.ERC721){
        IERC721(GalleryOrder[_OrderId].Collection).transferFrom(address(this), msg.sender, GalleryOrder[_OrderId].TokenId);
        GalleryArt[GalleryOrder[_OrderId].Collection][GalleryOrder[_OrderId].TokenId].Owner = msg.sender;
    }
    if(GalleryOrder[_OrderId].TokenType == TokenType.ERC1155){
        IERC1155(GalleryOrder[_OrderId].Collection).safeTransferFrom(address(this), msg.sender, GalleryOrder[_OrderId].TokenId, _Amount, "0x69");
    }

    SoldIndex.increment();

    emit BuyOrderEvent(
        _OrderId,
        GalleryOrder[_OrderId].Collection,
        GalleryOrder[_OrderId].TokenId,
        _Amount,
        GalleryOrder[_OrderId].TokenType,
        GalleryOrder[_OrderId].Seller,
        msg.sender,
        GalleryOrder[_OrderId].TokenSale,
        GalleryOrder[_OrderId].Price
        );
    }

function ClaimOrder(uint _OrderId) public payable nonReentrant{
    require(GalleryOrder[_OrderId].State == OrderState.Started);
    require(GalleryOrder[_OrderId].OrderType == OrderType.Gift);
    require(!GalleryArt[GalleryOrder[_OrderId].Collection][GalleryOrder[_OrderId].TokenId].Banned);
    require(GalleryOrder[_OrderId].Reciver == msg.sender);
    require(!Config.Paused);

    GalleryOrder[_OrderId].State = OrderState.Completed;

    if(GalleryOrder[_OrderId].TokenType == TokenType.ERC721){
        IERC721(GalleryOrder[_OrderId].Collection).transferFrom(address(this), GalleryOrder[_OrderId].Reciver, GalleryOrder[_OrderId].TokenId);
    }
    if(GalleryOrder[_OrderId].TokenType == TokenType.ERC1155){
        IERC1155(GalleryOrder[_OrderId].Collection).safeTransferFrom(address(this), GalleryOrder[_OrderId].Reciver, GalleryOrder[_OrderId].TokenId, GalleryOrder[_OrderId].Amount, "0x72");
    }

    SoldIndex.increment();

    emit BuyOrderEvent(
        _OrderId,
        GalleryOrder[_OrderId].Collection,
        GalleryOrder[_OrderId].TokenId,
        GalleryOrder[_OrderId].Amount,
        GalleryOrder[_OrderId].TokenType,
        GalleryOrder[_OrderId].Seller,
        msg.sender,
        GalleryOrder[_OrderId].TokenSale,
        GalleryOrder[_OrderId].Price
        );
    }
}

// File: MNFT/MNFTGallery2.sol


pragma solidity ^0.8.17;



contract MNFTGallery2 is CoreGallery {

    function getBalance(address _Token) public view returns(uint){
        
        if(_Token == address(0)){
            return address(this).balance;
        }else{
            IERC20 Token = IERC20(_Token);
            return Token.balanceOf(address(this));
        }
    
    }

    function claimBalance(address _Token, uint _Amount) public payable{
        require(msg.sender == Config.MNFTAddress);
        uint _Balance = getBalance(_Token);
        require(_Balance >= _Amount);
        
        if(_Token == address(0)){
            bool sent = Config.MNFTAddress.send(_Amount);
            require(sent);
            
        }else{
            IERC20 Token = IERC20(_Token);
            Token.transfer(Config.MNFTAddress, _Amount);
        }
    
    }

}
