    "src/vnctools.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "lib/openzeppelin-contracts/contracts/security/Pausable.sol";
import "lib/openzeppelin-contracts/contracts/access/AccessControl.sol";

contract VNCTools is Pausable, AccessControl {
    bytes32 public constant WITHDRAW = keccak256("WITHDRAW");
    bytes32 public constant ADMIN = keccak256("ADMIN");

    mapping(address => string ) private seriNumber ;
    mapping(address => uint256) private timeExpTCSwap;
    mapping(address => uint256) private timeExpTCScan;
    mapping(address => uint256) private timeExpTCStoploss;
    mapping(address => uint256) private timeExpTCScanETH;
    mapping(address => uint16) private VIP; // 1 vip 2, 2 vip 2, 3 Svip
    mapping(string => uint256) private timeExpSerinumber;


    address[] private userScan;
    address[] private userSwap;


    uint256 private maxWithdrawAmount ;
    bool public withdrawEnable;
    uint256 private price30DaysScanVip1;
    uint256 private price30DaysScanVip2;
    uint256 private price30DaysScanSVip;

    uint256 private price30DaysSwap;
    uint256 private price30DaysCombo;


    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(WITHDRAW, msg.sender);
        _grantRole(ADMIN, msg.sender);
    }
    function setAdmin(address addressUser) public onlyRole(DEFAULT_ADMIN_ROLE){
        _grantRole(ADMIN, addressUser);
    }
    function unSetAdmin(address addressUser) public onlyRole(DEFAULT_ADMIN_ROLE){
        _revokeRole(ADMIN, addressUser);
    }
    // set withdown enabled
    function setWithdrawEnable() public onlyRole(DEFAULT_ADMIN_ROLE){
        withdrawEnable = true;
    }
    // set withdown disabled
    function setWithdrawDisable() public onlyRole(DEFAULT_ADMIN_ROLE){
        withdrawEnable = false;
    }
    //set Role
    function grantRoleWithdraw(address addressUser) public onlyRole (DEFAULT_ADMIN_ROLE){
        _grantRole(WITHDRAW, addressUser);

    }
    // Unset Role
    function revokeRoleWithdraw(address addressUser) public onlyRole (DEFAULT_ADMIN_ROLE){
        _revokeRole(WITHDRAW, addressUser);
    }
    // Pause
    function pause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }
    // Unpause
    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }
    // withdraw all
    function withdrawBNB() external onlyRole(DEFAULT_ADMIN_ROLE) {
        address payable sender = payable(msg.sender);
        require(msg.sender != address(0));
        sender.transfer(address(this).balance); 
    }
    //Set withraw limit
    function setWithrawLimit(uint256 amountLimit) public onlyRole (DEFAULT_ADMIN_ROLE) {
        maxWithdrawAmount = amountLimit;
    }
    //Set withraw limit
    function withdrawBNBLimit(uint256 amountInWei) external onlyRole(WITHDRAW) {
        require(withdrawEnable, "WITHDRAW is not enabled");
        require(amountInWei <= maxWithdrawAmount, "Amount must be smaller than the maximum amount");
        require(amountInWei <= address(this).balance, "Don't have enough money");
        require(msg.sender != address(0));
        address payable sender = payable(msg.sender);
        sender.transfer(amountInWei); 
    }
    receive() external payable {
        // Hàm này sẽ được gọi khi một số BNB được gửi vào smart contract
    }
    // set price for 30 days bot Swap
    function setPriceSwap(uint256 priceInWei) public onlyRole(DEFAULT_ADMIN_ROLE){
        price30DaysSwap = priceInWei;
    }
    // set price for 30 days bot Scan
    function setPriceScan(uint256 priceInWeiVip1,uint256 priceInWeiVip2,uint256 priceInWeiSVip) public onlyRole(DEFAULT_ADMIN_ROLE) {
        price30DaysScanVip1 = priceInWeiVip1;
        price30DaysScanVip2 = priceInWeiVip2;
        price30DaysScanSVip = priceInWeiSVip;

    }
    // set price for 30 days bot Scan
    function setPriceCombo(uint256 priceInWei) public onlyRole(DEFAULT_ADMIN_ROLE) {
        price30DaysCombo = priceInWei;
    }

    // ad 30 day TC Swap
    function buy30daysSwapBot() payable external  {
        if(timeExpTCSwap[msg.sender] < 1000){
            userSwap.push(msg.sender);
        }
        require(msg.value == price30DaysSwap);
        if (timeExpTCSwap[msg.sender] > block.timestamp){
                timeExpTCSwap[msg.sender] +=  2592000;     
        } else {
            timeExpTCSwap[msg.sender] = block.timestamp + 2592000;
        }   
        timeExpTCStoploss[msg.sender] = timeExpTCSwap[msg.sender];
    }
    // add 30 days to TC Scan
    function buy30daysScanBot(string memory seriNumberUser, uint16 vip) payable external  {
        // 1 = vip1, 2  = vip2, 3 = SVip
        if (vip == 1){
            require(msg.value == price30DaysScanVip1);   
        } else if (vip == 2){
            require(msg.value == price30DaysScanVip2);   
        } else if (vip == 3){
            require(msg.value == price30DaysScanSVip);   
        } else {
            return;
        }
        if(timeExpTCScan[msg.sender]< 1000){
            userScan.push(msg.sender);
        }
        if (timeExpTCScan[msg.sender] > block.timestamp){
                timeExpTCScan[msg.sender]  +=  2592000;           
        } else {
            timeExpTCScan[msg.sender] = block.timestamp + 2592000;
        } 
        seriNumber[msg.sender] = seriNumberUser;
        if(vip > VIP[msg.sender]){
            timeExpTCScan[msg.sender] = block.timestamp + 2592000;
        }
        timeExpTCScanETH[msg.sender] = timeExpTCScan[msg.sender];
        VIP[msg.sender] = vip;


    }
    function buy30daysCombo(string memory seriNumberUser) payable external  {
        if(timeExpTCScan[msg.sender]< 1000){
            userScan.push(msg.sender);
        }
        if(timeExpTCSwap[msg.sender] < 1000){
            userSwap.push(msg.sender);
        }
        require(msg.value == price30DaysCombo);
        if (timeExpTCScan[msg.sender] > block.timestamp){
                timeExpTCScan[msg.sender]  +=  2592000;           
        } else {
            timeExpTCScan[msg.sender] = block.timestamp + 2592000;
        } 
        seriNumber[msg.sender] = seriNumberUser;
        if(VIP[msg.sender]!=3){
            timeExpTCScan[msg.sender] = block.timestamp + 2592000;
        }
        timeExpTCScanETH[msg.sender] = timeExpTCScan[msg.sender];
        VIP[msg.sender] = 3;

        if (timeExpTCSwap[msg.sender] > block.timestamp){
                timeExpTCSwap[msg.sender] +=  2592000;     
        } else {
            timeExpTCSwap[msg.sender] = block.timestamp + 2592000;
        }   
        timeExpTCStoploss[msg.sender] = timeExpTCSwap[msg.sender];

    }
    function setLicenseScan(address addressUser, uint256 expTimestamp, uint16 vip) public onlyRole(ADMIN){
        if(timeExpTCScan[addressUser]< 1000){
            userScan.push(addressUser);
        }
        timeExpTCScan[addressUser] = expTimestamp;
        timeExpTCScanETH[addressUser] = timeExpTCScan[addressUser];
        VIP[addressUser] = vip;

    }
    function setLicenseScanETH(address addressUser, uint256 expTimestamp) public onlyRole(ADMIN){

        timeExpTCScanETH[addressUser] = expTimestamp;

    }
    //set expTimestamp for SeriNumber 
    function setLicenseSeriNumber(string memory SN, uint256 expTimestamp) public onlyRole(ADMIN){

        timeExpSerinumber[SN] = expTimestamp;

    }
    function setLicenseSwap(address addressUser, uint256 expTimestamp) public onlyRole(ADMIN){
        if(timeExpTCSwap[addressUser] < 1000){
            userSwap.push(addressUser);
        }
        timeExpTCSwap[addressUser] = expTimestamp;

    }
    function setLicenseStoploss(address addressUser, uint256 expTimestamp) public onlyRole(ADMIN){
        timeExpTCStoploss[addressUser] = expTimestamp;
    }
    function setSeriNumber(address addressUser, string memory seriNumberUser) public onlyRole(ADMIN){
        seriNumber[addressUser] = seriNumberUser;
    }
    function changeSeriNumber(string memory newSeriNumber) public {
        require(timeExpTCScan[msg.sender] > block.timestamp, "Error : Your license has expired");
        seriNumber[msg.sender] = newSeriNumber;
    }
    function changeWalletSwap(address newAddress) public {
        require(timeExpTCSwap[msg.sender]>block.timestamp, "Error : Your license has expired");
        uint256 temp;
        if(timeExpTCSwap[newAddress] < 1000){
            userSwap.push(newAddress);
        }
        temp = timeExpTCSwap[msg.sender];
        timeExpTCSwap[msg.sender] = 0;
        timeExpTCSwap[newAddress] = temp;

    }
    function changeWalletScan(address newAddress) public {
        require(timeExpTCScan[msg.sender]>block.timestamp, "Error : Your license has expired");
        uint256 temp;
        if(timeExpTCScan[newAddress]< 1000){
            userScan.push(newAddress);
        }
        temp = timeExpTCScan[msg.sender];
        timeExpTCScan[msg.sender] = 0;
        timeExpTCScanETH[msg.sender]=0;
        timeExpTCScan[newAddress] = temp;
        timeExpTCScanETH[newAddress] = temp;
        seriNumber[newAddress] = seriNumber[msg.sender];
        VIP[newAddress] = VIP[msg.sender];

    }
    function getSerial(address addressUser) public view returns (string memory){
        return seriNumber[addressUser];
    }
    function getExpScan(address addressUser) public view returns (uint256){
        return timeExpTCScan[addressUser];
    }
    function getExpScanETH(address addressUser) public view returns (uint256){
        return timeExpTCScanETH[addressUser];
    }
    function getExpSwap(address addressUser) public view returns (uint256){
        return timeExpTCSwap[addressUser];
    }
    function getExpStopLoss(address addressUser) public view returns (uint256){
        return timeExpTCStoploss[addressUser];
    }
    function getPriceScan() public view returns (uint256 [3] memory){
        return [price30DaysScanVip1 ,price30DaysScanVip2,price30DaysScanSVip];
    }
    function getVIP(address addressCheck) public view returns (uint16 ){
        return VIP[addressCheck];
    }
    function getPriceSwap() public view returns (uint256){
        return price30DaysSwap;
    }
    function getPriceCombo() public view returns (uint256){
        return price30DaysCombo;
    }
    function getUserSwap() public view onlyRole(ADMIN)returns (address [] memory){
        return userSwap;
    }
    function getUserScan() public view onlyRole(ADMIN)returns (address [] memory){
        return userScan;
    }
    function getExpSerinumber(string memory Seri) public view returns (uint256){
        return timeExpSerinumber[Seri];
    }
}"
