// File: BTMT/contracts/Staking.sol

pragma solidity ^0.8.17;



contract Staking is Ownable {
    struct topup {
        uint256 amount;
        uint256 stakeAt;
        uint256 stakeFor;
    }

    struct withdraw {
        uint256 withdrawAmount;
        uint256 withdrawAt;
    }

    struct Investor {
        bool registered;
        uint256 investedAmount;
        uint256 investedAt;
        uint256 investedFor;
        topup[] _topup;
    }

    struct WithdrawData {
        uint256 withdrawAmount;
        uint256 lastWithdrawTime;
        withdraw[] _withdraw;
    }

    mapping(address => Investor) public lastUserTransaction;
    mapping(address => WithdrawData) public withdrawData;
    mapping(address => uint256) private totalInvestedAmount;
    mapping(address => uint256) private totalWithdraw;

    IBEP20 public myToken;
    uint256 private investorID = 1;
    uint256 public totalInvestors;
    uint256 public totalCoinInvested;
    uint256 public totalWithdrawlTOKEN;

    event TOKENToken(
        address sender,
        address recevier,
        uint256 tokenAmount,
        uint256 timeOfDeposit,
        uint256 investedFor
    );

    event claimedToken(
        address receiver,
        uint256 withdrawAmount,
        uint256 withdrawTime
    );

    function initialize(address _tokenAddress) public onlyOwner {
        myToken = IBEP20(_tokenAddress);
    }

    uint256 private min_deposit = 0 * (10 ** 9);
    uint256 private min_withdrawl = 0 * (10 ** 9);

    function setMin_MaxDeposit(
        uint256 minValue,
        uint256 maxValue
    ) private onlyOwner {
        min_deposit = minValue;
        min_withdrawl = maxValue;
    }

    uint256 public stakeFor = 31536000;

    function setStakeTime(uint256 _stakeFor) public onlyOwner{
        stakeFor = _stakeFor;
    }

    function getMin_MaxValue() public view returns (uint256, uint256) {
        return (min_deposit, min_withdrawl);
    }

    function stakeToken(uint256 amount) public {
        require(
            myToken.balanceOf(msg.sender) >= amount,
            "Insufficient balance"
        );
        unchecked {
            investorID++;
        }
        if (!lastUserTransaction[msg.sender].registered) {
            lastUserTransaction[msg.sender].registered = true;
            totalInvestors++;
        }
        myToken.transferFrom(msg.sender, address(this), amount);
        lastUserTransaction[msg.sender].investedAmount = amount;
        totalCoinInvested += amount;
        lastUserTransaction[msg.sender].investedAt = block.timestamp;
        uint256 stakeFor_ = block.timestamp + stakeFor;
        // 31536000
        lastUserTransaction[msg.sender].investedFor = stakeFor_;
        lastUserTransaction[msg.sender]._topup.push(
            topup(amount, block.timestamp, stakeFor_)
        );
        totalInvestedAmount[msg.sender] += amount;
        emit TOKENToken(
            msg.sender,
            address(this),
            amount,
            block.timestamp,
            stakeFor
        );
    }

    function withdrawToken(uint256 amount) public {
        require(
            lastUserTransaction[msg.sender].registered == true,
            "Invalid User"
        );
        require(
            withdrawData[msg.sender].withdrawAmount <
                lastUserTransaction[msg.sender].investedAmount,
            "No coin left to withdraw"
        );
        if (block.timestamp < lastUserTransaction[msg.sender].investedFor) {
            myToken.transfer(msg.sender, amount);
        } else {
            uint256 coininvest = lastUserTransaction[msg.sender].investedAmount;
            uint256 coinwithdraw = withdrawData[msg.sender].withdrawAmount;
            uint256 coinLeft = coininvest - coinwithdraw;
            require(amount <= coinLeft, "No more coin left for withdraw");
            myToken.transfer(msg.sender, amount);
        }
        withdrawData[msg.sender].withdrawAmount += amount;
        withdrawData[msg.sender].lastWithdrawTime = block.timestamp;
        totalWithdrawlTOKEN += amount;
        totalWithdraw[msg.sender] = amount;
        withdrawData[msg.sender]._withdraw.push(
            withdraw(amount, block.timestamp)
        );
        emit claimedToken(msg.sender, amount, block.timestamp);
    }

    function getMatureTimeByUser(address user) public view returns (uint256) {
        require(lastUserTransaction[user].investedFor - block.timestamp < 0, "Time Expired, can claim");
        return (lastUserTransaction[user].investedFor - block.timestamp);
    }

    function getDetailForTOKENDeposit(
        address user
    )
        public
        view
        returns (
            bool isRegistered,
            uint256[] memory depositAmount,
            uint256[] memory depositAt,
            uint256[] memory depositFor
        )
    {
        Investor storage investor = lastUserTransaction[user];
        uint256 len = investor._topup.length;
        depositAmount = new uint256[](len);
        depositAt = new uint256[](len);
        depositFor = new uint256[](len);

        for (uint256 i = 0; i < investor._topup.length; i++) {
            topup storage topup_ = investor._topup[i];
            depositAmount[i] = topup_.amount;
            depositAt[i] = topup_.stakeAt;
            depositFor[i] = topup_.stakeFor;
        }
        return (investor.registered, depositAmount, depositAt, depositFor);
    }

    function getDetailForTOKENWithdrawl(
        address user
    )
        public
        view
        returns (uint256[] memory withdrawAmount, uint256[] memory withdrawAt)
    {
        WithdrawData storage investor = withdrawData[user];
        uint256 len = investor._withdraw.length;
        withdrawAmount = new uint256[](len);
        withdrawAt = new uint256[](len);

        for (uint256 i = 0; i < investor._withdraw.length; i++) {
            withdraw storage topup_ = investor._withdraw[i];
            withdrawAmount[i] = topup_.withdrawAmount;
            withdrawAt[i] = topup_.withdrawAt;
        }

        return (withdrawAmount, withdrawAt);
    }
}
