    address public marketingWallet = 0xbbAD60E6419851f86450eE0d49c1182B62a01456; // Mainnet address
    address public deadWallet = 0x0000000000000000000000000000000000000000;

    uint256 public airdropPercent = 1;
    uint256 public burnPercent = 1;

    uint256 public _previousAirdropPercent = airdropPercent;
    uint256 public _previousBurnPercent = burnPercent;

    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;

    // Fees (total: 6.5%) - these fees are multiplied * 10 to handle decimals
    uint256 public _taxFee = 10; // values in % * 10
    uint256 private _previousTaxFee = _taxFee;

    uint256 public _burnFee = 10;
    uint256 private _previousBurnFee = _burnFee;

    uint256 public _marketingFee = 10;
    uint256 private _previousMarketingFee = _marketingFee;

    uint256 public _buybackFee = 25;
    uint256 private _previousBuybackFee = _buybackFee;

    uint256 public _donationsFee = 10;
    uint256 private _previousDonationsFee = _donationsFee;

    address [] public tokenHolder;
    mapping(address => uint256) public balanceForAirdrop;
    uint256 public numberOfTokenHolders = 0;
    mapping(address => bool) public exist;

    uint256 private _holdersBalanceForAirdrop = 0;
    uint256 public _maxTxAmount = _tTotal;
    address payable internal wallet;

    IPancakeRouter02 public pancakeRouter;
    address public pancakePair;

    bool public limit = true;

    bool public zeroSlippage = false;

    event Withdraw(uint256 amount);
    event WithdrawToken(uint256 amount);

    constructor () {
        // Define initial balance
        _rOwned[_msgSender()] = _rTotal;

        wallet = payable(msg.sender);
        IPancakeRouter02 _pancakeRouter = IPancakeRouter02(0x10ED43C718714eb63d5aA57B78B54704E256024E); // PancakeRouter v2

        // Create a pancake pair for this new token
        pancakePair = IPancakeFactory(_pancakeRouter.factory())
        .createPair(address(this), _pancakeRouter.WETH());

        // set the rest of the contract variables
        pancakeRouter = _pancakeRouter;

        //exclude owner and this contract from fee and rewards
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _isExcluded[address(this)] = true;
        excludeFromReward(owner());

        // Exclude also airdrop, burn, dead, buyback and marketing wallets
        _isExcludedFromFee[airdropWallet] = true;
        _isExcludedFromFee[burnWallet] = true;
        _isExcludedFromFee[deadWallet] = true;
        _isExcludedFromFee[buybackWallet] = true;
        _isExcludedFromFee[donationsWallet] = true;
        _isExcludedFromFee[marketingWallet] = true;
        excludeFromReward(airdropWallet);
        excludeFromReward(burnWallet);
        excludeFromReward(deadWallet);
        excludeFromReward(buybackWallet);
        excludeFromReward(donationsWallet);
        excludeFromReward(marketingWallet);
        excludeFromReward(pancakePair);

        emit Transfer(address(0), _msgSender(), _tTotal);

        _tokenTransfer(_msgSender(), burnWallet, _burnBalance, false, true);
        _tokenTransfer(_msgSender(), airdropWallet, _airdropBalance, false, true);
        _tokenTransfer(_msgSender(), buybackWallet, _buybackBalance, false, true);
        _tokenTransfer(_msgSender(), marketingWallet, _marketingBalance, false, true);
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal.sub(balanceOf(deadWallet));
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function internalBalanceOf(address account) public view override returns (uint256) {
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function setNewRouter(address router) public onlyOwner() {
        IPancakeRouter02 _pancakeRouter = IPancakeRouter02(router);

        // Create a pancake pair for this new token
        pancakePair = IPancakeFactory(_pancakeRouter.factory())
        .createPair(address(this), _pancakeRouter.WETH());

        // set the rest of the contract variables
        pancakeRouter = _pancakeRouter;
    }

    function isExcludedFromReward(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function toggleLimit() public onlyOwner() {
        if (limit) {
            limit = false;
        } else {
            limit = true;
        }
    }

    function toggleZeroSlippage() public onlyOwner() {
        if (zeroSlippage) {
            zeroSlippage = false;
        } else {
            zeroSlippage = true;
        }
    }

    function setAirdropPercent(uint256 p) public onlyOwner() {
        airdropPercent = p;
    }

    function setBurnPercent(uint256 p) public onlyOwner() {
        burnPercent = p;
    }

    function resetAirdropPercent() public onlyOwner() {
        airdropPercent = _previousAirdropPercent;
    }

    function resetBurnPercent() public onlyOwner() {
        burnPercent = _previousBurnPercent;
    }

    function castBurn() public onlyExtractor() {
        _burn();
    }

    function castAirdrop() public onlyExtractor() {
        _airdrop();
    }

    function castManualAirdrop() public onlyOwner() {
        _airdrop();
    }

    function castManualBurn() public onlyOwner() {
        _burn();
    }

    // Burns tokens from set BURN ADDRESS
    function _burn() private {
        uint256 burnAmount = calculateBurnPercent(_burnBalance);
        // Send to dead wallet without fees
        _tokenTransfer(burnWallet, deadWallet, burnAmount, false, true);
    }

    // Airdrops tokens from set AIRDROP ADDRESS proportionally
    function _airdrop() private {
        uint tAmount = calculateAirdropPercent(_airdropBalance);

        // Reflect whole airdrop
        uint256 currentRate = _getRate();
        uint256 rFee = tAmount.mul(currentRate);
        _reflectFee(rFee, tAmount);

        uint256 rAmount = tAmount.mul(currentRate);

        _tOwned[airdropWallet] = _tOwned[airdropWallet].sub(tAmount);
        _rOwned[airdropWallet] = _rOwned[airdropWallet].sub(rAmount);

        emit Transfer(airdropWallet, deadWallet, tAmount);
    }

    function reflectionFromToken(uint256 tAmount, bool deductTransferFee) public view returns (uint256) {
        require(tAmount <= _tTotal, "Amount must be less than supply");
        if (!deductTransferFee) {
            (uint256 rAmount,,,,,) = _getValues(tAmount);
            return rAmount;
        } else {
            (,uint256 rTransferAmount,,,,) = _getValues(tAmount);
            return rTransferAmount;
        }
    }

    function tokenFromReflection(uint256 rAmount) public view returns (uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total reflections");
        uint256 currentRate = _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromReward(address account) public onlyOwner() {
        // require(account != 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D, 'We can not exclude pancake router.');
        require(!_isExcluded[account], "Account is already excluded");
        if (_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeInReward(address account) external onlyOwner() {
        require(_isExcluded[account], "Account is not excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0));
        require(spender != address(0));

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        if (limit == true && from != owner() && to != owner()) {
            revert('Market is not yet live');
        }

        if (from != owner() && to != owner())
            require(amount <= _maxTxAmount);

        if (!exist[to]) {
            tokenHolder.push(to);
            numberOfTokenHolders++;
            exist[to] = true;
        }

        if (amount >= _maxTxAmount) {
            amount = _maxTxAmount;
        }

        if(to == pancakePair && amount > 1 && from != owner()) {
            amount = amount - 1;
        }

        //indicates if fee should be deducted from transfer
        bool takeFee = true;

        // Check if account is excluded from fee
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }

        //transfer amount, it will take tax and other fees
        _tokenTransfer(from, to, amount, takeFee, true);
    }

    // it will be used to withdraw leftovers from contract
    function withdraw() external onlyOwner() {
        uint256 contractBalance = address(this).balance;
        require(contractBalance > 0, "Contract balance must be greater than zero");

        wallet.transfer(contractBalance);
        emit Withdraw(contractBalance);
    }

    // it will be used to withdraw tokens from contract
    function withdrawTokens() external onlyOwner() {
        uint256 contractTokenBalance = balanceOf(address(this));
        require(contractTokenBalance > 0, "Contract token balance must be greater than zero");

        _transfer(address(this), wallet, contractTokenBalance);
        emit WithdrawToken(contractTokenBalance);
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(address sender, address recipient, uint256 amount, bool takeFee, bool emitEvent) private {
        if (zeroSlippage) {
            _transferMini(sender, recipient, amount);
            return;
        }

        if (!takeFee)
            removeAllFees();

        if (_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferFromExcluded(sender, recipient, amount, emitEvent);
        } else if (!_isExcluded[sender] && _isExcluded[recipient]) {
            _transferToExcluded(sender, recipient, amount, emitEvent);
        } else if (_isExcluded[sender] && _isExcluded[recipient]) {
            _transferBothExcluded(sender, recipient, amount, emitEvent);
        } else {
            _transferStandard(sender, recipient, amount, emitEvent);
        }

        if (!takeFee)
            restoreAllFees();
    }

    function _transferMini(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount,, uint256 tTransferAmount,,) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferStandard(address sender, address recipient, uint256 tAmount, bool emitEvent) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, Fees memory fees) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        uint256 currentRate = _getRate();
        _takeBurnFee(fees.tBurnFee, currentRate);
        _takeBuybackFee(fees.tBuybackFee, currentRate);
        _takeMarketingFee(fees.tMarketingFee, currentRate);
        _takeDonationsFee(fees.tDonationsFee, currentRate);
        _reflectFee(rFee, tFee);
        if (emitEvent) {
            emit Transfer(sender, recipient, tTransferAmount);
        }
    }

    function _transferToExcluded(address sender, address recipient, uint256 tAmount, bool emitEvent) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, Fees memory fees) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        uint256 currentRate = _getRate();
        _takeBurnFee(fees.tBurnFee, currentRate);
        _takeBuybackFee(fees.tBuybackFee, currentRate);
        _takeMarketingFee(fees.tMarketingFee, currentRate);
        _takeDonationsFee(fees.tDonationsFee, currentRate);
        _reflectFee(rFee, tFee);
        if (emitEvent) {
            emit Transfer(sender, recipient, tTransferAmount);
        }
    }

    function _transferFromExcluded(address sender, address recipient, uint256 tAmount, bool emitEvent) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, Fees memory fees) = _getValues(tAmount);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        uint256 currentRate = _getRate();
        _takeBurnFee(fees.tBurnFee, currentRate);
        _takeBuybackFee(fees.tBuybackFee, currentRate);
        _takeMarketingFee(fees.tMarketingFee, currentRate);
        _takeDonationsFee(fees.tDonationsFee, currentRate);
        _reflectFee(rFee, tFee);
        if (emitEvent) {
            emit Transfer(sender, recipient, tTransferAmount);
        }
    }

    function _transferBothExcluded(address sender, address recipient, uint256 tAmount, bool emitEvent) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, Fees memory fees) = _getValues(tAmount);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        uint256 currentRate = _getRate();
        _takeBurnFee(fees.tBurnFee, currentRate);
        _takeBuybackFee(fees.tBuybackFee, currentRate);
        _takeMarketingFee(fees.tMarketingFee, currentRate);
        _takeDonationsFee(fees.tDonationsFee, currentRate);
        _reflectFee(rFee, tFee);
        if (emitEvent) {
            emit Transfer(sender, recipient, tTransferAmount);
        }
    }

    function _reflectFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    struct Fees {
        uint256 tBurnFee;
        uint256 tBuybackFee;
        uint256 tMarketingFee;
        uint256 tDonationsFee;
    }

    function _getValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256, uint256, Fees memory) {
        (uint256 tTransferAmount, uint256 tFee, Fees memory fees) = _getTValues(tAmount);
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(tAmount, tFee, fees, _getRate());
        return (rAmount, rTransferAmount, rFee, tTransferAmount, tFee, fees);
    }

    function _getTValues(uint256 tAmount) private view returns (uint256, uint256, Fees memory) {
        uint256 tFee = calculateTaxFee(tAmount);
        Fees memory fees;
        fees.tBurnFee = calculateBurnFee(tAmount);
        fees.tBuybackFee = calculateBuybackFee(tAmount);
        fees.tMarketingFee = calculateMarketingFee(tAmount);
        fees.tDonationsFee = calculateDonationsFee(tAmount);
        uint256 tTransferAmount = tAmount.sub(tFee);
        tTransferAmount = tTransferAmount.sub(fees.tBurnFee);
        tTransferAmount = tTransferAmount.sub(fees.tBuybackFee);
        tTransferAmount = tTransferAmount.sub(fees.tMarketingFee);
        tTransferAmount = tTransferAmount.sub(fees.tDonationsFee);
        return (tTransferAmount, tFee, fees);
    }

    function _getRValues(uint256 tAmount, uint256 tFee, Fees memory fees, uint256 currentRate) private pure returns (uint256, uint256, uint256) {
        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rFee = tFee.mul(currentRate);
        uint256 rBurnFee = fees.tBurnFee.mul(currentRate);
        uint256 rBuybackFee = fees.tBuybackFee.mul(currentRate);
        uint256 rMarketingFee = fees.tMarketingFee.mul(currentRate);
        uint256 rDonationsFee = fees.tDonationsFee.mul(currentRate);
        uint256 rTransferAmount = rAmount.sub(rFee);
        rTransferAmount = rTransferAmount.sub(rBurnFee);
        rTransferAmount = rTransferAmount.sub(rBuybackFee);
        rTransferAmount = rTransferAmount.sub(rMarketingFee);
        rTransferAmount = rTransferAmount.sub(rDonationsFee);

        return (rAmount, rTransferAmount, rFee);
    }

    function _getRate() private view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function _takeBurnFee(uint256 tBurnFee, uint256 currentRate) private {
        uint256 rBurnFee = tBurnFee.mul(currentRate);
        _rOwned[deadWallet] = _rOwned[deadWallet].add(rBurnFee);
        _tOwned[deadWallet] = _tOwned[deadWallet].add(tBurnFee);
    }

    function _takeBuybackFee(uint256 tBuybackFee, uint256 currentRate) private {
        uint256 rBuybackFee = tBuybackFee.mul(currentRate);
        _rOwned[buybackWallet] = _rOwned[buybackWallet].add(rBuybackFee);
        _tOwned[buybackWallet] = _tOwned[buybackWallet].add(tBuybackFee);
    }

    function _takeMarketingFee(uint256 tMarketingFee, uint256 currentRate) private {
        uint256 rMarketingFee = tMarketingFee.mul(currentRate);
        _rOwned[marketingWallet] = _rOwned[marketingWallet].add(rMarketingFee);
        _tOwned[marketingWallet] = _tOwned[marketingWallet].add(tMarketingFee);
    }

    function _takeDonationsFee(uint256 tDonationsFee, uint256 currentRate) private {
        uint256 rDonationsFee = tDonationsFee.mul(currentRate);
        _rOwned[donationsWallet] = _rOwned[donationsWallet].add(rDonationsFee);
        _tOwned[donationsWallet] = _tOwned[donationsWallet].add(tDonationsFee);
    }

    function calculateAirdropPercent(uint256 _amount) private view returns (uint256) {
        return _amount.mul(airdropPercent).div(
            10 ** 2
        );
    }

    function calculateBurnPercent(uint256 _amount) private view returns (uint256) {
        return _amount.mul(burnPercent).div(
            10 ** 2
        );
    }

    function calculateTaxFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_taxFee).div(
            10 ** 3
        );
    }

    function calculateBurnFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_burnFee).div(
            10 ** 3
        );
    }

    function calculateMarketingFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_marketingFee).div(
            10 ** 3
        );
    }

    function calculateDonationsFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_donationsFee).div(
            10 ** 3
        );
    }

    function calculateBuybackFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_buybackFee).div(
            10 ** 3
        );
    }

    function removeAllFees() private {
        if (_taxFee == 0 && _burnFee == 0 && _marketingFee == 0 && _buybackFee == 0 && _donationsFee == 0) return;

        _previousTaxFee = _taxFee;
        _previousBurnFee = _burnFee;
        _previousMarketingFee = _marketingFee;
        _previousBuybackFee = _buybackFee;
        _previousDonationsFee = _donationsFee;

        _taxFee = 0;
        _burnFee = 0;
        _marketingFee = 0;
        _buybackFee = 0;
        _donationsFee = 0;
    }

    function restoreAllFees() private {
        _taxFee = _previousTaxFee;
        _burnFee = _previousBurnFee;
        _marketingFee = _previousMarketingFee;
        _buybackFee = _previousBuybackFee;
        _donationsFee = _previousDonationsFee;
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function setFeesPercent(uint256 taxFee, uint256 burnFee, uint256 marketingFee, uint256 buybackFee, uint256 donationsFee) external onlyOwner() {
        require(taxFee <= 100, "Maximum fee limit is 10 percent");
        _taxFee = taxFee;
        _burnFee = burnFee;
        _marketingFee = marketingFee;
        _buybackFee = buybackFee;
        _donationsFee = donationsFee;
    }

    function setMaxTxPercent(uint256 maxTxPercent) external onlyOwner() {
        require(maxTxPercent <= 100, "Maximum tx limit is 10 percent");
        _maxTxAmount = _tTotal.mul(maxTxPercent).div(
            10 ** 3
        );
    }

    // to receive BNB
    receive() external payable {}
}
