    "contracts/P2P_Binance.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract P2PBinance is Ownable, AccessControl, ReentrancyGuard ,Pausable{
    using SafeERC20 for IERC20;

address public tokenAddress;

struct OrderBuyNaka {
        address buyer;
        uint256 busdPrice;
        uint256 amount;
        uint256 totalPriceInOrder;
        bool active;
}
struct  RequestBuyNaka{
        bytes32 polygonOrderSellId;
        address buyer;
        address seller;
        uint256 sellAmount;
        uint256 nakaPrice;
        uint256 busdPrice;
        uint256 buyAmount;
}

uint256 public fee;
uint256 public accumulateFee;

mapping(bytes32 => address[])  orderSeller;
mapping(bytes32 => OrderBuyNaka) public OrdersBuyNaka;
mapping(bytes32 => RequestBuyNaka) public RequestsBuyNaka;
mapping(address => bytes32[]) OrderbyAddress;
mapping(address => bytes32[])  allOrderIds;

/**
     * @dev Declare event for use emit `createOrdersBuyNaka`,`ExecuteOrdersBuyNaka`,`submitBuyNakas`,`editOrdersBuy`
     * ,`requestBuyNakas`, `cancelOrdersBuyNaka`.
     */

event createOrdersBuyNaka(
        bytes32 orderBuyId,
        address buyer,
        uint256 busdPrice,
        uint256 amount,
        uint256 totalPrice,
        uint256 fee
 );

event ExecuteOrdersBuyNaka(
        bytes32 orderBuyId,
        address buyer,
        address seller,
        uint256 amount,
        uint256 busdPrice,
        uint256 totalPriceInOrder
 );

event submitBuyNakas(bytes32 requestBuyID,uint256 buyAmount);
event editOrdersBuy(bytes32 orderBuyId,uint256 amount, uint256 busdPrice,uint256 totalPriceInOrder);
event requestBuyNakas(
        bytes32 requestBuyNakaId,
        bytes32 polygonOrderSellId,
        address seller,
        address buyer,
        uint256 sellAmount,
        uint256 nakaPrice,
        uint256 totalBusdPrice,
        uint256 buyAmount
);
event cancelOrdersBuyNaka(bytes32 orderBuyId);

bytes32 public constant WORKER_ROLE = keccak256("WORKER_ROLE");

  /** 
    * @dev Sets the value of the `tokenAddress` and `fee`. 
    * it can only be set once during construction.
    * @param _tokenAddress - Contract address of BUSD token.
    * @param _fee - Amount of fee use when user createOrder.
    * Set up a role for contract deployer to be DEFAULT_ADMIN_ROLE.
    */
constructor(
        address _tokenAddress ,
        uint256 _fee
    ) {
        tokenAddress = _tokenAddress;
        fee = _fee;

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }
/**
     * @dev CreateOrderBuyNaka : For user want to buy Naka token by Create Order on BSC Chain and pay is BUSD.
     * User need to approve this contract in BUSD token contract .
     * When user CreateOrderBuyNaka BUSD in userwallet will transfer to contract for contract will transfer to seller by ExecuteOrderBuyNaka.
     * @param _busdPrice - Price of BUSD per Naka Token that user wanted to buy.
     * @param _amount - Amount of Naka token that user wanted to Buy.
     */

function createOrderBuyNaka(uint256 _busdPrice ,uint256 _amount) external whenNotPaused {
        uint256 total_price = _busdPrice * _amount / 1000000000000000000 ;
        uint256 total = total_price + fee;
        IERC20 token = IERC20(tokenAddress);
        accumulateFee += fee;
        token.safeTransferFrom(msg.sender, address(this),total);

          bytes32 orderBuyId = keccak256(
            abi.encodePacked(
                block.timestamp,
                msg.sender,
                _busdPrice
            )
        );

        OrderBuyNaka storage order = OrdersBuyNaka [orderBuyId];
        order.buyer = msg.sender;
        order.busdPrice = _busdPrice;
        order.amount = _amount;
        order.totalPriceInOrder = total_price;
        order.active = true;

        OrderbyAddress[msg.sender].push(orderBuyId);
        allOrderIds[address(this)].push(orderBuyId); 

        emit createOrdersBuyNaka (orderBuyId,msg.sender,_busdPrice, _amount ,total_price, fee);
    }

/**
     * @dev RequestBuyNaka : For user want to buy Naka in OrderSellNaka on Polygon Chain.
     * User need to approve this contract in BUSD token contract.
     * When user RequestBuyNaka BUSD in userwallet will transfer to contract for contract will transfer to seller by submitBuyNaka.
     * @param _polygonOrderSellId - OrderSellID of order sell on Polygon chain that user wanted to buy.
     * @param _seller - Seller Address owner of Order sell on Polygon chain.
     * @param _nakaPrice - Price of Naka token in Order sell.
     * @param _sellAmount - Amount of Naka token sell in Order.
     * @param _buyAmount - Amount of Naka token that user wanted to Buy.
     */
function requestBuyNaka(
        bytes32 _polygonOrderSellId,
        address _seller,
        uint256 _nakaPrice,
        uint256 _sellAmount,
        uint256 _buyAmount) 
        external whenNotPaused {
        uint256 totalPrice = _nakaPrice * _buyAmount / 1000000000000000000 ;
        IERC20 token = IERC20(tokenAddress);
        token.safeTransferFrom(msg.sender, address(this),totalPrice);

          bytes32 requestBuyNakaId = keccak256(
            abi.encodePacked(
                block.timestamp,
                _polygonOrderSellId,
                _seller,
                msg.sender,
                _sellAmount,
                _nakaPrice,
                _buyAmount
            )
        );

        RequestBuyNaka storage request = RequestsBuyNaka [requestBuyNakaId];
            request.polygonOrderSellId = _polygonOrderSellId;
            request.seller = _seller;
            request.buyer = msg.sender;
            request.sellAmount = _sellAmount;
            request.nakaPrice = _nakaPrice;
            request.busdPrice = totalPrice;
            request.buyAmount = _buyAmount;

        emit requestBuyNakas (requestBuyNakaId,_polygonOrderSellId,_seller,msg.sender,_sellAmount,_nakaPrice,totalPrice,_buyAmount);
    }

/**
     * @dev SubmitBuyNaka : For Worker use when user requestBuyNaka.
     * When worker SubmitBuyNaka BUSD in contract by requestBuyNaka will transfer to seller
     * @param _requestBuyNakaId - requestBuyNakaId get from requestBuyNaka by user.
     */
function submitBuyNaka (bytes32 _requestBuyNakaId)  external whenNotPaused onlyRole(WORKER_ROLE) {
        require(_requestBuyNakaId != 0, "[P2PBinance.submitBuyNaka] RequestBuyNakaId is Zero");
        RequestBuyNaka storage request = RequestsBuyNaka [_requestBuyNakaId];
        uint256 buyAmount = request.buyAmount;
        IERC20 token = IERC20(tokenAddress);
        token.safeTransfer(request.seller,request.busdPrice);
        delete RequestsBuyNaka[_requestBuyNakaId];
        emit submitBuyNakas (_requestBuyNakaId,buyAmount);

    }
/**
     * @dev CancelSubmitBuyNaka : For Worker use when ExecuteOrderSellNaka on polygon chain is fail.
     * When worker CancelSubmitBuyNaka BUSD in contract by requestBuyNaka will transfer return to user. 
     * @param _requestBuyNakaId - requestBuyNakaId get from requestBuyNaka by user.
     */
function cancelSubmitBuyNaka (bytes32 _requestBuyNakaId) external whenNotPaused onlyRole(WORKER_ROLE) {
        require(_requestBuyNakaId != 0, "[P2PBinance.cancelSubmitBuyNaka] RequestBuyNakaId is Zero");
        RequestBuyNaka storage request = RequestsBuyNaka [_requestBuyNakaId];
        IERC20 token = IERC20(tokenAddress);
        token.safeTransfer(request.buyer,request.busdPrice);
        delete RequestsBuyNaka[_requestBuyNakaId];
}
 /**
     * @dev CancelOrderBuyNaka : For user to want to cancel orderbuy.
     * When user CancelOrderBuyNaka BUSD in contract by createOrderBuyNaka will transfer return to user.
     * @param  _orderBuyId - orderBuyId get from createOrderBuy by user.
     */
function cancelOrderBuyNaka(bytes32 _orderBuyId) external {
        OrderBuyNaka storage order = OrdersBuyNaka [_orderBuyId];
        require(order.active,"[P2PBinance.cancelOrderBuyNaka] Order inactive.");
        require(order.buyer == msg.sender, "[P2PBinance.cancelOrderBuyNaka]  Not buyer.");
        uint256 busdPrice = order.busdPrice* order.amount / 1000000000000000000 ;
        IERC20 token = IERC20(tokenAddress);
        token.safeTransfer(order.buyer,busdPrice);

        order.active = false;
        deleteOrderbyAddress(_orderBuyId,msg.sender);
        deleteAllOrder(_orderBuyId);
        delete OrdersBuyNaka[_orderBuyId];

        emit cancelOrdersBuyNaka(_orderBuyId);

    }
/**
     * @dev ExecuteOrderBuyNaka : For Worker use when user(seller) requestSellNaka on Polygon chain.
     * When worker ExecuteOrderBuyNaka BUSD in orderBuyNaka will transfer to user(seller) by amount user(seller) sell.
     * @param _orderBuyId - OrderBuyId that user(seller) wanted to Sell
     * @param _amount - Amount of Naka that user(seller) wanted to Sell.
     * @param _seller - Address of user(seller).
     */
function ExecuteOrderBuyNaka (bytes32 _orderBuyId, uint256 _amount, address _seller) external whenNotPaused onlyRole(WORKER_ROLE){
        OrderBuyNaka storage order = OrdersBuyNaka [_orderBuyId];
        require(order.active, "[P2PBinance.ExecuteOrderBuyNaka] Order inactive");
        require(order.amount >= _amount, "[P2PBinance.ExecuteOrderBuyNaka] Can't buy more than order remaining amount");

        uint256 busdPrice = order.busdPrice * _amount / 1000000000000000000 ;
        address _buyer = order.buyer;
        order.amount -= _amount;
        order.totalPriceInOrder -= busdPrice;
        IERC20 token = IERC20(tokenAddress);
        token.safeTransfer(_seller, busdPrice);

        if (order.amount == 0){
            order.active = false;
            deleteOrderbyAddress(_orderBuyId,order.buyer);
            deleteAllOrder(_orderBuyId);
            delete OrdersBuyNaka[_orderBuyId];

        } else {
            orderSeller[_orderBuyId].push(_seller);
        }

        emit  ExecuteOrdersBuyNaka (_orderBuyId,_buyer, _seller , _amount, busdPrice, order.totalPriceInOrder);

    }
/**
     * @dev EditOrderBuy : For User want to editorder.
     * If user edit _busdPrice and _amount is more than the latest order user must transfer add by amount edited.
     * If user edit _busdPrice and _amount is less than the latest order contract will transfer return to user by amount edited. 
     * @param _orderBuyId - OrderBuyId that user wanted to edit
     * @param _busdPrice - Price of BUSD per Naka Token that user wanted to buy.
     * @param _amount - Amount of Naka token that user wanted to Buy.
     */
function editOrderBuy(bytes32 _orderBuyId,uint256 _busdPrice, uint256 _amount) external{
        OrderBuyNaka storage order = OrdersBuyNaka [_orderBuyId];
        require(order.active, "[P2PBinance.editAmountOderBuy] Order inactive");
        require(order.buyer == msg.sender, "[P2PBinance.editOrderBuy] Not buyer");
        require(_amount & _busdPrice  > 0, "[P2PBinance.editOrderBuy] BusdPrice and Amout must be more than 0");
        IERC20 token = IERC20(tokenAddress);
        uint256 sumEdit = (_busdPrice * _amount) / 1000000000000000000;
        uint256 sumInOrder = (order.busdPrice * order.amount) / 1000000000000000000;
        if(sumEdit >= sumInOrder){
            uint256 total = sumEdit - sumInOrder;
            token.safeTransferFrom(msg.sender, address(this),total);
            order.totalPriceInOrder += total;
        }else{
            uint256 total = sumInOrder - sumEdit;
            token.safeTransfer(msg.sender,total);
            order.totalPriceInOrder -= total;
        }
        order.amount = _amount;
        order.busdPrice = _busdPrice;
        emit editOrdersBuy(_orderBuyId, _amount,_busdPrice,order.totalPriceInOrder);
    }
/**
     * @dev SetFee : For Worker use Setfee.
     * @param _fee - Amount of fee that wanted to set.
     */
function setFee(uint256 _fee) external whenNotPaused onlyRole(WORKER_ROLE) {
        fee = _fee;
    }
/**
     * @dev WithdrawFee : For Worker use WithdrawFee.
     * @param _amount - Amount of fee that wanted to Withdraw.
     */
function withdrawFee(uint256 _amount) external whenNotPaused onlyRole(WORKER_ROLE) {
        require(accumulateFee != 0, "[P2PBinance.withdrawFee] AccumulateFee is have 0 ");
        require(accumulateFee >= _amount, "[P2PBinance.withdrawFee] AccumulateFee not enough");
        IERC20 token = IERC20(tokenAddress);
        token.safeTransfer(msg.sender, _amount);
        accumulateFee = 0;
    }

function calculateBusdPrice(bytes32 _orderBuyId, uint256 _amount) public view returns (uint256)
    {
        OrderBuyNaka storage order = OrdersBuyNaka [_orderBuyId];
        uint256 busdPrice = order.busdPrice * _amount / 1000000000000000000 ;
        return busdPrice;
    }

function calculateProtocolFee(uint256 _amount) public view returns (uint256)
    {
        return (fee * _amount) / 10000;
    }
function getOrderIdByAddress(address _address) external view returns (bytes32[] memory)
    {   
        return OrderbyAddress[_address];
    }
function getAllOrders(address _contractAddress) external view returns (bytes32[] memory)
    {   
        return allOrderIds[_contractAddress];
    }
function getSeller(bytes32 _orderBuyId) external view returns (address[] memory)
    {   
        return orderSeller[_orderBuyId];
    }

function multiGrandRole(bytes32 _role,address [] memory _workerAddress) external {
        uint256 _length = _workerAddress.length;
           for (uint256 i = 0; i < _length; i++){
                grantRole(_role, _workerAddress[i]);
           }     
}

function findIndexOrder(bytes32 _orderBuyId, address _walletAddress) private view returns  (uint256){
        for (uint256 i; i < OrderbyAddress[_walletAddress].length; i++) {
            if (OrderbyAddress[_walletAddress][i] == _orderBuyId) {
                return i;
            }
        }
        revert("Error findIndexOrderbyAddress" );
    }

function findIndexAllOrder(bytes32 _orderBuyId, address _walletAddress) private view returns  (uint256){
       for (uint256 i; i < allOrderIds[_walletAddress].length; i++) {
            if (allOrderIds[_walletAddress][i] == _orderBuyId) {
                return i;
            }
        }
        revert("Error findIndexAllOrder");
    }

function deleteAllOrder(bytes32 _orderBuyId) private  {
        uint256 index = findIndexAllOrder(_orderBuyId,address(this));
        for (uint256 i = index; i < allOrderIds[address(this)].length-1; i++){
            allOrderIds[address(this)][i] = allOrderIds[address(this)][i+1];
        }
        delete allOrderIds[address(this)][allOrderIds[address(this)].length-1];
        allOrderIds[address(this)].pop();
}
function deleteOrderbyAddress(bytes32 _orderBuyId ,address _walletAddress) private  {
        uint256 index = findIndexOrder(_orderBuyId,_walletAddress);
        for (uint256 i = index; i < OrderbyAddress[_walletAddress].length-1; i++){
            OrderbyAddress[_walletAddress][i] = OrderbyAddress[_walletAddress][i+1];
        }
        delete OrderbyAddress[_walletAddress][OrderbyAddress[_walletAddress].length-1];
        OrderbyAddress[_walletAddress].pop();
}

 function pause() external onlyOwner whenNotPaused {
        _pause();
    }

function unpause() external onlyOwner whenPaused {
        _unpause();
    }
}"
