// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract YUYToken {
    string public name = "YUY";
    string public symbol = "YUY";
    uint8 public decimals = 18;
    address public owner;

    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    // Constants
    uint256 public constant INITIAL_SUPPLY = 71000000 * (10 ** 18); // 71,000,000 YUY
    address public constant MARKETING_ADDRESS = 0x88a126Ce20f821A4eA3EE1D869fF97B9E1100000; // Marketing address

    // Constructor
    constructor() {
        name = "YUY";
        symbol = "YUY";
        decimals = 18;
        owner = msg.sender;
        _totalSupply = INITIAL_SUPPLY;
        _balances[msg.sender] = _totalSupply;
    }

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 value) external returns (bool) {
        _transfer(msg.sender, to, value);
        return true;
    }

    function allowance(address ownerAddress, address spenderAddress) external view returns (uint256) {
        return _allowances[ownerAddress][spenderAddress];
    }

    function approve(address spender, uint256 value) external returns (bool) {
        _approve(msg.sender, spender, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) external returns (bool) {
        _transfer(from, to, value);
        _approve(from, msg.sender, _allowances[from][msg.sender] - value);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) external returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) external returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender] - subtractedValue);
        return true;
    }

    function destroyTokens(uint256 amount) external {
        require(msg.sender == owner, "Not the owner");
        uint256 balance = _balances[msg.sender];
        require(balance >= amount, "Insufficient balance");
        _balances[msg.sender] -= amount;
        _totalSupply -= amount;
        emit Transfer(msg.sender, address(0), amount);
    }

    function transferMarketingTokens() external {
        require(msg.sender == owner, "Not the owner");
        uint256 marketingAmount = (_totalSupply * 5) / 100;
        _transfer(msg.sender, MARKETING_ADDRESS, marketingAmount);
    }

    function _transfer(address from, address to, uint256 value) internal {
        require(to != address(0), "Invalid address");
        require(_balances[from] >= value, "Insufficient balance");

        _balances[from] -= value;
        _balances[to] += value;
        emit Transfer(from, to, value);
    }

    function _approve(address ownerAddress, address spender, uint256 value) internal {
        _allowances[ownerAddress][spender] = value;
        emit Approval(ownerAddress, spender, value);
    }
}
